@tailwind base;
@tailwind components;
@tailwind utilities;

:root {
  --background: hsl(0, 0%, 100%);
  --foreground: hsl(20, 14.3%, 4.1%);
  --muted: hsl(60, 4.8%, 95.9%);
  --muted-foreground: hsl(25, 5.3%, 44.7%);
  --popover: hsl(0, 0%, 100%);
  --popover-foreground: hsl(20, 14.3%, 4.1%);
  --card: hsl(0, 0%, 100%);
  --card-foreground: hsl(20, 14.3%, 4.1%);
  --border: hsl(20, 5.9%, 90%);
  --input: hsl(20, 5.9%, 90%);
  --primary: hsl(220, 91%, 60%);
  --primary-foreground: hsl(210, 40%, 98%);
  --secondary: hsl(60, 4.8%, 95.9%);
  --secondary-foreground: hsl(24, 9.8%, 10%);
  --accent: hsl(60, 4.8%, 95.9%);
  --accent-foreground: hsl(24, 9.8%, 10%);
  --destructive: hsl(0, 84.2%, 60.2%);
  --destructive-foreground: hsl(60, 9.1%, 97.8%);
  --ring: hsl(20, 14.3%, 4.1%);
  --radius: 0.5rem;

  /* Brand colors matching the design */
  --brand-blue: hsl(220, 91%, 60%);
  --brand-indigo: hsl(239, 84%, 67%);
  --brand-dark: hsl(224, 76%, 48%);
}

.dark {
  --background: hsl(240, 10%, 3.9%);
  --foreground: hsl(0, 0%, 98%);
  --muted: hsl(240, 3.7%, 15.9%);
  --muted-foreground: hsl(240, 5%, 64.9%);
  --popover: hsl(240, 10%, 3.9%);
  --popover-foreground: hsl(0, 0%, 98%);
  --card: hsl(240, 10%, 3.9%);
  --card-foreground: hsl(0, 0%, 98%);
  --border: hsl(240, 3.7%, 15.9%);
  --input: hsl(240, 3.7%, 15.9%);
  --primary: hsl(220, 91%, 60%);
  --primary-foreground: hsl(210, 40%, 98%);
  --secondary: hsl(240, 3.7%, 15.9%);
  --secondary-foreground: hsl(0, 0%, 98%);
  --accent: hsl(240, 3.7%, 15.9%);
  --accent-foreground: hsl(0, 0%, 98%);
  --destructive: hsl(0, 62.8%, 30.6%);
  --destructive-foreground: hsl(0, 0%, 98%);
  --ring: hsl(240, 4.9%, 83.9%);
}

@layer base {
  * {
    @apply border-border;
  }

  body {
    @apply font-sans antialiased bg-background text-foreground;
  }
}

@layer components {
  .brand-gradient {
    background: linear-gradient(135deg, var(--brand-blue) 0%, var(--brand-indigo) 100%);
  }

  .simulation-nav-item {
    @apply flex items-center space-x-3 px-4 py-3 mx-4 my-1 text-gray-600 rounded-lg text-sm font-medium transition-all duration-200 hover:bg-gray-50 hover:text-gray-900;
  }

  .simulation-nav-item.active {
    @apply bg-blue-50 text-blue-700;
  }

  .simulation-nav-item i {
    @apply w-4 h-4;
  }

  .difficulty-btn, .personality-btn {
    @apply px-4 py-2 border border-gray-300 rounded-lg bg-white text-gray-700 text-sm font-medium transition-all duration-200 cursor-pointer hover:border-gray-400 hover:bg-gray-50;
  }

  .difficulty-btn.active, .personality-btn.active {
    @apply border-blue-500 bg-blue-50 text-blue-700;
  }

  .scenario-card {
    @apply p-6 border-2 border-gray-200 rounded-xl bg-white cursor-pointer transition-all duration-200 hover:border-gray-300 hover:shadow-md;
  }

  .scenario-card.active {
    @apply border-green-500 bg-green-50;
  }
}

