import { FC } from 'react'
import { PAGINATION_LIMITS } from '../../features/app/_store/constants'
import { type PaginationControls } from '../../features/app/_store/state'
import Limiter from './limiter'
import Pager from './pager'
import styles from './pagination.module.css'

interface PaginationControlsProps {
    count: number
    pagination: PaginationControls
    onPaginationChange: (
        p: Pick<PaginationControls, 'offset' | 'limit'>,
    ) => void
}

const Pagination: FC<PaginationControlsProps> = ({
    count,
    pagination,
    onPaginationChange,
}) => {
    const { hasMore, limit, offset } = pagination

    if (count < 1) return null

    return (
        <center-l andText className={styles.pagination} data-landmark>
            <Pager
                count={count}
                hasMore={hasMore}
                limit={limit}
                offset={offset}
                onOffsetChange={(offset) => {
                    onPaginationChange({ offset })
                }}
            >
                <Limiter
                    count={count}
                    limit={limit}
                    limits={PAGINATION_LIMITS}
                    offset={offset}
                    onLimitChange={(limit) => {
                        onPaginationChange({ limit, offset: 0 })
                    }}
                />
            </Pager>
        </center-l>
    )
}

export default Pagination

frontend/src/components/pagination/pager.content:
import { ReactNode } from 'react'
import { PaginationControls } from '../../features/app/_store/state'
import styles from './pager.module.css'

const Pager = ({
    children,
    limit,
    hasMore,
    offset,
    onOffsetChange,
}: PaginationControls & {
    count: number
    onOffsetChange: (offset: number) => void
    children: ReactNode
}) => {
    const prevDisabled = offset! <= 0
    const nextDisabled = !hasMore

    return (
        <sl-button-group className={styles.pager}>
            <sl-button
                disabled={prevDisabled}
                variant="default"
                onClick={
                    !prevDisabled
                        ? () => onOffsetChange(offset! - limit!)
                        : null
                }
            >
                <sl-icon slot="prefix" name="chevron-left" />
            </sl-button>
            {children}
            <sl-button
                disabled={!hasMore}
                variant="default"
                onClick={
                    !nextDisabled
                        ? () => onOffsetChange(offset! + limit!)
                        : null
                }
            >
                <sl-icon slot="prefix" name="chevron-right" />
            </sl-button>
        </sl-button-group>
    )
}

export default Pager

