import { useCallback, useRef } from 'react'
import { useDispatch } from 'react-redux'
import { SessionStatusEnum } from '../../../__generated__/api'
import SimulationChat from '../../../components/simulation-chat'
import { RootDispatch } from '../../app/_store'
import { Status } from '../../app/_store/state'
import navigation from '../../app/navigation'
import { resetFeedback } from '../../interview-feedback/_store/reducer'
import { sendMessage, startInterviewSession } from '../_store/effects'
import { resetSimulation } from '../_store/reducer'
import useInterviewSimulation from '../_store/use-interview-details'
import InterviewMessage from '../interview-message'
import ChatControls from './controls'
import styles from './index.module.css'

const InterviewMessages = () => {
    const { sessionId, messages, sessionState, status } =
        useInterviewSimulation()
    const dispatch = useDispatch<RootDispatch>()

    const isPending = status === Status.PENDING
    const isDone = sessionState === SessionStatusEnum.Done

    const handleReset = useCallback(() => {
        dispatch(resetFeedback())
        dispatch(resetSimulation())
    }, [dispatch])

    const handleRetake = useCallback(() => {
        handleReset()
        dispatch(startInterviewSession())
    }, [handleReset, dispatch])

    const handleReview = useCallback(() => {
        navigation.goToInterviewFeedbackPage(sessionId!)
    }, [sessionId])

    return (
        <stack-l className={styles.messages} space="var(--s-2)">
            {messages.map((message) => {
                return (
                    <InterviewMessage
                        key={message.id}
                        message={message}
                        isPending={isPending}
                        showQuestionReason={false}
                    />
                )
            })}

            {isDone ? (
                <cluster-l space="var(--s-2)">
                    <sl-button onClick={handleRetake}>
                        <sl-icon name="arrow-repeat" slot="prefix" />
                        Retake Simulation
                    </sl-button>

                    <sl-button onClick={handleReview}>
                        <sl-icon name="check" slot="prefix" />
                        Review and Finish
                    </sl-button>
                </cluster-l>
            ) : null}
        </stack-l>
    )
}

const InterviewChat = () => {
    const { sessionId, messages, sessionState, status } =
        useInterviewSimulation()
    const dispatch = useDispatch<RootDispatch>()

    const formRef = useRef<HTMLFormElement | null>(null)
    const isPending = status === Status.PENDING
    const isDone = sessionState === SessionStatusEnum.Done

    const onSend = useCallback(
        (message: string) => {
            dispatch(
                sendMessage({
                    session_id: sessionId!,
                    message,
                }),
            )
        },
        [dispatch, sessionId],
    )

    return (
        <SimulationChat
            messages={<InterviewMessages />}
            controls={
                <ChatControls
                    messages={messages}
                    disabled={isPending || isDone}
                    onSend={onSend}
                    formRef={formRef}
                />
            }
        />
    )
}

export default InterviewChat

