import { ChatGroq } from "@langchain/groq";
import { PromptTemplate } from "@langchain/core/prompts";
import { RunnableSequence } from "@langchain/core/runnables";
import { StringOutputParser } from "@langchain/core/output_parsers";

/**
 * Strip Qwen3's <think>...</think> reasoning tags from output
 * Qwen3 is a reasoning model that outputs its chain-of-thought in these tags
 */
export function stripThinkingTags(text: string): string {
  // Remove <think>...</think> blocks (including multiline)
  let cleaned = text.replace(/<think>[\s\S]*?<\/think>/gi, '').trim();

  // Remove wrapping quotation marks if the entire response is quoted
  if ((cleaned.startsWith('"') && cleaned.endsWith('"')) ||
      (cleaned.startsWith("'") && cleaned.endsWith("'"))) {
    cleaned = cleaned.slice(1, -1).trim();
  }

  return cleaned;
}

// Using Qwen3-32B for better reasoning quality (latency is acceptable - gives "thinking" feel)
const MODEL_NAME = "qwen/qwen3-32b";

const model = new ChatGroq({
  model: MODEL_NAME,
  temperature: 0.6,
  topP: 0.95,
  apiKey: process.env.GROQ_API_KEY,
});

const questionModel = new ChatGroq({
  model: MODEL_NAME,
  temperature: 0.6,
  topP: 0.95,
  apiKey: process.env.GROQ_API_KEY,
});

const evaluatorModel = new ChatGroq({
  model: MODEL_NAME,
  temperature: 0.6,
  topP: 0.95,
  apiKey: process.env.GROQ_API_KEY,
});

export type InterviewStage = "greeting" | "intro_exchange" | "opening" | "core" | "wrapup" | "closure";

export function getInterviewStage(questionIndex: number, totalQuestions: number): InterviewStage {
  if (questionIndex === 0) return "opening";
  if (questionIndex >= totalQuestions - 1) return "wrapup";
  return "core";
}

export function getPreludeStage(preludeStep: number): InterviewStage {
  if (preludeStep === 0) return "greeting";
  if (preludeStep === 1) return "intro_exchange";
  return "opening";
}

export function shouldGenerateReflection(answer: string, stage: InterviewStage): boolean {
  if (stage === "opening") return false;
  if (stage === "wrapup") return false;
  const wordCount = answer.trim().split(/\s+/).length;
  if (wordCount < 15) return false;
  return Math.random() < 0.4;
}

export function extractCvHighlights(cvText: string): string {
  if (!cvText || cvText.length < 50) {
    return "No CV provided";
  }

  const highlights: string[] = [];

  const experienceMatch = cvText.match(/(\d+)\+?\s*years?/i);
  if (experienceMatch) {
    highlights.push(`- Years of experience: ${experienceMatch[0]}`);
  }

  const companies = [
    { pattern: /rabobank/i, name: "Rabobank" },
    { pattern: /redkite/i, name: "RedKite" },
    { pattern: /unicef/i, name: "UNICEF" },
    { pattern: /unfpa|united nations population fund/i, name: "UNFPA" },
    { pattern: /world bank/i, name: "World Bank" },
    { pattern: /maastricht university/i, name: "Maastricht University" },
    { pattern: /tilburg university/i, name: "Tilburg University" },
    { pattern: /american university of armenia/i, name: "American University of Armenia" },
    { pattern: /yerevan state university/i, name: "Yerevan State University" },
  ];

  const foundCompanies: string[] = [];
  const foundEducation: string[] = [];

  for (const { pattern, name } of companies) {
    if (pattern.test(cvText)) {
      if (name.includes("University")) {
        foundEducation.push(name);
      } else {
        foundCompanies.push(name);
      }
    }
  }

  if (foundCompanies.length > 0) {
    highlights.push(`- Companies worked at: ${foundCompanies.join(", ")}`);
  }

  if (foundEducation.length > 0) {
    highlights.push(`- Education: ${foundEducation.join(", ")}`);
  }

  const rolePatterns = [
    /snr\.?\s*data\s*scientist/i,
    /senior\s*data\s*scientist/i,
    /lead\s*data\s*scientist/i,
    /data\s*scientist/i,
    /phd\s*candidate/i,
    /data\s*analy[sz]t/i,
    /program\s*manager/i,
  ];

  const foundRoles: string[] = [];
  for (const pattern of rolePatterns) {
    const match = cvText.match(pattern);
    if (match && !foundRoles.some(r => r.toLowerCase() === match[0].toLowerCase())) {
      foundRoles.push(match[0]);
    }
  }

  if (foundRoles.length > 0) {
    highlights.push(`- Roles held: ${foundRoles.slice(0, 3).join(", ")}`);
  }

  const phdMatch = cvText.match(/ph\.?d\.?|philosophy\s*doctor/i);
  const masterMatch = cvText.match(/m\.?s\.?c?\.?|master/i);
  if (phdMatch) {
    highlights.push(`- Highest degree: PhD`);
  } else if (masterMatch) {
    highlights.push(`- Highest degree: Master's`);
  }

  const skillPatterns = [
    /python/i, /r(?:\s|,|$)/i, /sql/i, /machine\s*learning/i, 
    /power\s*bi/i, /tableau/i, /spark|pyspark/i, /statistics/i
  ];
  const foundSkills: string[] = [];
  for (const pattern of skillPatterns) {
    if (pattern.test(cvText)) {
      const skill = cvText.match(pattern)?.[0].trim();
      if (skill && skill.length > 1) foundSkills.push(skill);
    }
  }
  if (foundSkills.length > 0) {
    highlights.push(`- Key skills: ${foundSkills.slice(0, 5).join(", ")}`);
  }

  return highlights.length > 0 
    ? highlights.join("\n") 
    : "CV provided but key details not extracted";
}

export interface InterviewConfig {
  interviewType: string;
  targetRole: string;
  difficulty: string;
  totalQuestions: number;
  companyName?: string;
  companyDescription?: string;
  jobTitle?: string;
  jobRequirements?: string;
  candidateCv?: string;
  interviewerName?: string;
  // Stage runtime settings for dynamic interview flow
  stageSettings?: StageRuntimeSettings;
}

// Stage-specific runtime settings for dynamic interview flow
export interface StageRuntimeSettings {
  minDurationMs: number;      // Minimum interview duration in milliseconds
  maxDurationMs: number;      // Maximum interview duration in milliseconds
  minQuestions: number;       // Minimum core questions to ask
  maxQuestions: number;       // Hard limit on questions (prevents endless interviews)
  criticalCriteria: AssessmentCriterion[]; // Must-cover criteria for this stage
  sufficiencyThresholds: {
    overall: number;          // Required overall coverage (0-1)
    critical: number;         // Required coverage for critical criteria (0-1)
    confidence: number;       // Required evaluator confidence for early end (0-1)
  };
}

// Interview stage type definitions
export type InterviewStageType = 'hr_screening' | 'technical' | 'behavioral' | 'final' | 'case_study';

// Default stage settings for each interview type
export const DEFAULT_STAGE_SETTINGS: Record<InterviewStageType, StageRuntimeSettings> = {
  hr_screening: {
    minDurationMs: 15 * 60 * 1000,  // 15 minutes
    maxDurationMs: 30 * 60 * 1000,  // 30 minutes
    minQuestions: 4,
    maxQuestions: 12,
    criticalCriteria: ['background', 'motivation', 'culture_fit'],
    sufficiencyThresholds: {
      overall: 0.70,
      critical: 0.60,
      confidence: 0.75
    }
  },
  technical: {
    minDurationMs: 35 * 60 * 1000,  // 35 minutes
    maxDurationMs: 55 * 60 * 1000,  // 55 minutes
    minQuestions: 6,
    maxQuestions: 18,
    criticalCriteria: ['skills', 'background'],
    sufficiencyThresholds: {
      overall: 0.75,
      critical: 0.70,
      confidence: 0.80
    }
  },
  behavioral: {
    minDurationMs: 25 * 60 * 1000,  // 25 minutes
    maxDurationMs: 40 * 60 * 1000,  // 40 minutes
    minQuestions: 5,
    maxQuestions: 15,
    criticalCriteria: ['behavioral', 'culture_fit', 'motivation'],
    sufficiencyThresholds: {
      overall: 0.70,
      critical: 0.65,
      confidence: 0.75
    }
  },
  final: {
    minDurationMs: 25 * 60 * 1000,  // 25 minutes
    maxDurationMs: 40 * 60 * 1000,  // 40 minutes
    minQuestions: 5,
    maxQuestions: 12,
    criticalCriteria: ['culture_fit', 'motivation', 'logistics'],
    sufficiencyThresholds: {
      overall: 0.75,
      critical: 0.70,
      confidence: 0.80
    }
  },
  case_study: {
    minDurationMs: 40 * 60 * 1000,  // 40 minutes
    maxDurationMs: 60 * 60 * 1000,  // 60 minutes
    minQuestions: 3,
    maxQuestions: 8,
    criticalCriteria: ['skills', 'behavioral'],
    sufficiencyThresholds: {
      overall: 0.80,
      critical: 0.75,
      confidence: 0.85
    }
  }
};

// Helper to get stage settings from interview type
export function getStageSettings(interviewType: string): StageRuntimeSettings {
  const typeMapping: Record<string, InterviewStageType> = {
    'hr': 'hr_screening',
    'hr_screening': 'hr_screening',
    'recruiter_call': 'hr_screening',
    'behavioral': 'behavioral',
    'technical': 'technical',
    'case_study': 'case_study',
    'final': 'final'
  };

  const stageType = typeMapping[interviewType.toLowerCase()] || 'hr_screening';
  return DEFAULT_STAGE_SETTINGS[stageType];
}

export interface QuestionContext {
  config: InterviewConfig;
  questionIndex: number;
  previousQuestions: string[];
  previousAnswers: string[];
  previousScores: number[];
  lastAnswer?: string;
  activeProject?: string | null;
  prioritizedTopic?: 'background' | 'skills' | 'behavioral' | 'motivation' | 'culture_fit' | 'logistics';
}

export interface EvaluationResult {
  score: number;
  feedback: string;
  strengths: string[];
  improvements: string[];
  projectMentioned?: string | null;
}

export interface FollowUpDecision {
  action: "next_question" | "follow_up" | "adjust_difficulty" | "end_interview";
  reason: string;
  difficultyAdjustment?: "easier" | "harder" | "same";
}

export interface FinalReport {
  overallScore: number;
  communicationScore: number;
  technicalScore: number;
  problemSolvingScore: number;
  cultureFitScore: number;
  summary: string;
  strengths: string[];
  improvements: string[];
  recommendations: string[];
  hiringDecision: string;
}

// Pre-conversation preparation types
export type AssessmentCriterion = 'background' | 'skills' | 'behavioral' | 'motivation' | 'culture_fit' | 'logistics';

export interface PlannedQuestion {
  id: string;
  question: string;
  criterion: AssessmentCriterion;
  priority: number; // 1 = highest priority
  rationale: string;
  desiredDepth: 'brief' | 'detailed' | 'in-depth';
  status: 'pending' | 'asked' | 'covered_proactively' | 'skipped';
  keywords: string[]; // For detecting proactive coverage
}

export interface InterviewPlan {
  candidateStrengths: string[];
  areasToProbe: string[];
  potentialConcerns: string[];
  questions: PlannedQuestion[];
}

// Enhanced follow-up decision with more nuanced outcomes
export interface TriageDecision {
  outcome: 'satisfied' | 'partial' | 'vague' | 'has_question' | 'proactive_coverage';
  action: 'proceed_next' | 'follow_up' | 'answer_then_proceed' | 'acknowledge_coverage';
  reason: string;
  followUpPrompt?: string; // Specific follow-up if needed
  candidateQuestion?: string; // The question the candidate asked (if has_question)
  coveredQuestionIds?: string[]; // Questions proactively covered by this answer
  acknowledgment?: string; // Natural acknowledgment for proactive coverage
}

