import { useState, useMemo } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Progress } from "@/components/ui/progress";
import { apiRequest, queryClient } from "@/lib/queryClient";
import { 
  Target, 
  Users, 
  Clock, 
  Play, 
  RotateCcw,
  ChevronRight,
  ChevronLeft,
  Calendar,
  Code,
  FileText,
  GitBranch,
  MessageSquare,
  Lock,
  CheckCircle2,
  Building2,
  MoreVertical,
  Briefcase,
  TestTube,
  Server,
  Database,
  Loader2
} from "lucide-react";
import { useAvailableRoles, type AvailableRole } from "@/hooks/use-adapters";
import {
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuTrigger,
} from "@/components/ui/dropdown-menu";
import InternOnboardingSession from "@/components/simulation/intern-onboarding-session";

type WizardStep = 'project' | 'role' | 'scenario';

interface Scenario {
  id: string;
  day: number;
  title: string;
  description: string;
  skills: string[];
  duration: string;
  icon: any;
  available: boolean;
  prerequisite?: string;
}

const SCENARIOS: Scenario[] = [
  {
    id: 'day-1',
    day: 1,
    title: 'Day 1: Onboarding',
    description: 'Meet your team, read project docs, and have your first check-in',
    skills: ['Team Communication', 'Documentation', 'Active Listening'],
    duration: '~45 min',
    icon: Users,
    available: true
  },
  {
    id: 'day-2',
    day: 2,
    title: 'Day 2: First Ticket',
    description: 'Fix your first bug, learn Git workflow, and submit a PR',
    skills: ['Git', 'Code Review', 'Problem Solving'],
    duration: '~30 min',
    icon: GitBranch,
    available: true
  },
  {
    id: 'day-3',
    day: 3,
    title: 'Day 3: Code Review',
    description: 'Respond to PR feedback and revise your code',
    skills: ['Code Quality', 'Feedback', 'Iteration'],
    duration: '~25 min',
    icon: Code,
    available: false,
    prerequisite: 'Complete Day 2 first'
  },
  {
    id: 'day-4',
    day: 4,
    title: 'Day 4: Documentation',
    description: 'Write a README section and receive feedback',
    skills: ['Technical Writing', 'Documentation', 'Clarity'],
    duration: '~20 min',
    icon: FileText,
    available: false,
    prerequisite: 'Complete Day 3 first'
  },
  {
    id: 'day-5',
    day: 5,
    title: 'Day 5: Bug Fix & Wrap-up',
    description: 'Fix an edge case bug and complete your final evaluation',
    skills: ['Debugging', 'Testing', 'Self-Reflection'],
    duration: '~30 min',
    icon: MessageSquare,
    available: false,
    prerequisite: 'Complete Day 4 first'
  }
];

export default function WorkspacePractice() {
  const [activeSession, setActiveSession] = useState<any>(null);
  const [selectedProject, setSelectedProject] = useState<any>(null);
  const [wizardStep, setWizardStep] = useState<WizardStep>('project');
  const [selectedRole, setSelectedRole] = useState<string>('');
  const [selectedScenario, setSelectedScenario] = useState<Scenario | null>(null);

  const { data: user } = useQuery({
    queryKey: ['/api/user'],
  });

  const { data: projects, isLoading: projectsLoading } = useQuery({
    queryKey: ['/api/workspace/projects'],
  });

  const { data: progressData, isLoading: progressLoading } = useQuery({
    queryKey: [`/api/workspace/progress/${(user as any)?.id}/practice`],
    enabled: !!(user as any)?.id,
  });

  const { data: adapterRoles = [], isLoading: rolesLoading } = useAvailableRoles();

  const roleIconMap: Record<string, any> = {
    developer: Code,
    pm: Briefcase,
    qa: TestTube,
    devops: Server,
    data_science: Database,
  };

  const availableRoles = useMemo(() => {
    if (adapterRoles.length === 0) {
      return [
        { name: 'Developer', role: 'developer', available: true, description: 'Write code, fix bugs, and collaborate with your team', icon: Code },
        { name: 'Product Manager', role: 'pm', available: false, description: 'Coming soon', icon: Briefcase },
        { name: 'Designer', role: 'designer', available: false, description: 'Coming soon', icon: Briefcase }
      ];
    }
    return adapterRoles.map((role: AvailableRole) => ({
      name: role.displayName,
      role: role.role,
      available: role.role === 'developer',
      description: role.role === 'developer' ? role.description : 'Coming soon in Phase 5',
      icon: roleIconMap[role.role] || Code
    }));
  }, [adapterRoles]);

  const createSessionMutation = useMutation({
    mutationFn: async (config: any) => {
      const response = await apiRequest("POST", "/api/sessions", config);
      return response.json();
    },
    onSuccess: () => {
      // Don't set activeSession here - let handleStartPractice do it after progress is created
      queryClient.invalidateQueries({ 
        predicate: (query) => (query.queryKey[0] as string)?.startsWith('/api/workspace/progress')
      });
    },
  });

  const handleSelectProject = (project: any) => {
    setSelectedProject(project);
    setWizardStep('role');
  };

  const handleSelectRole = (role: string) => {
    setSelectedRole(role);
    setWizardStep('scenario');
  };

  const handleSelectScenario = (scenario: Scenario) => {
    if (!scenario.available) return;
    setSelectedScenario(scenario);
  };

  const handleStartPractice = async () => {
    if (!selectedProject || !selectedRole || !selectedScenario || !user) return;

    // Check if there's already an existing session with the same project, role, and day
    const existingProgress = Array.isArray(progressData) 
      ? progressData.find((p: any) => 
          p.projectId === selectedProject.id && 
          p.role === selectedRole && 
          p.currentDay === selectedScenario.day &&
          p.mode === 'practice' &&
          p.status === 'in_progress'
        )
      : null;

    if (existingProgress) {
      // Resume existing session instead of creating a new one
      handleResumePractice(existingProgress, selectedProject);
      return;
    }

    const session = await createSessionMutation.mutateAsync({
      userId: (user as any).id,
      type: 'workspace',
      status: 'active',
      configuration: {
        projectId: selectedProject.id,
        projectName: selectedProject.name,
        projectCategory: selectedProject.category,
        projectDescription: selectedProject.description,
        activeRole: selectedRole,
        sprintPhase: 'onboarding',
        teamMembers: selectedProject.teamStructure,
        duration: selectedProject.estimatedDuration,
        requirements: selectedProject.requirements,
        scenarioScript: selectedProject.scenarioScript,
        mode: 'practice',
        scenarioId: selectedScenario.id,
        startDay: selectedScenario.day
      },
      messages: []
    });

    // Save initial progress record BEFORE setting activeSession
    // This ensures the component receives savedProgressId at mount time
    try {
      const progressResponse = await apiRequest("POST", "/api/workspace/progress", {
        sessionId: session.id,
        userId: (user as any).id,
        projectId: selectedProject.id,
        role: selectedRole,
        mode: 'practice',
        currentDay: selectedScenario.day,
        scenarioId: selectedScenario.id,
        dayProgress: {},
        overallProgress: 0,
        status: 'in_progress'
      });
      const savedProgress = await progressResponse.json();

      // Set activeSession with savedProgressId included
      setActiveSession({
        ...session,
        configuration: {
          ...session.configuration,
          savedProgressId: savedProgress.id
        }
      });
    } catch (error) {
      console.error('Failed to save initial progress:', error);
      // Still set the session even if progress save fails
      setActiveSession(session);
    }
  };

  const handleResumePractice = (progress: any, project: any) => {
    setSelectedProject(project);
    setActiveSession({
      id: progress.sessionId,
      configuration: {
        projectId: project.id,
        projectName: project.name,
        projectCategory: project.category,
        projectDescription: project.description,
        activeRole: progress.role,
        sprintPhase: 'onboarding',
        teamMembers: project.teamStructure,
        duration: project.estimatedDuration,
        requirements: project.requirements,
        scenarioScript: project.scenarioScript,
        mode: 'practice',
        scenarioId: progress.scenarioId,
        savedProgress: progress.dayProgress,
        savedProgressId: progress.id,
        currentDay: progress.currentDay
      }
    });
  };

  const handleSessionComplete = () => {
    setActiveSession(null);
    setSelectedProject(null);
    setSelectedRole('');
    setSelectedScenario(null);
    setWizardStep('project');
    queryClient.invalidateQueries({ 
      predicate: (query) => (query.queryKey[0] as string)?.startsWith('/api/workspace/progress')
    });
  };

  const handleBack = () => {
    if (wizardStep === 'role') {
      setWizardStep('project');
      setSelectedProject(null);
    } else if (wizardStep === 'scenario') {
      setWizardStep('role');
      setSelectedRole('');
    }
  };

  if (activeSession) {
    const config = activeSession.configuration;
    const isInternOnboarding = config?.projectCategory === 'intern-onboarding';

    const projectData = selectedProject || {
      id: config?.projectId,
      name: config?.projectName,
      description: config?.projectDescription,
      category: config?.projectCategory,
      teamStructure: config?.teamMembers,
      estimatedDuration: config?.duration,
      requirements: config?.requirements || {},
      scenarioScript: config?.scenarioScript || {}
    };

    if (isInternOnboarding) {
      return (
        <InternOnboardingSession
          session={activeSession}
          project={projectData}
          onComplete={handleSessionComplete}
          mode="practice"
          savedProgress={config?.savedProgress}
          savedProgressId={config?.savedProgressId}
          initialDay={config?.currentDay || config?.startDay}
        />
      );
    }

    return null;
  }

  const practiceProjects = Array.isArray(projects) 
    ? projects.filter((p: any) => p.category === 'intern-onboarding')
    : [];

  const practiceSessions = Array.isArray(progressData) 
    ? progressData.filter((p: any) => p.mode === 'practice')
    : [];

  const inProgressSessions = practiceSessions.filter((p: any) => p.status === 'in_progress');
  const completedSessions = practiceSessions.filter((p: any) => p.status === 'completed');

  const renderWizardSteps = () => (
    <div className="flex items-center justify-center mb-8">
      <div className="flex items-center gap-4">
        <div className={`flex items-center gap-2 ${wizardStep === 'project' ? 'text-teal-600' : 'text-gray-400'}`}>
          <div className={`w-8 h-8 rounded-full flex items-center justify-center text-sm font-medium ${
            wizardStep === 'project' ? 'bg-teal-600 text-white' : 
            selectedProject ? 'bg-teal-100 text-teal-600 border border-teal-600' : 'bg-gray-200 text-gray-500'
          }`}>
            {selectedProject ? '' : '1'}
          </div>
          <span className="font-medium">Project</span>
        </div>

        <div className="w-12 h-0.5 bg-gray-200" />

        <div className={`flex items-center gap-2 ${wizardStep === 'role' ? 'text-teal-600' : 'text-gray-400'}`}>
          <div className={`w-8 h-8 rounded-full flex items-center justify-center text-sm font-medium ${
            wizardStep === 'role' ? 'bg-teal-600 text-white' : 
            selectedRole ? 'bg-teal-100 text-teal-600 border border-teal-600' : 'bg-gray-200 text-gray-500'
          }`}>
            {selectedRole ? '' : '2'}
          </div>
          <span className="font-medium">Role</span>
        </div>

        <div className="w-12 h-0.5 bg-gray-200" />

        <div className={`flex items-center gap-2 ${wizardStep === 'scenario' ? 'text-teal-600' : 'text-gray-400'}`}>
          <div className={`w-8 h-8 rounded-full flex items-center justify-center text-sm font-medium ${
            wizardStep === 'scenario' ? 'bg-teal-600 text-white' : 'bg-gray-200 text-gray-500'
          }`}>
            3
          </div>
          <span className="font-medium">Scenario</span>
        </div>
      </div>
    </div>
  );

  const renderProjectStep = () => (
    <div>
      <h2 className="text-lg font-semibold text-gray-900 mb-4">Select a Project</h2>
      {projectsLoading ? (
        <div className="text-center py-12">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-teal-600 mx-auto"></div>
          <p className="mt-4 text-gray-600">Loading projects...</p>
        </div>
      ) : (
        <div className="grid md:grid-cols-2 gap-4">
          {practiceProjects.map((project: any) => (
            <Card 
              key={project.id}
              className={`cursor-pointer transition-all hover:shadow-lg ${
                selectedProject?.id === project.id ? 'ring-2 ring-teal-500 shadow-lg bg-teal-50/30' : ''
              }`}
              onClick={() => handleSelectProject(project)}
              data-testid={`card-practice-project-${project.id}`}
            >
              <CardContent className="p-6">
                <div className="flex items-start gap-4">
                  <div className="p-3 bg-teal-100 rounded-xl">
                    <Building2 className="h-6 w-6 text-teal-600" />
                  </div>
                  <div className="flex-1">
                    <div className="flex items-center justify-between mb-1">
                      <h3 className="font-semibold text-gray-900">{project.name}</h3>
                      <Badge className="bg-blue-100 text-blue-800">{project.difficulty}</Badge>
                    </div>
                    <p className="text-sm text-gray-600 mb-3">{project.description}</p>
                    <div className="flex items-center gap-4 text-xs text-gray-500">
                      <span className="flex items-center gap-1">
                        <Users className="h-3 w-3" />
                        {project.teamStructure?.length || 0} team
                      </span>
                      <span className="flex items-center gap-1">
                        <Calendar className="h-3 w-3" />
                        5 days
                      </span>
                    </div>
                  </div>
                </div>
              </CardContent>
            </Card>
          ))}

          {practiceProjects.length === 0 && (
            <Card className="col-span-2 p-8 text-center">
              <Target className="h-12 w-12 text-gray-300 mx-auto mb-4" />
              <h3 className="font-medium text-gray-900 mb-2">No Projects Available</h3>
              <p className="text-gray-600">Check back soon for practice projects.</p>
            </Card>
          )}
        </div>
      )}
    </div>
  );

  const renderRoleStep = () => (
    <div>
      <Button variant="ghost" size="sm" onClick={handleBack} className="mb-4">
        <ChevronLeft className="h-4 w-4 mr-1" />
        Back
      </Button>

      <h2 className="text-lg font-semibold text-gray-900 mb-2">Select Your Role</h2>
      <p className="text-gray-600 mb-4">For: {selectedProject?.name}</p>

      {rolesLoading ? (
        <div className="flex items-center justify-center py-8">
          <Loader2 className="h-6 w-6 animate-spin text-teal-600" />
          <span className="ml-2 text-gray-500">Loading roles...</span>
        </div>
      ) : (
        <div className="grid md:grid-cols-3 lg:grid-cols-5 gap-4">
          {availableRoles.map((role: any) => {
            const Icon = role.icon || Code;
            return (
              <Card 
                key={role.name}
                className={`transition-all ${
                  role.available 
                    ? `cursor-pointer hover:shadow-lg ${selectedRole === role.name ? 'ring-2 ring-teal-500 shadow-lg bg-teal-50/30' : ''}`
                    : 'opacity-50 cursor-not-allowed'
                }`}
                onClick={() => role.available && handleSelectRole(role.name)}
                data-testid={`card-practice-role-${role.role || role.name.toLowerCase().replace(' ', '-')}`}
              >
                <CardContent className="p-6">
                  <div className="flex items-center justify-between mb-3">
                    <div className={`p-2 rounded-lg ${role.available ? 'bg-teal-100' : 'bg-gray-100'}`}>
                      <Icon className={`h-5 w-5 ${role.available ? 'text-teal-600' : 'text-gray-400'}`} />
                    </div>
                    {!role.available && (
                      <Badge variant="secondary" className="text-xs">Coming Soon</Badge>
                    )}
                  </div>
                  <h3 className="font-semibold text-gray-900 mb-1">{role.name}</h3>
                  <p className="text-sm text-gray-600">{role.description}</p>
                </CardContent>
              </Card>
            );
          })}
        </div>
      )}
    </div>
  );

  const renderScenarioStep = () => (
    <div>
      <Button variant="ghost" size="sm" onClick={handleBack} className="mb-4">
        <ChevronLeft className="h-4 w-4 mr-1" />
        Back
      </Button>

      <h2 className="text-lg font-semibold text-gray-900 mb-2">Select a Scenario</h2>
      <p className="text-gray-600 mb-4">{selectedProject?.name}  {selectedRole}</p>

      <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-4 mb-6">
        {SCENARIOS.map((scenario) => {
          const Icon = scenario.icon;
          return (
            <Card 
              key={scenario.id}
              className={`transition-all ${
                scenario.available 
                  ? `cursor-pointer hover:shadow-lg ${selectedScenario?.id === scenario.id ? 'ring-2 ring-teal-500 shadow-lg bg-teal-50/30' : ''}`
                  : 'opacity-60 cursor-not-allowed relative'
              }`}
              onClick={() => handleSelectScenario(scenario)}
              data-testid={`card-practice-scenario-${scenario.id}`}
            >
              {!scenario.available && (
                <div className="absolute inset-0 bg-gray-100/50 rounded-lg flex items-center justify-center z-10">
                  <div className="text-center p-4">
                    <Lock className="h-5 w-5 text-gray-400 mx-auto mb-2" />
                    <p className="text-xs text-gray-500">{scenario.prerequisite}</p>
                  </div>
                </div>
              )}
              <CardContent className="p-5">
                <div className="flex items-center gap-3 mb-3">
                  <div className={`p-2 rounded-lg ${scenario.available ? 'bg-teal-100' : 'bg-gray-100'}`}>
                    <Icon className={`h-5 w-5 ${scenario.available ? 'text-teal-600' : 'text-gray-400'}`} />
                  </div>
                  <Badge variant="outline" className="text-xs">{scenario.duration}</Badge>
                </div>
                <h3 className="font-semibold text-gray-900 mb-1">{scenario.title}</h3>
                <p className="text-sm text-gray-600 mb-3">{scenario.description}</p>
                <div className="flex flex-wrap gap-1">
                  {scenario.skills.map((skill) => (
                    <Badge key={skill} variant="secondary" className="text-xs bg-gray-100">
                      {skill}
                    </Badge>
                  ))}
                </div>
              </CardContent>
            </Card>
          );
        })}
      </div>

      {selectedScenario && (
        <div className="flex justify-end">
          <Button 
            onClick={handleStartPractice}
            disabled={createSessionMutation.isPending}
            className="bg-teal-600 hover:bg-teal-700"
            data-testid="button-start-practice"
          >
            {createSessionMutation.isPending ? 'Starting...' : (
              <>
                Start Practice
                <ChevronRight className="h-4 w-4 ml-2" />
              </>
            )}
          </Button>
        </div>
      )}
    </div>
  );

  return (
    <div className="p-8">
      <div className="max-w-6xl mx-auto">
        <div className="mb-8">
          <div className="flex items-center gap-3 mb-2">
            <div className="p-2 bg-teal-100 rounded-lg">
              <Target className="h-6 w-6 text-teal-600" />
            </div>
            <h1 className="text-3xl font-bold text-gray-900" data-testid="text-workspace-practice-title">
              Workspace Practice
            </h1>
          </div>
          <p className="text-gray-600 mt-2">
            Pick any day, any company. Build specific skills without completing the full journey.
          </p>
        </div>

        {inProgressSessions.length > 0 && wizardStep === 'project' && (
          <div className="mb-8">
            <h2 className="text-lg font-semibold text-gray-900 mb-4">My Practice Workspaces</h2>
            <div className="grid md:grid-cols-2 gap-4">
              {inProgressSessions.map((progress: any) => {
                const project = practiceProjects.find((p: any) => p.id === progress.projectId);
                if (!project) return null;
                const scenario = SCENARIOS.find(s => s.id === progress.scenarioId);

                return (
                  <Card key={progress.id} className="border-teal-200 bg-gradient-to-r from-teal-50 to-cyan-50">
                    <CardContent className="p-5">
                      <div className="flex items-start justify-between mb-3">
                        <div>
                          <div className="flex items-center gap-2 mb-1">
                            <span className="font-medium text-gray-900">{project.name}</span>
                            <span className="text-gray-400"></span>
                            <span className="text-gray-600">{progress.role}</span>
                          </div>
                          <h3 className="font-semibold text-gray-900">{scenario?.title || `Day ${progress.currentDay}`}</h3>
                        </div>
                        <Badge className="bg-amber-100 text-amber-800">In Progress</Badge>
                      </div>

                      <div className="flex items-center gap-3 mb-4">
                        <Progress value={progress.overallProgress} className="flex-1 h-2" />
                        <span className="text-sm text-gray-600">{progress.overallProgress}%</span>
                      </div>

                      <div className="flex items-center justify-between">
                        <span className="text-xs text-gray-500">
                          Started {new Date(progress.startedAt).toLocaleDateString()}
                        </span>
                        <div className="flex gap-2">
                          <Button
                            size="sm"
                            onClick={() => handleResumePractice(progress, project)}
                            className="bg-teal-600 hover:bg-teal-700"
                            data-testid={`button-resume-practice-${progress.id}`}
                          >
                            <Play className="h-3 w-3 mr-1" />
                            Resume
                          </Button>
                          <DropdownMenu>
                            <DropdownMenuTrigger asChild>
                              <Button variant="ghost" size="sm">
                                <MoreVertical className="h-4 w-4" />
                              </Button>
                            </DropdownMenuTrigger>
                            <DropdownMenuContent align="end">
                              <DropdownMenuItem>
                                <RotateCcw className="h-4 w-4 mr-2" />
                                Restart
                              </DropdownMenuItem>
                            </DropdownMenuContent>
                          </DropdownMenu>
                        </div>
                      </div>
                    </CardContent>
                  </Card>
                );
              })}
            </div>
          </div>
        )}

        {completedSessions.length > 0 && wizardStep === 'project' && (
          <div className="mb-8">
            <h2 className="text-lg font-semibold text-gray-900 mb-4">Completed Practice Sessions</h2>
            <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-4">
              {completedSessions.slice(0, 6).map((progress: any) => {
                const project = practiceProjects.find((p: any) => p.id === progress.projectId);
                if (!project) return null;
                const scenario = SCENARIOS.find(s => s.id === progress.scenarioId);

                return (
                  <Card key={progress.id} className="bg-gray-50">
                    <CardContent className="p-4">
                      <div className="flex items-start justify-between mb-2">
                        <div>
                          <p className="text-xs text-gray-500">{project.name}  {progress.role}</p>
                          <h3 className="font-medium text-gray-900">{scenario?.title || `Day ${progress.currentDay}`}</h3>
                        </div>
                        <div className="flex items-center gap-1">
                          <CheckCircle2 className="h-4 w-4 text-green-600" />
                          {progress.score && (
                            <span className="text-sm font-medium text-green-600">{progress.score}%</span>
                          )}
                        </div>
                      </div>
                      <p className="text-xs text-gray-500">
                        Completed {progress.completedAt ? new Date(progress.completedAt).toLocaleDateString() : 'recently'}
                      </p>
                    </CardContent>
                  </Card>
                );
              })}
            </div>
          </div>
        )}

        <Card className="p-6">
          {renderWizardSteps()}

          {wizardStep === 'project' && renderProjectStep()}
          {wizardStep === 'role' && renderRoleStep()}
          {wizardStep === 'scenario' && renderScenarioStep()}
        </Card>
      </div>
    </div>
  );
}

server/services/team-interview-questions.content:
import { ExperienceLevel, TeamInterviewPersona } from '@shared/schema';

export interface TeamInterviewQuestion {
  id: string;
  category: 'warmup' | 'learning' | 'collaboration' | 'technical' | 'curiosity';
  personaRole: string; // Which persona typically asks this
  question: string;
  followUps: string[]; // Possible follow-up questions
  lookingFor: string[]; // What a good answer includes
  redFlags: string[]; // Warning signs in answers
  sampleArtifact?: string; // Optional code/scenario to discuss
}

export const INTERN_QUESTIONS: TeamInterviewQuestion[] = [
  // WARM-UP / MOTIVATION (First question - sets the tone)
  {
    id: 'intern_warmup_1',
    category: 'warmup',
    personaRole: 'peer_engineer',
    question: "What got you interested in software engineering in the first place?",
    followUps: [
      "Was there a specific moment or project that sparked that interest?",
      "What do you enjoy most about coding so far?",
    ],
    lookingFor: [
      'Genuine enthusiasm for building things',
      'Can articulate what draws them to the field',
      'Shows curiosity about technology',
      'Personal connection to the work',
    ],
    redFlags: [
      'Only mentions salary or job security',
      'Cannot explain any interest in the work itself',
      'Seems disengaged when talking about coding',
    ],
  },
  {
    id: 'intern_warmup_2',
    category: 'warmup',
    personaRole: 'peer_engineer',
    question: "What excites you most about this opportunity?",
    followUps: [
      "What are you hoping to learn or work on?",
      "What kind of projects interest you?",
    ],
    lookingFor: [
      'Has researched the company/role',
      'Shows enthusiasm for learning',
      'Connects their interests to the opportunity',
      'Has realistic expectations',
    ],
    redFlags: [
      'Generic answer that could apply anywhere',
      'No awareness of what the role involves',
      'Only focused on what they can get, not contribute',
    ],
  },
  {
    id: 'intern_warmup_3',
    category: 'warmup',
    personaRole: 'tech_lead',
    question: "Tell me a bit about your journey into tech so far - how did you get here?",
    followUps: [
      "What made you choose this path?",
      "What's been the most rewarding part so far?",
    ],
    lookingFor: [
      'Clear narrative of their path',
      'Shows self-awareness about their journey',
      'Demonstrates initiative in learning',
      'Authentic and thoughtful response',
    ],
    redFlags: [
      'Cannot explain their own choices',
      'Seems to have fallen into it accidentally with no interest',
      'Dismissive of their own learning journey',
    ],
  },

  // LEARNING & GROWTH (40% of interview)
  {
    id: 'intern_learn_1',
    category: 'learning',
    personaRole: 'peer_engineer',
    question: "When you're learning something completely new - like a framework or language you've never used - how do you typically approach it?",
    followUps: [
      "Can you give me a specific example of when you did this?",
      "What resources do you find most helpful?",
      "How do you know when you've learned enough to start building?",
    ],
    lookingFor: [
      'Has a structured approach (docs, tutorials, small projects)',
      'Mentions hands-on practice',
      'Shows awareness of different learning resources',
      'Balances learning and doing',
    ],
    redFlags: [
      'Only mentions watching videos passively',
      'No concrete examples',
      'Claims to never struggle with learning',
    ],
  },
  {
    id: 'intern_learn_2',
    category: 'learning',
    personaRole: 'tech_lead',
    question: "Tell me about a time you were stuck on a problem for a while. What did you do?",
    followUps: [
      "At what point did you decide to ask for help?",
      "Looking back, would you do anything differently?",
      "What did you learn from that experience?",
    ],
    lookingFor: [
      'Tried multiple approaches before giving up',
      'Knows when to ask for help (not too early, not too late)',
      'Can articulate what they tried',
      'Learned something from the experience',
    ],
    redFlags: [
      'Immediately asks for help without trying',
      'Spends days stuck without seeking help',
      'Gets frustrated and gives up',
      'Blames tools or others',
    ],
  },
  {
    id: 'intern_learn_3',
    category: 'learning',
    personaRole: 'peer_engineer',
    question: "What's something technical you've taught yourself recently? Walk me through how you learned it.",
    followUps: [
      "What made you interested in learning that?",
      "What was the hardest part?",
      "Have you used it in a project yet?",
    ],
    lookingFor: [
      'Shows genuine curiosity',
      'Can explain their learning process',
      'Applied what they learned',
      'Specific about what they learned',
    ],
    redFlags: [
      'Can not name anything specific',
      'Only learns what is required for class',
      'Vague about details',
    ],
  },
  {
    id: 'intern_learn_4',
    category: 'learning',
    personaRole: 'tech_lead',
    question: "How do you decide when to keep trying on your own versus asking for help?",
    followUps: [
      "Is there a time limit you set for yourself?",
      "How do you prepare before asking someone for help?",
      "What makes a good question when asking for help?",
    ],
    lookingFor: [
      'Has a reasonable threshold (15-30 min for simple, longer for complex)',
      'Documents what they tried before asking',
      'Asks specific questions, not "it doesn not work"',
      'Respects others time',
    ],
    redFlags: [
      'No sense of when to escalate',
      'Expects others to solve problems for them',
      'Asks vague questions without context',
    ],
  },

  // COLLABORATION BASICS (30% of interview)
  {
    id: 'intern_collab_1',
    category: 'collaboration',
    personaRole: 'peer_engineer',
    question: "Imagine you're working on a shared codebase with a teammate. How would you coordinate your work to avoid stepping on each other's toes?",
    followUps: [
      "What if you accidentally made conflicting changes?",
      "How would you communicate what you're working on?",
      "Have you used version control for group projects before?",
    ],
    lookingFor: [
      'Mentions communication (standups, chat, etc.)',
      'Understands version control basics',
      'Proactive about updates',
      'Thinks about impact on others',
    ],
    redFlags: [
      'Works in isolation without updates',
      'Does not understand why coordination matters',
      'Assumes conflicts will not happen',
    ],
  },
  {
    id: 'intern_collab_2',
    category: 'collaboration',
    personaRole: 'tech_lead',
    question: "If you disagreed with how a teammate wanted to solve a problem, how would you handle it?",
    followUps: [
      "What if they were more senior than you?",
      "How would you make your case?",
      "What if they still disagreed after you explained?",
    ],
    lookingFor: [
      'Seeks to understand their perspective first',
      'Explains their reasoning respectfully',
      'Open to being wrong',
      'Focuses on the best solution, not winning',
    ],
    redFlags: [
      'Avoids conflict entirely',
      'Insists they are right without listening',
      'Would just do it their way anyway',
    ],
  },
  {
    id: 'intern_collab_3',
    category: 'collaboration',
    personaRole: 'peer_engineer',
    question: "How do you prefer to receive feedback on your code or work?",
    followUps: [
      "Can you tell me about a time you got tough feedback?",
      "What kind of feedback is most helpful to you?",
      "How do you give feedback to others?",
    ],
    lookingFor: [
      'Open to constructive criticism',
      'Sees feedback as learning opportunity',
      'Can separate ego from work',
      'Appreciates specific feedback over vague',
    ],
    redFlags: [
      'Gets defensive about feedback',
      'Only wants positive feedback',
      'Takes criticism personally',
    ],
  },
  {
    id: 'intern_collab_4',
    category: 'collaboration',
    personaRole: 'tech_lead',
    question: "What does good communication look like when you're working on code with someone else?",
    followUps: [
      "How often should you sync with teammates?",
      "What information is important to share?",
      "How do you handle async communication?",
    ],
    lookingFor: [
      'Mentions regular check-ins',
      'Shares blockers proactively',
      'Clear about what they are working on',
      'Responsive to messages',
    ],
    redFlags: [
      'Goes silent for long periods',
      'Only communicates when asked',
      'Does not see value in updates',
    ],
  },

  // TECHNICAL FUNDAMENTALS (20% of interview)
  {
    id: 'intern_tech_1',
    category: 'technical',
    personaRole: 'peer_engineer',
    question: "Walk me through how you'd approach debugging a simple issue - say, a button on a webpage that doesn't work when clicked.",
    followUps: [
      "What tools would you use?",
      "How would you narrow down where the problem is?",
      "What if the console showed no errors?",
    ],
    lookingFor: [
      'Checks browser console for errors',
      'Inspects the element',
      'Looks at event handlers',
      'Systematic approach (not random changes)',
    ],
    redFlags: [
      'Random trial and error only',
      'Does not know about browser dev tools',
      'Gives up quickly without investigating',
    ],
    sampleArtifact: `// This button should show an alert when clicked, but it doesn't work
<button onclick="showMessage()">Click me</button>

<script>
function showMessge() {
  alert("Hello!");
}
</script>`,
  },
  {
    id: 'intern_tech_2',
    category: 'technical',
    personaRole: 'tech_lead',
    question: "Before starting a new coding task, what questions would you want answered first?",
    followUps: [
      "What if some requirements were unclear?",
      "How do you handle ambiguity?",
      "When would you start coding vs. ask more questions?",
    ],
    lookingFor: [
      'Asks about expected behavior',
      'Clarifies edge cases',
      'Understands the "why" not just the "what"',
      'Checks for existing similar code',
    ],
    redFlags: [
      'Just starts coding immediately',
      'Assumes they understand everything',
      'Does not think about edge cases',
    ],
  },
  {
    id: 'intern_tech_3',
    category: 'technical',
    personaRole: 'peer_engineer',
    question: "How do you keep your code organized when working on a larger project?",
    followUps: [
      "How do you name things?",
      "What makes code easy to read?",
      "How do you decide when to break something into smaller pieces?",
    ],
    lookingFor: [
      'Meaningful variable/function names',
      'Breaks code into functions',
      'Comments when necessary',
      'Some awareness of structure',
    ],
    redFlags: [
      'Everything in one giant file',
      'Single-letter variable names everywhere',
      'No concept of organization',
    ],
  },

  // CURIOSITY & QUESTIONS (10% of interview)
  {
    id: 'intern_curiosity_1',
    category: 'curiosity',
    personaRole: 'tech_lead',
    question: "What questions do you have about our team or how we work?",
    followUps: [],
    lookingFor: [
      'Asks about learning opportunities',
      'Interested in team dynamics',
      'Curious about the work itself',
      'Thoughtful, prepared questions',
    ],
    redFlags: [
      'No questions at all',
      'Only asks about time off/perks',
      'Questions show no research',
    ],
  },
  {
    id: 'intern_curiosity_2',
    category: 'curiosity',
    personaRole: 'peer_engineer',
    question: "What would you want to learn in your first month here?",
    followUps: [
      "How would you go about learning that?",
      "What's your biggest growth area right now?",
    ],
    lookingFor: [
      'Specific learning goals',
      'Realistic expectations',
      'Eager but humble',
      'Relevant to the role',
    ],
    redFlags: [
      'No thoughts on development',
      'Unrealistic expectations (lead the team quickly)',
      'Only interested in one narrow thing',
    ],
  },
];

export const JUNIOR_QUESTIONS: TeamInterviewQuestion[] = [
  {
    id: 'junior_tech_1',
    category: 'technical',
    personaRole: 'peer_engineer',
    question: "Tell me about a bug you've debugged that took a while to find. What was your process?",
    followUps: [
      "What made it tricky?",
      "What tools did you use?",
      "How did you prevent it from happening again?",
    ],
    lookingFor: [
      'Systematic debugging process',
      'Uses appropriate tools',
      'Documents findings',
      'Thinks about prevention',
    ],
    redFlags: [
      'No real debugging experience',
      'Random trial and error only',
      'Cannot explain their process',
    ],
  },
  {
    id: 'junior_collab_1',
    category: 'collaboration',
    personaRole: 'tech_lead',
    question: "How do you handle code reviews - both giving and receiving them?",
    followUps: [
      "What makes a helpful code review comment?",
      "How do you respond to feedback you disagree with?",
    ],
    lookingFor: [
      'Sees code review as learning',
      'Gives constructive feedback',
      'Separates ego from code',
    ],
    redFlags: [
      'Gets defensive',
      'Harsh or unconstructive feedback',
      'Rubber-stamps everything',
    ],
  },
];

export function getQuestionsForLevel(level: ExperienceLevel): TeamInterviewQuestion[] {
  switch (level) {
    case 'intern':
      return INTERN_QUESTIONS;
    case 'junior':
      return [...JUNIOR_QUESTIONS, ...INTERN_QUESTIONS.filter(q => 
        q.category === 'collaboration' || q.category === 'learning'
      )];
    default:
      return INTERN_QUESTIONS;
  }
}

export function getQuestionsForPersona(
  questions: TeamInterviewQuestion[],
  persona: TeamInterviewPersona
): TeamInterviewQuestion[] {
  return questions.filter(q => 
    q.personaRole === persona.role || persona.focusAreas.includes(q.category)
  );
}

export function selectQuestionsForInterview(
  level: ExperienceLevel,
  weights: { learning: number; collaboration: number; technical: number; curiosity: number },
  maxQuestions: number
): TeamInterviewQuestion[] {
  const allQuestions = getQuestionsForLevel(level);
  const selected: TeamInterviewQuestion[] = [];

  // ALWAYS start with a warmup question (motivation/interest)
  const warmupQuestions = allQuestions.filter(q => q.category === 'warmup');
  if (warmupQuestions.length > 0) {
    const shuffledWarmup = warmupQuestions.sort(() => Math.random() - 0.5);
    selected.push(shuffledWarmup[0]);
  }

  // Then add questions from other categories in progression order:
  // learning  collaboration  technical  curiosity
  const categories: Array<'learning' | 'collaboration' | 'technical' | 'curiosity'> = 
    ['learning', 'collaboration', 'technical', 'curiosity'];

  // Reduce maxQuestions by 1 to account for warmup
  const remainingSlots = maxQuestions - selected.length;

  for (const category of categories) {
    const categoryQuestions = allQuestions.filter(q => q.category === category);
    const targetCount = Math.round((weights[category] / 100) * remainingSlots);

    const shuffled = categoryQuestions.sort(() => Math.random() - 0.5);
    selected.push(...shuffled.slice(0, targetCount));
  }

  return selected.slice(0, maxQuestions);
}

