      <div className="max-w-[70%]">
        <div className={cn(
          "rounded-lg p-4 text-gray-900 dark:text-gray-100",
          isTeamInterview ? `border-l-4 ${style.borderColor.replace('border-', 'border-l-')}` : "",
          style.messageBg
        )}>
