export type Role = 'developer' | 'pm' | 'qa' | 'devops' | 'data_science';
export type Level = 'intern' | 'junior' | 'mid' | 'senior';
export type Language = 'javascript' | 'python' | 'c_cpp';

export interface InterviewConfig {
  role: Role;
  level: Level;
  focusAreas: string[];
  panelRoles: string[];
  questionTypes: string[];
  difficultyCalibration: {
    complexity: string;
    scaffolding: string;
    expectations: string[];
  };
  evaluationCriteria: {
    strictness: number;
    passingThreshold: number;
    criticalSkills: string[];
  };
}

export interface WorkspaceConfig {
  role: Role;
  level: Level;
  language: Language;
  focusAreas: string[];
  teamInteractions: string[];
  problemTypes: string[];
  toolchain: string[];
  frameworks: string[];
  difficultyCalibration: {
    complexity: string;
    scaffolding: string;
    expectations: string[];
  };
}

export interface InterviewQuestionConfig {
  questionTypes: string[];
  difficulty: string;
  focusAreas: string[];
  panelRoles: string[];
  levelAdjustments: {
    difficultyLabel: string;
    questionDepth: 'surface' | 'moderate' | 'deep' | 'expert';
