import { useState, useMemo } from "react";
import { useParams, Link } from "wouter";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Progress } from "@/components/ui/progress";
import { Separator } from "@/components/ui/separator";
import { Skeleton } from "@/components/ui/skeleton";
import { 
  ArrowLeft,
  GitBranch, 
  GitPullRequest,
  CheckCircle2, 
  Clock, 
  Target,
  Users,
  Play,
  AlertCircle,
  ExternalLink,
  ChevronRight,
  Trophy,
} from "lucide-react";
import { 
  useSprintOverview, 
  useKanbanState,
  useMoveTicket,
  useJourneyDashboard,
  useJourneyWorkspace,
  type TicketStatus,
} from "@/hooks/use-sprint-workflow";
import type { SprintTicket } from "@shared/schema";
import { useToast } from "@/hooks/use-toast";
import { getSprintExecutionAdapter } from "@shared/adapters/execution";
import type { Role, Level } from "@shared/adapters";

type TicketGitState = {
  branchCreated?: boolean;
  branchName?: string;
  hasPR?: boolean;
  prTitle?: string;
  merged?: boolean;
};

function TicketCard({ 
  ticket, 
  sprintId,
  onSelect 
}: { 
  ticket: SprintTicket;
  sprintId: number;
  onSelect: (ticket: SprintTicket) => void;
}) {
  const gitState = ticket.gitState as TicketGitState | null;

  const priorityColors = {
    critical: 'bg-red-500',
    high: 'bg-orange-500',
    medium: 'bg-blue-500',
    low: 'bg-gray-400',
  };

  return (
    <Card 
      className="cursor-pointer hover:shadow-md transition-shadow"
      onClick={() => onSelect(ticket)}
      data-testid={`card-ticket-${ticket.id}`}
    >
      <CardContent className="p-3 space-y-2">
        <div className="flex items-start justify-between gap-2">
          <Badge variant="outline" className="text-xs font-mono">
            {ticket.ticketKey}
          </Badge>
          <div 
            className={`h-2 w-2 rounded-full ${priorityColors[ticket.priority as keyof typeof priorityColors] || priorityColors.medium}`}
            title={`Priority: ${ticket.priority}`}
          />
        </div>

        <p className="text-sm font-medium line-clamp-2">{ticket.title}</p>

        <div className="flex items-center justify-between text-xs text-muted-foreground">
          <div className="flex items-center gap-2">
            <Badge variant="secondary" className="text-xs">
              {ticket.storyPoints || 0} pts
            </Badge>
            <Badge variant="outline" className="capitalize text-xs">
              {ticket.type}
            </Badge>
          </div>

          <div className="flex items-center gap-1">
            {gitState?.branchCreated && (
              <GitBranch className="h-3 w-3 text-blue-500" />
            )}
            {gitState?.hasPR && (
              <GitPullRequest className="h-3 w-3 text-purple-500" />
            )}
            {gitState?.merged && (
              <CheckCircle2 className="h-3 w-3 text-green-500" />
            )}
          </div>
        </div>
      </CardContent>
    </Card>
  );
}

function KanbanColumn({ 
  title, 
  status, 
  tickets,
  sprintId,
  count,
  onSelectTicket,
  onDropTicket,
}: {
  title: string;
  status: TicketStatus;
  tickets: SprintTicket[];
  sprintId: number;
  count: number;
  onSelectTicket: (ticket: SprintTicket) => void;
  onDropTicket: (ticketId: number, newStatus: TicketStatus) => void;
}) {
  const [isDragOver, setIsDragOver] = useState(false);

  const handleDragOver = (e: React.DragEvent) => {
    e.preventDefault();
    setIsDragOver(true);
  };

  const handleDragLeave = () => {
    setIsDragOver(false);
  };

  const handleDrop = (e: React.DragEvent) => {
    e.preventDefault();
    setIsDragOver(false);
    const ticketId = parseInt(e.dataTransfer.getData('ticketId'));
    if (!isNaN(ticketId)) {
      onDropTicket(ticketId, status);
    }
  };

  const statusColors = {
    todo: 'border-t-gray-400',
    in_progress: 'border-t-blue-500',
    in_review: 'border-t-amber-500',
    done: 'border-t-green-500',
  };

  return (
    <div 
      className={`flex flex-col bg-muted/30 rounded-lg overflow-hidden border-t-4 ${statusColors[status]} ${
        isDragOver ? 'ring-2 ring-blue-400' : ''
      }`}
      onDragOver={handleDragOver}
      onDragLeave={handleDragLeave}
      onDrop={handleDrop}
      data-testid={`column-${status}`}
    >
      <div className="p-3 bg-background border-b">
        <div className="flex items-center justify-between">
          <h3 className="font-semibold text-sm">{title}</h3>
          <Badge variant="secondary" className="text-xs">
            {count}
          </Badge>
        </div>
      </div>

      <div className="flex-1 p-2 space-y-2 min-h-[200px] overflow-y-auto">
        {tickets.map(ticket => (
          <div
            key={ticket.id}
            draggable
            onDragStart={(e) => {
              e.dataTransfer.setData('ticketId', ticket.id.toString());
            }}
          >
            <TicketCard
              ticket={ticket}
              sprintId={sprintId}
              onSelect={onSelectTicket}
            />
          </div>
        ))}

        {tickets.length === 0 && (
          <div className="h-full flex items-center justify-center text-sm text-muted-foreground">
            No tickets
          </div>
        )}
      </div>
    </div>
  );
}

function TicketDetailPanel({ 
  ticket, 
  sprintId,
  onClose 
}: { 
  ticket: SprintTicket;
  sprintId: number;
  onClose: () => void;
}) {
  const params = useParams<{ journeyId: string }>();
  const journeyId = params.journeyId;
  const gitState = ticket.gitState as TicketGitState | null;

  return (
    <Card className="sticky top-4" data-testid="panel-ticket-detail">
      <CardHeader className="pb-3">
        <div className="flex items-start justify-between">
          <div>
            <Badge variant="outline" className="mb-2 font-mono">
              {ticket.ticketKey}
            </Badge>
            <CardTitle className="text-lg">{ticket.title}</CardTitle>
          </div>
          <Button variant="ghost" size="sm" onClick={onClose}>

          </Button>
        </div>
      </CardHeader>
      <CardContent className="space-y-4">
        <p className="text-sm text-muted-foreground">
          {ticket.description}
        </p>

        <div className="grid grid-cols-2 gap-3 text-sm">
          <div>
            <p className="text-muted-foreground">Status</p>
            <Badge className="mt-1 capitalize">{ticket.status.replace('_', ' ')}</Badge>
          </div>
          <div>
            <p className="text-muted-foreground">Type</p>
            <Badge variant="outline" className="mt-1 capitalize">{ticket.type}</Badge>
          </div>
          <div>
            <p className="text-muted-foreground">Priority</p>
            <Badge variant="secondary" className="mt-1 capitalize">{ticket.priority}</Badge>
          </div>
          <div>
            <p className="text-muted-foreground">Story Points</p>
            <Badge variant="secondary" className="mt-1">{ticket.storyPoints || 0}</Badge>
          </div>
        </div>

        <Separator />

        <div>
          <h4 className="text-sm font-medium mb-2 flex items-center gap-2">
            <GitBranch className="h-4 w-4" />
            Git Workflow
          </h4>
          <div className="space-y-2 text-sm">
            <div className="flex items-center gap-2">
              {gitState?.branchCreated ? (
                <CheckCircle2 className="h-4 w-4 text-green-500" />
              ) : (
                <AlertCircle className="h-4 w-4 text-muted-foreground" />
              )}
              <span className={gitState?.branchCreated ? '' : 'text-muted-foreground'}>
                Branch created
              </span>
            </div>
            <div className="flex items-center gap-2">
              {gitState?.hasPR ? (
                <CheckCircle2 className="h-4 w-4 text-green-500" />
              ) : (
                <AlertCircle className="h-4 w-4 text-muted-foreground" />
              )}
              <span className={gitState?.hasPR ? '' : 'text-muted-foreground'}>
                Pull request submitted
              </span>
            </div>
            <div className="flex items-center gap-2">
              {gitState?.merged ? (
                <CheckCircle2 className="h-4 w-4 text-green-500" />
              ) : (
                <AlertCircle className="h-4 w-4 text-muted-foreground" />
              )}
              <span className={gitState?.merged ? '' : 'text-muted-foreground'}>
                Merged to main
              </span>
            </div>
          </div>
        </div>

        <Separator />

        <Link href={`/journey/${journeyId}/sprint/${sprintId}/ticket/${ticket.id}`}>
          <Button className="w-full gap-2" data-testid="button-work-on-ticket">
            <Play className="h-4 w-4" />
            Work on Ticket
            <ChevronRight className="h-4 w-4 ml-auto" />
          </Button>
        </Link>
      </CardContent>
    </Card>
  );
}

export default function SprintHub() {
  const params = useParams<{ journeyId: string; sprintId: string }>();
  const journeyId = params.journeyId ? parseInt(params.journeyId) : null;
  const sprintId = params.sprintId ? parseInt(params.sprintId) : null;

  const [selectedTicket, setSelectedTicket] = useState<SprintTicket | null>(null);

  const { data: overview, isLoading } = useSprintOverview(sprintId);
  const { data: journeyDashboard } = useJourneyDashboard(journeyId);
  const { data: workspace } = useJourneyWorkspace(journeyId);
  const { ticketsByStatus } = useKanbanState(sprintId);
  const moveTicket = useMoveTicket();
  const { toast } = useToast();

  const adapter = useMemo(() => {
    const role = (journeyDashboard?.journey?.journeyMetadata as any)?.role || 'developer';
    const level = (journeyDashboard?.journey?.journeyMetadata as any)?.entryLevel || 'intern';
    return getSprintExecutionAdapter(role as Role, level as Level);
  }, [journeyDashboard]);

  const handleMoveTicket = (ticketId: number, newStatus: TicketStatus) => {
    moveTicket.mutate(
      { ticketId, newStatus, sprintId: sprintId || undefined },
      {
        onError: (error) => {
          toast({
            title: "Cannot move ticket",
            description: error.message,
            variant: "destructive",
          });
        },
        onSuccess: () => {
          if (selectedTicket?.id === ticketId) {
            setSelectedTicket(prev => prev ? { ...prev, status: newStatus } : null);
          }
        }
      }
    );
  };

  if (isLoading || !overview) {
    return (
      <div className="container max-w-7xl mx-auto p-6 space-y-6" data-testid="sprint-hub-loading">
        <Skeleton className="h-8 w-64" />
        <div className="grid grid-cols-4 gap-4">
          <Skeleton className="h-96" />
          <Skeleton className="h-96" />
          <Skeleton className="h-96" />
          <Skeleton className="h-96" />
        </div>
      </div>
    );
  }

  const { sprint, currentDay, ticketStats, upcomingCeremonies } = overview;
  const totalPoints = sprint.storyPointsTarget;
  const completedPoints = overview.tickets
    .filter(t => t.status === 'done')
    .reduce((sum, t) => sum + (t.storyPoints || 0), 0);
  const progressPercent = totalPoints > 0 ? Math.round((completedPoints / totalPoints) * 100) : 0;

  const allDone = overview.tickets.length > 0 && overview.tickets.every(t => t.status === 'done');
  const nearComplete = overview.tickets.length > 0 && ticketsByStatus.done.length === overview.tickets.length - 1;

  const sprintConfig = adapter.sprintCompletion;
  const progressMessage = allDone 
    ? sprintConfig.progressMessages.allDone 
    : nearComplete 
      ? sprintConfig.progressMessages.nearComplete 
      : sprintConfig.progressMessages.inProgress;

  return (
    <div className="container max-w-7xl mx-auto p-6 space-y-6" data-testid="sprint-hub">
      <div className="flex items-center gap-4">
        <Link href={`/journey/${journeyId}`}>
          <Button variant="ghost" size="sm" className="gap-2" data-testid="button-back">
            <ArrowLeft className="h-4 w-4" />
            Back to Dashboard
          </Button>
        </Link>
      </div>

      <div className="flex flex-col md:flex-row justify-between items-start md:items-center gap-4">
        <div>
          <h1 className="text-2xl font-bold tracking-tight" data-testid="text-sprint-title">
            {sprint.theme}
          </h1>
          <p className="text-muted-foreground">{sprint.goal}</p>
        </div>

        <div className="flex items-center gap-4">
          <div className="text-right">
            <p className="text-sm text-muted-foreground">Sprint Day</p>
            <p className="text-2xl font-bold" data-testid="text-sprint-day">{currentDay}/10</p>
          </div>
          <Separator orientation="vertical" className="h-10" />
          <div className="text-right">
            <p className="text-sm text-muted-foreground">Story Points</p>
            <p className="text-2xl font-bold" data-testid="text-story-points">
              {completedPoints}/{totalPoints}
            </p>
          </div>
        </div>
      </div>

      <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
        <Card>
          <CardContent className="p-4 flex items-center gap-3">
            <div className="h-10 w-10 rounded-full bg-gray-100 dark:bg-gray-800 flex items-center justify-center">
              <Target className="h-5 w-5 text-gray-600" />
            </div>
            <div>
              <p className="text-sm text-muted-foreground">Progress</p>
              <p className="text-xl font-semibold">{progressPercent}%</p>
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="p-4 flex items-center gap-3">
            <div className="h-10 w-10 rounded-full bg-blue-100 dark:bg-blue-900 flex items-center justify-center">
              <Clock className="h-5 w-5 text-blue-600" />
            </div>
            <div>
              <p className="text-sm text-muted-foreground">In Progress</p>
              <p className="text-xl font-semibold">{ticketStats.inProgress}</p>
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="p-4 flex items-center gap-3">
            <div className="h-10 w-10 rounded-full bg-amber-100 dark:bg-amber-900 flex items-center justify-center">
              <GitPullRequest className="h-5 w-5 text-amber-600" />
            </div>
            <div>
              <p className="text-sm text-muted-foreground">In Review</p>
              <p className="text-xl font-semibold">{ticketStats.inReview}</p>
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="p-4 flex items-center gap-3">
            <div className="h-10 w-10 rounded-full bg-green-100 dark:bg-green-900 flex items-center justify-center">
              <CheckCircle2 className="h-5 w-5 text-green-600" />
            </div>
            <div>
              <p className="text-sm text-muted-foreground">Done</p>
              <p className="text-xl font-semibold">{ticketStats.done}</p>
            </div>
          </CardContent>
        </Card>
      </div>

      {allDone && (
        <Card className="bg-green-50 dark:bg-green-900/20 border-green-200 dark:border-green-800">
          <CardContent className="p-6">
            <div className="flex items-center justify-between">
              <div className="flex items-center gap-4">
                <div className="h-12 w-12 rounded-full bg-green-100 dark:bg-green-800 flex items-center justify-center">
                  <Trophy className="h-6 w-6 text-green-600 dark:text-green-300" />
                </div>
                <div>
                  <h3 className="font-semibold text-green-800 dark:text-green-200">
                    {sprintConfig.progressMessages.allDone}
                  </h3>
                  {sprintConfig.showTeamMessage && sprintConfig.teamMessage && (
                    <p className="text-sm text-green-600 dark:text-green-400">
                      {sprintConfig.teamMessage}
                    </p>
                  )}
                  <p className="text-xs text-green-500 dark:text-green-500 mt-1">
                    {sprintConfig.completionCTA.description}
                  </p>
                </div>
              </div>
              <Link href={`/workspace/${workspace?.id}/review`}>
                <Button 
                  className="bg-green-600 hover:bg-green-700 gap-2"
                  data-testid="button-complete-sprint"
                  disabled={!workspace?.id}
                >
                  {sprintConfig.completionCTA.label}
                  <ChevronRight className="h-4 w-4" />
                </Button>
              </Link>
            </div>
          </CardContent>
        </Card>
      )}

      {!allDone && nearComplete && (
        <Card className="border-amber-200 dark:border-amber-800 bg-amber-50/50 dark:bg-amber-900/10">
          <CardContent className="p-4">
            <div className="flex items-center gap-4">
              <Target className="h-5 w-5 text-amber-600" />
              <p className="text-sm font-medium text-amber-700 dark:text-amber-300">
                {sprintConfig.progressMessages.nearComplete}
              </p>
            </div>
          </CardContent>
        </Card>
      )}

      {!allDone && upcomingCeremonies.length > 0 && upcomingCeremonies[0].ceremonyType === 'standup' && (
        <Card className="border-blue-200 dark:border-blue-800 bg-blue-50/50 dark:bg-blue-950/50">
          <CardContent className="p-4">
            <div className="flex items-center justify-between">
              <div className="flex items-center gap-3">
                <Users className="h-5 w-5 text-blue-600" />
                <div>
                  <p className="font-medium">Daily Standup</p>
                  <p className="text-sm text-muted-foreground">
                    Share your progress with the team
                  </p>
                </div>
              </div>
              <Link href={`/workspace/${workspace?.id}/standup?journeyId=${journeyId}`}>
                <Button className="gap-2" data-testid="button-start-ceremony">
                  <Play className="h-4 w-4" />
                  Start
                </Button>
              </Link>
            </div>
          </CardContent>
        </Card>
      )}

      <div className="flex gap-6">
        <div className={`flex-1 grid grid-cols-4 gap-4 ${selectedTicket ? 'lg:w-2/3' : 'w-full'}`}>
          <KanbanColumn
            title="To Do"
            status="todo"
            tickets={ticketsByStatus.todo}
            sprintId={sprintId || 0}
            count={ticketStats.todo}
            onSelectTicket={setSelectedTicket}
            onDropTicket={handleMoveTicket}
          />
          <KanbanColumn
            title="In Progress"
            status="in_progress"
            tickets={ticketsByStatus.in_progress}
            sprintId={sprintId || 0}
            count={ticketStats.inProgress}
            onSelectTicket={setSelectedTicket}
            onDropTicket={handleMoveTicket}
          />
          <KanbanColumn
            title="In Review"
            status="in_review"
            tickets={ticketsByStatus.in_review}
            sprintId={sprintId || 0}
            count={ticketStats.inReview}
            onSelectTicket={setSelectedTicket}
            onDropTicket={handleMoveTicket}
          />
          <KanbanColumn
            title="Done"
            status="done"
            tickets={ticketsByStatus.done}
            sprintId={sprintId || 0}
            count={ticketStats.done}
            onSelectTicket={setSelectedTicket}
            onDropTicket={handleMoveTicket}
          />
        </div>

        {selectedTicket && (
          <div className="w-80 shrink-0">
            <TicketDetailPanel
              ticket={selectedTicket}
              sprintId={sprintId || 0}
              onClose={() => setSelectedTicket(null)}
            />
          </div>
        )}
      </div>

      <Progress value={currentDay * 10} className="h-2" />
      <p className="text-xs text-muted-foreground text-center">
        Day {currentDay} of 10 - {10 - currentDay} days remaining in sprint
      </p>
    </div>
  );
}

client/src/pages/journey-dashboard.tsx.content:
import { useParams, Link } from "wouter";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Progress } from "@/components/ui/progress";
import { Separator } from "@/components/ui/separator";
import { Skeleton } from "@/components/ui/skeleton";
import { 
  Calendar, 
  GitBranch, 
  CheckCircle2, 
  Clock, 
  Target, 
  TrendingUp,
  Users,
  Play,
  ArrowRight,
  Trophy,
  BarChart3,
  Rocket,
  Zap,
  Building2,
  Briefcase,
} from "lucide-react";
import { 
  useJourneyDashboard, 
  useJourneyWorkspace,
  type MasteryBand,
  type WorkspacePhase,
} from "@/hooks/use-sprint-workflow";

function getBandColor(band: MasteryBand): string {
  switch (band) {
    case 'explorer': return 'bg-amber-100 text-amber-800 dark:bg-amber-900 dark:text-amber-200';
    case 'contributor': return 'bg-blue-100 text-blue-800 dark:bg-blue-900 dark:text-blue-200';
    case 'junior_ready': return 'bg-green-100 text-green-800 dark:bg-green-900 dark:text-green-200';
    default: return 'bg-gray-100 text-gray-800';
  }
}

function getBandLabel(band: MasteryBand): string {
  switch (band) {
    case 'explorer': return 'Explorer';
    case 'contributor': return 'Contributor';
    case 'junior_ready': return 'Junior Ready';
    default: return band;
  }
}

function formatDate(date: Date | string): string {
  return new Date(date).toLocaleDateString('en-US', {
    month: 'short',
    day: 'numeric',
  });
}

function getPhaseLabel(phase: WorkspacePhase): string {
  switch (phase) {
    case 'onboarding': return 'Onboarding';
    case 'planning': return 'Sprint Planning';
    case 'execution': return 'Sprint Execution';
    case 'review': return 'Sprint Review';
    case 'retro': return 'Sprint Retrospective';
    default: return phase;
  }
}

function getPhaseColor(phase: WorkspacePhase): string {
  switch (phase) {
    case 'onboarding': return 'bg-teal-500';
    case 'planning': return 'bg-indigo-500';
    case 'execution': return 'bg-blue-500';
    case 'review': return 'bg-amber-500';
    case 'retro': return 'bg-violet-500';
    default: return 'bg-gray-500';
  }
}

function getPhaseBadgeVariant(phase: WorkspacePhase): string {
  switch (phase) {
    case 'onboarding': return 'bg-teal-100 text-teal-800 dark:bg-teal-900 dark:text-teal-200';
    case 'planning': return 'bg-indigo-100 text-indigo-800 dark:bg-indigo-900 dark:text-indigo-200';
    case 'execution': return 'bg-blue-100 text-blue-800 dark:bg-blue-900 dark:text-blue-200';
    case 'review': return 'bg-amber-100 text-amber-800 dark:bg-amber-900 dark:text-amber-200';
    case 'retro': return 'bg-violet-100 text-violet-800 dark:bg-violet-900 dark:text-violet-200';
    default: return 'bg-gray-100 text-gray-800';
  }
}

export default function JourneyDashboard() {
  const params = useParams<{ journeyId: string }>();
  const journeyId = params.journeyId ? parseInt(params.journeyId) : null;

  const { data: dashboard, isLoading, error } = useJourneyDashboard(journeyId);
  const { data: workspace, isLoading: workspaceLoading } = useJourneyWorkspace(journeyId);

  if (isLoading) {
    return (
      <div className="container max-w-7xl mx-auto p-6 space-y-6" data-testid="journey-dashboard-loading">
        <div className="flex justify-between items-start">
          <div className="space-y-2">
            <Skeleton className="h-8 w-64" />
            <Skeleton className="h-4 w-96" />
          </div>
          <Skeleton className="h-10 w-32" />
        </div>
        <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
          <Skeleton className="h-32" />
          <Skeleton className="h-32" />
          <Skeleton className="h-32" />
        </div>
        <Skeleton className="h-96" />
      </div>
    );
  }

  if (error || !dashboard) {
    return (
      <div className="container max-w-7xl mx-auto p-6" data-testid="journey-dashboard-error">
        <Card className="border-destructive">
          <CardHeader>
            <CardTitle className="text-destructive">Error Loading Journey</CardTitle>
            <CardDescription>
              {error?.message || 'Journey not found. Please check the URL and try again.'}
            </CardDescription>
          </CardHeader>
        </Card>
      </div>
    );
  }

  const { journey, currentArc, currentSprint, readinessScore, competencyScores, timeline, canGraduate, estimatedSprintsRemaining } = dashboard;

  return (
    <div className="container max-w-7xl mx-auto p-6 space-y-6" data-testid="journey-dashboard">
      <div className="flex flex-col md:flex-row justify-between items-start md:items-center gap-4">
        <div>
          <h1 className="text-3xl font-bold tracking-tight" data-testid="text-journey-title">
            Your Journey Dashboard
          </h1>
          <p className="text-muted-foreground mt-1">
            Track your progress toward becoming Junior Ready
          </p>
        </div>

        {canGraduate && (
          <Link href={`/journey/${journeyId}/graduate`}>
            <Button size="lg" className="gap-2" data-testid="button-graduate">
              <Trophy className="h-5 w-5" />
              Graduate Now
            </Button>
          </Link>
        )}
      </div>

      {workspace && (
        <Card 
          className={`border-l-4 ${getPhaseColor(workspace.currentPhase).replace('bg-', 'border-')}`}
          data-testid="card-workspace-hero"
        >
          <CardHeader>
            <div className="flex flex-col md:flex-row justify-between items-start md:items-center gap-4">
              <div className="flex items-center gap-4">
                <div className={`p-3 rounded-lg ${getPhaseColor(workspace.currentPhase)}`}>
                  <Building2 className="h-6 w-6 text-white" />
                </div>
                <div>
                  <CardTitle className="flex items-center gap-2" data-testid="text-workspace-company">
                    {workspace.companyName} Workspace
                  </CardTitle>
                  <CardDescription className="flex items-center gap-2 mt-1">
                    <Briefcase className="h-4 w-4" />
                    <span data-testid="text-workspace-role">{workspace.role}</span>
                    <span className="text-muted-foreground"></span>
                    <Badge 
                      className={getPhaseBadgeVariant(workspace.currentPhase)}
                      data-testid="badge-workspace-phase"
                    >
                      {getPhaseLabel(workspace.currentPhase)}
                    </Badge>
                  </CardDescription>
                </div>
              </div>
              <Link href={`/workspace/${workspace.id}`}>
                <Button className="gap-2" size="lg" data-testid="button-open-workspace">
                  <Play className="h-4 w-4" />
                  Open Workspace
                  <ArrowRight className="h-4 w-4" />
                </Button>
              </Link>
            </div>
          </CardHeader>
          <CardContent>
            <div className="flex items-center gap-2 text-sm text-muted-foreground">
              <span>Current Phase:</span>
              <div className="flex items-center gap-1">
                {['onboarding', 'planning', 'execution', 'review', 'retro'].map((phase, index) => (
                  <div key={phase} className="flex items-center">
                    {index > 0 && <div className="w-4 h-0.5 bg-muted" />}
                    <div 
                      className={`w-2 h-2 rounded-full ${
                        phase === workspace.currentPhase 
                          ? getPhaseColor(phase as WorkspacePhase)
                          : 'bg-muted'
                      }`}
                    />
                  </div>
                ))}
              </div>
            </div>
          </CardContent>
        </Card>
      )}

      <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
        <Card data-testid="card-readiness">
          <CardHeader className="pb-2">
            <CardTitle className="text-sm font-medium text-muted-foreground flex items-center gap-2">
              <Target className="h-4 w-4" />
              Readiness Score
            </CardTitle>
          </CardHeader>
          <CardContent>
            <div className="flex items-end gap-2">
              <span className="text-4xl font-bold" data-testid="text-readiness-score">{readinessScore}%</span>
              <span className="text-sm text-muted-foreground mb-1">of 85% target</span>
            </div>
            <Progress value={readinessScore} max={100} className="mt-3 h-2" />
            {readinessScore >= 85 ? (
              <p className="text-sm text-green-600 dark:text-green-400 mt-2 flex items-center gap-1">
                <CheckCircle2 className="h-3 w-3" />
                You've reached the graduation threshold!
              </p>
            ) : (
              <p className="text-sm text-muted-foreground mt-2">
                ~{estimatedSprintsRemaining} sprint(s) to reach 85%
              </p>
            )}
          </CardContent>
        </Card>

        <Card data-testid="card-sprint-progress">
          <CardHeader className="pb-2">
            <CardTitle className="text-sm font-medium text-muted-foreground flex items-center gap-2">
              <Rocket className="h-4 w-4" />
              Sprint Progress
            </CardTitle>
          </CardHeader>
          <CardContent>
            <div className="flex items-end gap-2">
              <span className="text-4xl font-bold" data-testid="text-sprints-completed">
                {journey.completedSprints}
              </span>
              <span className="text-sm text-muted-foreground mb-1">sprints completed</span>
            </div>
            {currentSprint ? (
              <div className="mt-3 space-y-1">
                <div className="flex justify-between text-sm">
                  <span>Current: Day {currentSprint.currentDay}/10</span>
                  <span className="text-muted-foreground">
                    {currentSprint.ticketStats.done}/{currentSprint.tickets.length} tickets
                  </span>
                </div>
                <Progress 
                  value={(currentSprint.currentDay / 10) * 100} 
                  className="h-2" 
                />
              </div>
            ) : (
              <p className="text-sm text-muted-foreground mt-3">
                No active sprint
              </p>
            )}
          </CardContent>
        </Card>

        <Card data-testid="card-current-arc">
          <CardHeader className="pb-2">
            <CardTitle className="text-sm font-medium text-muted-foreground flex items-center gap-2">
              <Calendar className="h-4 w-4" />
              Current Phase
            </CardTitle>
          </CardHeader>
          <CardContent>
            <div className="flex items-end gap-2">
              <span className="text-2xl font-bold capitalize" data-testid="text-arc-type">
                {currentArc?.arcType || 'Not started'}
              </span>
            </div>
            {currentArc && (
              <>
                <p className="text-sm text-muted-foreground mt-1">
                  {currentArc.arcType === 'onboarding' ? 'Getting set up' : `Sprint ${currentArc.arcOrder - 1}`}
                </p>
                <Badge 
                  variant="outline" 
                  className="mt-3"
                  data-testid="badge-arc-status"
                >
                  {currentArc.status}
                </Badge>
              </>
            )}
          </CardContent>
        </Card>
      </div>

      {currentSprint && (
        <Card data-testid="card-active-sprint">
          <CardHeader>
            <div className="flex justify-between items-start">
              <div>
                <CardTitle className="flex items-center gap-2">
                  <Zap className="h-5 w-5 text-blue-500" />
                  Active Sprint
                </CardTitle>
                <CardDescription className="mt-1">
                  {currentSprint.sprint.theme} - {currentSprint.sprint.goal}
                </CardDescription>
              </div>
              <Link href={`/journey/${journeyId}/sprint/${currentSprint.sprint.id}`}>
                <Button className="gap-2" data-testid="button-enter-sprint">
                  <Play className="h-4 w-4" />
                  Enter Sprint
                </Button>
              </Link>
            </div>
          </CardHeader>
          <CardContent>
            <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
              <div className="space-y-1">
                <p className="text-sm text-muted-foreground">Todo</p>
                <p className="text-2xl font-semibold" data-testid="text-tickets-todo">
                  {currentSprint.ticketStats.todo}
                </p>
              </div>
              <div className="space-y-1">
                <p className="text-sm text-muted-foreground">In Progress</p>
                <p className="text-2xl font-semibold text-blue-600" data-testid="text-tickets-in-progress">
                  {currentSprint.ticketStats.inProgress}
                </p>
              </div>
              <div className="space-y-1">
                <p className="text-sm text-muted-foreground">In Review</p>
                <p className="text-2xl font-semibold text-amber-600" data-testid="text-tickets-in-review">
                  {currentSprint.ticketStats.inReview}
                </p>
              </div>
              <div className="space-y-1">
                <p className="text-sm text-muted-foreground">Done</p>
                <p className="text-2xl font-semibold text-green-600" data-testid="text-tickets-done">
                  {currentSprint.ticketStats.done}
                </p>
              </div>
            </div>

            {currentSprint.upcomingCeremonies.length > 0 && (
              <>
                <Separator className="my-4" />
                <div>
                  <h4 className="text-sm font-medium mb-2 flex items-center gap-2">
                    <Users className="h-4 w-4" />
                    Upcoming Ceremonies
                  </h4>
                  <div className="flex flex-wrap gap-2">
                    {currentSprint.upcomingCeremonies.slice(0, 3).map(ceremony => (
                      <Link 
                        key={ceremony.id} 
                        href={`/journey/${journeyId}/sprint/${currentSprint.sprint.id}/ceremony/${ceremony.id}`}
                      >
                        <Badge 
                          variant="secondary" 
                          className="cursor-pointer hover:bg-secondary/80"
                          data-testid={`badge-ceremony-${ceremony.id}`}
                        >
                          <Clock className="h-3 w-3 mr-1" />
                          {ceremony.ceremonyType}
                        </Badge>
                      </Link>
                    ))}
                  </div>
                </div>
              </>
            )}
          </CardContent>
        </Card>
      )}

      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        <Card data-testid="card-competencies">
          <CardHeader>
            <CardTitle className="flex items-center gap-2">
              <BarChart3 className="h-5 w-5" />
              Competency Progress
            </CardTitle>
            <CardDescription>
              Your skill levels across key competencies
            </CardDescription>
          </CardHeader>
          <CardContent>
            <div className="space-y-4">
              {Object.entries(competencyScores).map(([slug, { score, band }]) => (
                <div key={slug} className="space-y-2">
                  <div className="flex justify-between items-center">
                    <span className="text-sm font-medium capitalize">
                      {slug.replace(/_/g, ' ')}
                    </span>
                    <Badge 
                      className={getBandColor(band)}
                      data-testid={`badge-competency-${slug}`}
                    >
                      {getBandLabel(band)}
                    </Badge>
                  </div>
                  <Progress value={score} className="h-2" />
                </div>
              ))}
              {Object.keys(competencyScores).length === 0 && (
                <p className="text-sm text-muted-foreground text-center py-4">
                  Complete activities to track competency progress
                </p>
              )}
            </div>
          </CardContent>
        </Card>

        <Card data-testid="card-timeline">
          <CardHeader>
            <CardTitle className="flex items-center gap-2">
              <TrendingUp className="h-5 w-5" />
              Journey Timeline
            </CardTitle>
            <CardDescription>
              Your path from start to graduation
            </CardDescription>
          </CardHeader>
          <CardContent>
            <div className="space-y-3">
              {timeline.arcs.map((arc, arcIndex) => (
                <div 
                  key={arc.id} 
                  className={`flex items-center gap-3 p-3 rounded-lg ${
                    arcIndex === timeline.currentPosition.arcIndex
                      ? 'bg-blue-50 dark:bg-blue-950 border border-blue-200 dark:border-blue-800'
                      : arc.status === 'completed'
                      ? 'bg-green-50 dark:bg-green-950'
                      : 'bg-muted/50'
                  }`}
                  data-testid={`timeline-arc-${arc.id}`}
                >
                  <div className={`h-8 w-8 rounded-full flex items-center justify-center ${
                    arc.status === 'completed'
                      ? 'bg-green-500 text-white'
                      : arc.status === 'in_progress'
                      ? 'bg-blue-500 text-white'
                      : 'bg-muted text-muted-foreground'
                  }`}>
                    {arc.status === 'completed' ? (
                      <CheckCircle2 className="h-4 w-4" />
                    ) : arc.arcType === 'onboarding' ? (
                      <Rocket className="h-4 w-4" />
                    ) : (
                      <span className="text-xs font-bold">{arc.arcOrder - 1}</span>
                    )}
                  </div>
                  <div className="flex-1">
                    <p className="font-medium capitalize">
                      {arc.arcType === 'onboarding' ? 'Onboarding' : `Sprint ${arc.arcOrder - 1}`}
                    </p>
                    <p className="text-xs text-muted-foreground">
                      {arc.sprints.length > 0 && arc.sprints[0].theme}
                    </p>
                  </div>
                  {arcIndex === timeline.currentPosition.arcIndex && (
                    <Badge variant="outline" className="text-blue-600">
                      Current
                    </Badge>
                  )}
                </div>
              ))}

              <div className="flex items-center gap-3 p-3 rounded-lg bg-muted/30 border-2 border-dashed">
                <div className="h-8 w-8 rounded-full flex items-center justify-center bg-gradient-to-br from-amber-400 to-amber-600 text-white">
                  <Trophy className="h-4 w-4" />
                </div>
                <div className="flex-1">
                  <p className="font-medium">Graduation</p>
                  <p className="text-xs text-muted-foreground">
                    {canGraduate ? 'Ready to graduate!' : `${estimatedSprintsRemaining} sprint(s) away`}
                  </p>
                </div>
                {canGraduate && (
                  <Badge className="bg-green-500">Ready!</Badge>
                )}
              </div>
            </div>
          </CardContent>
        </Card>
      </div>

      <Card className="border-blue-200 dark:border-blue-800 bg-blue-50/50 dark:bg-blue-950/50" data-testid="card-quick-actions">
        <CardHeader>
          <CardTitle className="text-lg">Quick Actions</CardTitle>
        </CardHeader>
        <CardContent>
          <div className="flex flex-wrap gap-3">
            {currentSprint && (
              <>
                <Link href={`/journey/${journeyId}/sprint/${currentSprint.sprint.id}`}>
                  <Button variant="outline" className="gap-2" data-testid="button-view-kanban">
                    <GitBranch className="h-4 w-4" />
                    View Kanban Board
                  </Button>
                </Link>
                <Link href={`/journey/${journeyId}/sprint/${currentSprint.sprint.id}/standup`}>
                  <Button variant="outline" className="gap-2" data-testid="button-daily-standup">
                    <Users className="h-4 w-4" />
                    Daily Standup
                  </Button>
                </Link>
              </>
            )}
            <Link href={`/journey/${journeyId}/portfolio`}>
              <Button variant="outline" className="gap-2" data-testid="button-view-portfolio">
                <BarChart3 className="h-4 w-4" />
                View Portfolio
              </Button>
            </Link>
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

