import { useState, useRef, useEffect, useCallback } from "react";
import { useMutation } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Textarea } from "@/components/ui/textarea";
import { ScrollArea } from "@/components/ui/scroll-area";
import { Separator } from "@/components/ui/separator";
import { Avatar, AvatarFallback, AvatarImage } from "@/components/ui/avatar";
import { Progress } from "@/components/ui/progress";
import { apiRequest, queryClient } from "@/lib/queryClient";
import {
  Users,
  Clock,
  CheckCircle2,
  Send,
  AlertTriangle,
  ThumbsUp,
  ThumbsDown,
  Lightbulb,
  MessageSquare,
  Coffee,
  Target,
  RotateCcw,
  Loader2,
  ChevronRight,
} from "lucide-react";
import type { CeremonyInstance, SprintTicket, Sprint } from "@shared/schema";

type CeremonyType = 'standup' | 'review' | 'retrospective';

interface TeamMember {
  id: string;
  name: string;
  role: string;
  avatar?: string;
}

interface CeremonyMessage {
  id: string;
  speaker: TeamMember | 'user';
  content: string;
  timestamp: Date;
  type: 'message' | 'question' | 'action' | 'feedback';
}

interface CeremonySessionProps {
  ceremony: CeremonyInstance;
  sprint: Sprint;
  tickets: SprintTicket[];
  teamMembers: TeamMember[];
  onComplete: (outcome: any) => void;
  mode: 'journey' | 'practice';
}

const CEREMONY_CONFIG: Record<CeremonyType, {
  title: string;
  icon: any;
  description: string;
  prompts: string[];
  color: string;
}> = {
  standup: {
    title: 'Daily Standup',
    icon: Coffee,
    description: 'Share your progress, plans, and blockers with the team',
    prompts: [
      "What did you work on yesterday?",
      "What are you planning to work on today?",
      "Do you have any blockers or need help with anything?",
    ],
    color: 'blue',
  },
  review: {
    title: 'Sprint Review',
    icon: Target,
    description: 'Demo completed work and gather feedback from stakeholders',
    prompts: [
      "Walk us through what you accomplished this sprint.",
      "Can you demo the main feature you worked on?",
      "What feedback do you have on the sprint goal?",
    ],
    color: 'green',
  },
  retrospective: {
    title: 'Sprint Retrospective',
    icon: RotateCcw,
    description: 'Reflect on the sprint and identify improvements',
    prompts: [
      "What went well this sprint?",
      "What could we improve?",
      "What actions should we take going forward?",
    ],
    color: 'purple',
  },
};

export default function CeremonySession({
  ceremony,
  sprint,
  tickets,
  teamMembers,
  onComplete,
  mode
}: CeremonySessionProps) {
  const [messages, setMessages] = useState<CeremonyMessage[]>([]);
  const [currentInput, setCurrentInput] = useState("");
  const [currentPromptIndex, setCurrentPromptIndex] = useState(0);
  const [isProcessing, setIsProcessing] = useState(false);
  const [userResponses, setUserResponses] = useState<string[]>([]);
  const [isCompleting, setIsCompleting] = useState(false);
  const scrollRef = useRef<HTMLDivElement>(null);
  const hasInitialized = useRef(false);

  const ceremonyType = ceremony.ceremonyType as CeremonyType;
  const config = CEREMONY_CONFIG[ceremonyType];

  const completeCeremonyMutation = useMutation({
    mutationFn: async (outcome: any) => {
      const response = await apiRequest('PATCH', `/api/ceremonies/${ceremony.id}`, {
        status: 'completed',
        outcome,
      });
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/ceremonies', ceremony.id] });
      queryClient.invalidateQueries({ queryKey: ['/api/sprints', sprint.id, 'ceremonies'] });
      queryClient.invalidateQueries({ queryKey: ['/api/sprints', sprint.id] });
    },
  });

  useEffect(() => {
    if (scrollRef.current) {
      scrollRef.current.scrollTop = scrollRef.current.scrollHeight;
    }
  }, [messages]);

  useEffect(() => {
    if (!hasInitialized.current) {
      hasInitialized.current = true;
      startCeremony();
    }
  }, []);

  const startCeremony = () => {
    const facilitator = teamMembers.find(m => m.role.toLowerCase().includes('lead') || m.role.toLowerCase().includes('manager')) || teamMembers[0];

    const welcomeMessage: CeremonyMessage = {
      id: `msg-${Date.now()}`,
      speaker: facilitator,
      content: getWelcomeMessage(ceremonyType, facilitator.name),
      timestamp: new Date(),
      type: 'message',
    };

    setMessages([welcomeMessage]);

    setTimeout(() => {
      const firstPrompt: CeremonyMessage = {
        id: `msg-${Date.now()}-1`,
        speaker: facilitator,
        content: config.prompts[0],
        timestamp: new Date(),
        type: 'question',
      };
      setMessages(prev => [...prev, firstPrompt]);
    }, 1000);
  };

  const getWelcomeMessage = (type: CeremonyType, facilitatorName: string): string => {
    switch (type) {
      case 'standup':
        return `Good morning team! I'm ${facilitatorName}, and I'll be facilitating today's standup. Let's keep it focused - remember, it's about what you're working on, not a detailed status report. We'll go around the room.`;
      case 'review':
        return `Welcome everyone to our Sprint Review! I'm ${facilitatorName}. Today we'll be showcasing the work completed this sprint and gathering feedback. Let's start with our intern developer.`;
      case 'retrospective':
        return `Hi team, ${facilitatorName} here. Time for our retrospective! This is a safe space to discuss what went well, what didn't, and how we can improve. Let's be constructive and focus on the process, not individuals.`;
      default:
        return `Let's get started with today's ${type}.`;
    }
  };

  const handleSendMessage = async () => {
    if (!currentInput.trim() || isProcessing) return;

    const userMessage: CeremonyMessage = {
      id: `msg-${Date.now()}`,
      speaker: 'user',
      content: currentInput,
      timestamp: new Date(),
      type: 'message',
    };

    setMessages(prev => [...prev, userMessage]);
    setUserResponses(prev => [...prev, currentInput]);
    setCurrentInput("");
    setIsProcessing(true);

    setTimeout(() => {
      processResponse(currentInput);
    }, 1000);
  };

  const processResponse = (userResponse: string) => {
    const newPromptIndex = currentPromptIndex + 1;

    const respondingMember = teamMembers[newPromptIndex % teamMembers.length] || teamMembers[0];

    const acknowledgment: CeremonyMessage = {
      id: `msg-${Date.now()}-ack`,
      speaker: respondingMember,
      content: getAcknowledgment(ceremonyType, userResponse, respondingMember.name),
      timestamp: new Date(),
      type: 'feedback',
    };

    setMessages(prev => [...prev, acknowledgment]);

    if (newPromptIndex < config.prompts.length) {
      setTimeout(() => {
        const nextPrompt: CeremonyMessage = {
          id: `msg-${Date.now()}-q`,
          speaker: teamMembers[0],
          content: config.prompts[newPromptIndex],
          timestamp: new Date(),
          type: 'question',
        };
        setMessages(prev => [...prev, nextPrompt]);
        setCurrentPromptIndex(newPromptIndex);
        setIsProcessing(false);
      }, 1500);
    } else {
      setCurrentPromptIndex(newPromptIndex);
      setTimeout(() => {
        const closingMessage: CeremonyMessage = {
          id: `msg-${Date.now()}-close`,
          speaker: teamMembers[0],
          content: getClosingMessage(ceremonyType, teamMembers[0].name),
          timestamp: new Date(),
          type: 'message',
        };
        setMessages(prev => [...prev, closingMessage]);
        setIsProcessing(false);
      }, 1500);
    }
  };

  const getAcknowledgment = (type: CeremonyType, response: string, memberName: string): string => {
    const acknowledgments = {
      standup: [
        "Thanks for the update! Sounds like you're making good progress.",
        "Got it. Let us know if you need any help with that.",
        "Nice work! Keep the momentum going.",
      ],
      review: [
        "Great demo! I like how you approached that problem.",
        "Solid work. The stakeholders will appreciate this.",
        "Interesting solution. Can you tell us more about the technical decisions?",
      ],
      retrospective: [
        "That's a great observation. I've noticed that too.",
        "Thanks for bringing that up. It's important we address this.",
        "I agree. Let's add that to our action items.",
      ],
    };

    const options = acknowledgments[type];
    return options[Math.floor(Math.random() * options.length)];
  };

  const getClosingMessage = (type: CeremonyType, facilitatorName: string): string => {
    switch (type) {
      case 'standup':
        return `Great standup everyone! Remember, if anyone gets blocked, don't wait until tomorrow - reach out on Slack. Have a productive day!`;
      case 'review':
        return `Excellent work this sprint, team! The stakeholders were impressed with the progress. Let's take these feedback items into our next planning session.`;
      case 'retrospective':
        return `Thanks for the thoughtful discussion, team. I've captured our action items and we'll follow up on them next sprint. Great retrospective!`;
      default:
        return `Thanks everyone, great session!`;
    }
  };

  const handleComplete = async () => {
    setIsCompleting(true);
    const outcome = {
      responses: userResponses,
      messageCount: messages.length,
      completedAt: new Date().toISOString(),
    };

    try {
      await completeCeremonyMutation.mutateAsync(outcome);
      onComplete(outcome);
    } catch (error) {
      console.error('Failed to complete ceremony:', error);
      setIsCompleting(false);
    }
  };

  const isComplete = currentPromptIndex >= config.prompts.length && !isProcessing;
  const progress = ((currentPromptIndex + (isComplete ? 1 : 0)) / config.prompts.length) * 100;

  const Icon = config.icon;

  return (
    <div className="max-w-4xl mx-auto p-6 space-y-6">
      <Card>
        <CardHeader>
          <div className="flex items-center justify-between">
            <div className="flex items-center gap-3">
              <div className={`p-2 rounded-lg bg-${config.color}-100`}>
                <Icon className={`h-5 w-5 text-${config.color}-600`} />
              </div>
              <div>
                <CardTitle className="text-xl">{config.title}</CardTitle>
                <p className="text-sm text-muted-foreground">{config.description}</p>
              </div>
            </div>
            <Badge variant="outline" className="text-sm">
              {mode === 'journey' ? 'Journey Mode' : 'Practice Mode'}
            </Badge>
          </div>
        </CardHeader>
        <CardContent>
          <div className="mb-4">
            <div className="flex justify-between text-sm mb-1">
              <span>Progress</span>
              <span>{Math.round(progress)}%</span>
            </div>
            <Progress value={progress} />
          </div>

          <div className="flex gap-2 mb-4 flex-wrap">
            <span className="text-sm text-muted-foreground">Team:</span>
            {teamMembers.map((member) => (
              <Badge key={member.id} variant="secondary" className="text-xs">
                {member.name} ({member.role})
              </Badge>
            ))}
          </div>
        </CardContent>
      </Card>

      <Card>
        <CardContent className="p-0">
          <ScrollArea className="h-[400px] p-4" ref={scrollRef}>
            <div className="space-y-4">
              {messages.map((message) => (
                <MessageBubble key={message.id} message={message} />
              ))}

              {isProcessing && (
                <div className="flex items-center gap-2 text-muted-foreground">
                  <Loader2 className="h-4 w-4 animate-spin" />
                  <span className="text-sm">Team is responding...</span>
                </div>
              )}
            </div>
          </ScrollArea>

          <Separator />

          <div className="p-4">
            {!isComplete ? (
              <div className="flex gap-2">
                <Textarea
                  value={currentInput}
                  onChange={(e) => setCurrentInput(e.target.value)}
                  placeholder="Type your response..."
                  className="min-h-[80px] resize-none"
                  disabled={isProcessing}
                  onKeyDown={(e) => {
                    if (e.key === 'Enter' && !e.shiftKey) {
                      e.preventDefault();
                      handleSendMessage();
                    }
                  }}
                  data-testid="input-ceremony-response"
                />
                <Button
                  onClick={handleSendMessage}
                  disabled={!currentInput.trim() || isProcessing}
                  className="self-end"
                  data-testid="button-send-response"
                >
                  <Send className="h-4 w-4" />
                </Button>
              </div>
            ) : (
              <div className="flex items-center justify-between p-4 bg-green-50 rounded-lg">
                <div className="flex items-center gap-2">
                  <CheckCircle2 className="h-5 w-5 text-green-600" />
                  <span className="font-medium text-green-800">
                    {config.title} Complete!
                  </span>
                </div>
                <Button 
                  onClick={handleComplete} 
                  disabled={isCompleting}
                  data-testid="button-complete-ceremony"
                  aria-label="Complete ceremony and continue"
                >
                  {isCompleting ? (
                    <Loader2 className="h-4 w-4 animate-spin mr-2" />
                  ) : null}
                  Continue
                  {!isCompleting && <ChevronRight className="h-4 w-4 ml-2" />}
                </Button>
              </div>
            )}
          </div>
        </CardContent>
      </Card>

      {ceremonyType === 'standup' && (
        <StandupSidebar tickets={tickets} />
      )}

      {ceremonyType === 'review' && (
        <ReviewSidebar tickets={tickets} sprint={sprint} />
      )}

      {ceremonyType === 'retrospective' && (
        <RetroSidebar responses={userResponses} />
      )}
    </div>
  );
}

interface MessageBubbleProps {
  message: CeremonyMessage;
}

function MessageBubble({ message }: MessageBubbleProps) {
  const isUser = message.speaker === 'user';
  const speaker = isUser ? null : message.speaker as TeamMember;

  return (
    <div className={`flex gap-3 ${isUser ? 'flex-row-reverse' : ''}`}>
      {!isUser && speaker && (
        <Avatar className="h-8 w-8">
          <AvatarImage src={speaker.avatar} />
          <AvatarFallback className="text-xs bg-blue-100 text-blue-700">
            {speaker.name.split(' ').map(n => n[0]).join('')}
          </AvatarFallback>
        </Avatar>
      )}

      <div className={`flex-1 max-w-[80%] ${isUser ? 'text-right' : ''}`}>
        {!isUser && speaker && (
          <div className="flex items-center gap-2 mb-1">
            <span className="text-sm font-medium">{speaker.name}</span>
            <span className="text-xs text-muted-foreground">{speaker.role}</span>
          </div>
        )}
        <div
          className={`inline-block p-3 rounded-lg ${
            isUser
              ? 'bg-blue-600 text-white'
              : message.type === 'question'
              ? 'bg-yellow-50 border border-yellow-200'
              : 'bg-gray-100'
          }`}
        >
          <p className="text-sm whitespace-pre-wrap">{message.content}</p>
        </div>
        <p className="text-xs text-muted-foreground mt-1">
          {message.timestamp.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}
        </p>
      </div>
    </div>
  );
}

interface StandupSidebarProps {
  tickets: SprintTicket[];
}

function StandupSidebar({ tickets }: StandupSidebarProps) {
  const inProgress = tickets.filter(t => t.status === 'in_progress');
  const blocked = tickets.filter(t => t.priority === 'critical');

  return (
    <Card>
      <CardHeader className="pb-3">
        <CardTitle className="text-base">Your Current Work</CardTitle>
      </CardHeader>
      <CardContent className="space-y-4">
        <div>
          <h4 className="text-sm font-medium mb-2 flex items-center gap-2">
            <Clock className="h-4 w-4" />
            In Progress ({inProgress.length})
          </h4>
          <div className="space-y-2">
            {inProgress.map(ticket => (
              <div key={ticket.id} className="text-sm p-2 bg-muted rounded">
                <span className="font-mono text-muted-foreground">{ticket.ticketKey}</span>
                <span className="ml-2">{ticket.title}</span>
              </div>
            ))}
            {inProgress.length === 0 && (
              <p className="text-sm text-muted-foreground italic">No tickets in progress</p>
            )}
          </div>
        </div>

        {blocked.length > 0 && (
          <div>
            <h4 className="text-sm font-medium mb-2 flex items-center gap-2 text-red-600">
              <AlertTriangle className="h-4 w-4" />
              Critical Items
            </h4>
            <div className="space-y-2">
              {blocked.map(ticket => (
                <div key={ticket.id} className="text-sm p-2 bg-red-50 rounded border border-red-200">
                  <span className="font-mono text-red-600">{ticket.ticketKey}</span>
                  <span className="ml-2">{ticket.title}</span>
                </div>
              ))}
            </div>
          </div>
        )}
      </CardContent>
    </Card>
  );
}

interface ReviewSidebarProps {
  tickets: SprintTicket[];
  sprint: Sprint;
}

function ReviewSidebar({ tickets, sprint }: ReviewSidebarProps) {
  const completed = tickets.filter(t => t.status === 'done');
  const totalPoints = completed.reduce((sum, t) => sum + (t.storyPoints || 0), 0);

  return (
    <Card>
      <CardHeader className="pb-3">
        <CardTitle className="text-base">Sprint Summary</CardTitle>
      </CardHeader>
      <CardContent className="space-y-4">
        <div className="grid grid-cols-2 gap-4">
          <div className="text-center p-3 bg-green-50 rounded">
            <div className="text-2xl font-bold text-green-600">{completed.length}</div>
            <div className="text-xs text-green-700">Tickets Completed</div>
          </div>
          <div className="text-center p-3 bg-blue-50 rounded">
            <div className="text-2xl font-bold text-blue-600">{totalPoints}</div>
            <div className="text-xs text-blue-700">Story Points</div>
          </div>
        </div>

        <div>
          <h4 className="text-sm font-medium mb-2">Completed Work</h4>
          <div className="space-y-1">
            {completed.map(ticket => (
              <div key={ticket.id} className="flex items-center gap-2 text-sm">
                <CheckCircle2 className="h-3 w-3 text-green-500" />
                <span className="font-mono text-xs text-muted-foreground">{ticket.ticketKey}</span>
                <span className="truncate">{ticket.title}</span>
              </div>
            ))}
          </div>
        </div>
      </CardContent>
    </Card>
  );
}

interface RetroSidebarProps {
  responses: string[];
}

function RetroSidebar({ responses }: RetroSidebarProps) {
  const categories = [
    { icon: ThumbsUp, label: 'What went well', color: 'green' },
    { icon: ThumbsDown, label: 'What to improve', color: 'orange' },
    { icon: Lightbulb, label: 'Action items', color: 'blue' },
  ];

  return (
    <Card>
      <CardHeader className="pb-3">
        <CardTitle className="text-base">Retrospective Notes</CardTitle>
      </CardHeader>
      <CardContent className="space-y-4">
        {categories.map((category, index) => {
          const Icon = category.icon;
          const response = responses[index];
          return (
            <div key={category.label}>
              <h4 className={`text-sm font-medium mb-2 flex items-center gap-2 text-${category.color}-600`}>
                <Icon className="h-4 w-4" />
                {category.label}
              </h4>
              {response ? (
                <p className="text-sm p-2 bg-muted rounded">{response}</p>
              ) : (
                <p className="text-sm text-muted-foreground italic">Pending...</p>
              )}
            </div>
          );
        })}
      </CardContent>
    </Card>
  );
}

client/src/components/simulation/git-wor.content:
import { useState, useRef, useEffect } from "react";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Input } from "@/components/ui/input";
import { ScrollArea } from "@/components/ui/scroll-area";
import { Separator } from "@/components/ui/separator";
import {
  GitBranch,
  GitCommit,
  GitPullRequest,
  GitMerge,
  Terminal,
  Lightbulb,
  CheckCircle2,
  XCircle,
  AlertTriangle,
  Copy,
  ChevronRight,
  Info,
  Loader2,
} from "lucide-react";
import {
  Tooltip,
  TooltipContent,
  TooltipProvider,
  TooltipTrigger,
} from "@/components/ui/tooltip";
import {
  createInitialState,
  simulateGitCommand,
  parseGitCommand,
  getBranchNamingTips,
  getCommitMessageTips,
  suggestBranchName,
  suggestCommitMessage,
  type GitState,
  type GitCommandResult,
} from "@/lib/git-simulator";
import type { SprintTicket } from "@shared/schema";

interface GitWorkflowProps {
  ticket: SprintTicket;
  gitState: GitState;
  onStateChange: (newState: GitState) => void;
  onBranchCreate?: (branchName: string) => void;
  onCommit?: (message: string) => void;
  onPRCreate?: () => void;
  onMerge?: () => void;
  mode: 'journey' | 'practice';
}

interface CommandHistoryEntry {
  command: string;
  output: string;
  isError: boolean;
  timestamp: Date;
}

export default function GitWorkflow({
  ticket,
  gitState,
  onStateChange,
  onBranchCreate,
  onCommit,
  onPRCreate,
  onMerge,
  mode
}: GitWorkflowProps) {
  const [currentState, setCurrentState] = useState<GitState>(gitState);
  const [currentInput, setCurrentInput] = useState("");
  const [commandHistory, setCommandHistory] = useState<CommandHistoryEntry[]>([]);
  const [historyIndex, setHistoryIndex] = useState(-1);
  const [showTips, setShowTips] = useState(true);
  const [isPRLoading, setIsPRLoading] = useState(false);
  const [isMergeLoading, setIsMergeLoading] = useState(false);
  const inputRef = useRef<HTMLInputElement>(null);
  const scrollRef = useRef<HTMLDivElement>(null);

  useEffect(() => {
    if (scrollRef.current) {
      scrollRef.current.scrollTop = scrollRef.current.scrollHeight;
    }
  }, [commandHistory]);

  const executeCommand = (commandInput: string) => {
    if (!commandInput.trim()) return;

    const parsed = parseGitCommand(commandInput);

    if (!parsed.command) {
      setCommandHistory(prev => [...prev, {
        command: commandInput,
        output: 'Error: Not a valid git command. Commands must start with "git".',
        isError: true,
        timestamp: new Date(),
      }]);
      setCurrentInput("");
      inputRef.current?.focus();
      return;
    }

    try {
      const result = simulateGitCommand(currentState, parsed.command, parsed.args);

      setCommandHistory(prev => [...prev, {
        command: commandInput,
        output: result.output,
        isError: !result.success,
        timestamp: new Date(),
      }]);

      if (result.success && result.stateChange) {
        const newState = { ...currentState, ...result.stateChange };
        setCurrentState(newState);
        onStateChange(newState);

        if (commandInput.startsWith('git checkout -b') || commandInput.startsWith('git switch -c')) {
          const branchMatch = commandInput.match(/(?:checkout -b|switch -c)\s+(\S+)/);
          if (branchMatch && onBranchCreate) {
            onBranchCreate(branchMatch[1]);
          }
        } else if (commandInput.startsWith('git commit')) {
          if (onCommit) {
            const messageMatch = commandInput.match(/-m\s+["'](.+?)["']/);
            onCommit(messageMatch ? messageMatch[1] : 'Commit');
          }
        }
      }
    } catch (error) {
      setCommandHistory(prev => [...prev, {
        command: commandInput,
        output: `Error: An unexpected error occurred while processing the command.`,
        isError: true,
        timestamp: new Date(),
      }]);
    }

    setCurrentInput("");
    setHistoryIndex(-1);
    inputRef.current?.focus();
  };

  const handleKeyDown = (e: React.KeyboardEvent) => {
    if (e.key === 'Enter') {
      executeCommand(currentInput);
    } else if (e.key === 'ArrowUp') {
      e.preventDefault();
      const commands = commandHistory.map(h => h.command);
      if (historyIndex < commands.length - 1) {
        const newIndex = historyIndex + 1;
        setHistoryIndex(newIndex);
        setCurrentInput(commands[commands.length - 1 - newIndex] || "");
      }
    } else if (e.key === 'ArrowDown') {
      e.preventDefault();
      if (historyIndex > 0) {
        const newIndex = historyIndex - 1;
        setHistoryIndex(newIndex);
        const commands = commandHistory.map(h => h.command);
        setCurrentInput(commands[commands.length - 1 - newIndex] || "");
      } else if (historyIndex === 0) {
        setHistoryIndex(-1);
        setCurrentInput("");
      }
    } else if (e.key === 'Tab') {
      e.preventDefault();
      const suggestions = getAutocompleteSuggestions(currentInput);
      if (suggestions.length === 1) {
        setCurrentInput(suggestions[0]);
      }
    }
  };

  const getAutocompleteSuggestions = (input: string): string[] => {
    const commands = [
      'git status',
      'git add .',
      'git add',
      'git commit -m ""',
      'git checkout -b',
      'git checkout main',
      'git switch -c',
      'git switch main',
      'git push origin',
      'git pull origin main',
      'git branch',
      'git log --oneline',
      'git diff',
      'git stash',
      'git stash pop',
    ];

    if (!input) return [];
    return commands.filter(cmd => cmd.startsWith(input));
  };

  const copyToClipboard = (text: string) => {
    navigator.clipboard.writeText(text);
  };

  const handleCreatePR = async () => {
    setIsPRLoading(true);
    try {
      const prTitle = `${ticket.type === 'bug' ? 'Fix' : 'Feature'}: ${ticket.title}`;
      const newState: GitState = {
        ...currentState,
        remoteSyncStatus: 'synced',
      };
      setCurrentState(newState);
      onStateChange(newState);
      if (onPRCreate) onPRCreate();

      setCommandHistory(prev => [...prev, {
        command: `[PR Created: "${prTitle}"]`,
        output: `Pull request created successfully!\nTitle: ${prTitle}\nBranch: ${currentState.currentBranch}  main`,
        isError: false,
        timestamp: new Date(),
      }]);
    } finally {
      setIsPRLoading(false);
      inputRef.current?.focus();
    }
  };

  const handleMergePR = async () => {
    setIsMergeLoading(true);
    try {
      const newState: GitState = {
        ...currentState,
        currentBranch: 'main',
        remoteSyncStatus: 'synced',
      };
      setCurrentState(newState);
      onStateChange(newState);
      if (onMerge) onMerge();

      setCommandHistory(prev => [...prev, {
        command: '[PR Merged]',
        output: `Pull request merged successfully!\nBranch ${currentState.currentBranch} merged into main.`,
        isError: false,
        timestamp: new Date(),
      }]);
    } finally {
      setIsMergeLoading(false);
      inputRef.current?.focus();
    }
  };

  const branchTips = getBranchNamingTips();
  const commitTips = getCommitMessageTips();

  const currentBranchCommits = currentState.branches.find(b => b.name === currentState.currentBranch)?.commits?.length ?? 0;
  const hasCommits = currentBranchCommits > 0;
  const isPushed = currentState.remoteSyncStatus === 'synced' && currentState.currentBranch !== 'main';

  const workflowStatus = {
    branch: currentState.currentBranch !== 'main',
    hasChanges: currentState.stagedFiles.length > 0 || currentState.modifiedFiles.length > 0,
    committed: hasCommits,
    pushed: isPushed,
    prReady: isPushed && commandHistory.some(h => h.command.includes('[PR Created')),
    merged: commandHistory.some(h => h.command.includes('[PR Merged]')),
  };

  const suggestedBranch = suggestBranchName(ticket.ticketKey, ticket.title, ticket.type);

  return (
    <div className="space-y-4">
      <Card>
        <CardHeader className="pb-3">
          <div className="flex items-center justify-between">
            <CardTitle className="text-base flex items-center gap-2">
              <Terminal className="h-4 w-4" />
              Git Terminal
            </CardTitle>
            <Button
              variant="ghost"
              size="sm"
              onClick={() => setShowTips(!showTips)}
              data-testid="button-toggle-tips"
            >
              <Lightbulb className={`h-4 w-4 ${showTips ? 'text-yellow-500' : 'text-gray-400'}`} />
            </Button>
          </div>
        </CardHeader>
        <CardContent className="space-y-4">
          <div className="flex items-center gap-4 text-sm">
            <div className="flex items-center gap-1">
              <GitBranch className="h-4 w-4 text-purple-500" />
              <span className="font-mono">{currentState.currentBranch}</span>
            </div>
            <Separator orientation="vertical" className="h-4" />
            <WorkflowStatus status={workflowStatus} />
          </div>

          <div
            ref={scrollRef}
            className="bg-gray-900 text-gray-100 rounded-lg p-4 font-mono text-sm h-[300px] overflow-y-auto"
          >
            <div className="space-y-2">
              <div className="text-green-400">
                Welcome to the Git simulator. Type commands to practice your workflow.
              </div>
              <div className="text-gray-500">
                Working on: {ticket.ticketKey} - {ticket.title}
              </div>
              <Separator className="my-2 bg-gray-700" />

              {commandHistory.map((entry, index) => (
                <div key={index} className="space-y-1">
                  <div className="flex items-center gap-2">
                    <span className="text-blue-400">$</span>
                    <span>{entry.command}</span>
                  </div>
                  <div className={entry.isError ? 'text-red-400' : 'text-gray-300 whitespace-pre-wrap'}>
                    {entry.output}
                  </div>
                </div>
              ))}

              <div className="flex items-center gap-2">
                <span className="text-blue-400">$</span>
                <Input
                  ref={inputRef}
                  value={currentInput}
                  onChange={(e) => setCurrentInput(e.target.value)}
                  onKeyDown={handleKeyDown}
                  className="bg-transparent border-none text-white focus-visible:ring-0 p-0 h-auto font-mono"
                  placeholder="Enter git command..."
                  autoFocus
                  data-testid="input-git-command"
                />
              </div>
            </div>
          </div>

          {showTips && (
            <Card className="bg-yellow-50 border-yellow-200">
              <CardContent className="py-3">
                <div className="space-y-3">
                  {!workflowStatus.branch && (
                    <TipCard
                      title="Create a Branch"
                      suggestion={`git checkout -b ${suggestedBranch}`}
                      explanation={branchTips.bestPractices[0] || "Use descriptive branch names that indicate the work being done."}
                      onCopy={() => copyToClipboard(`git checkout -b ${suggestedBranch}`)}
                    />
                  )}

                  {workflowStatus.branch && !workflowStatus.committed && (
                    <TipCard
                      title="Stage and Commit"
                      suggestion={`git add . && git commit -m "fix(${ticket.ticketKey}): ${ticket.title.slice(0, 40)}"`}
                      explanation={commitTips.bestPractices[0] || "Use clear, descriptive commit messages that explain what changed."}
                      onCopy={() => copyToClipboard(`git add . && git commit -m "fix(${ticket.ticketKey}): ${ticket.title.slice(0, 40)}"`)}
                    />
                  )}

                  {workflowStatus.committed && !workflowStatus.pushed && (
                    <TipCard
                      title="Push Your Changes"
                      suggestion={`git push origin ${currentState.currentBranch}`}
                      explanation="Push your commits to the remote repository before creating a PR."
                      onCopy={() => copyToClipboard(`git push origin ${currentState.currentBranch}`)}
                    />
                  )}

                  {workflowStatus.pushed && !workflowStatus.prReady && (
                    <div className="flex items-start gap-2 p-2 bg-blue-100 rounded">
                      <Info className="h-4 w-4 text-blue-600 mt-0.5" />
                      <div className="text-sm">
                        <p className="font-medium text-blue-800">Ready for PR!</p>
                        <p className="text-blue-600">
                          Click the "Create Pull Request" button to open a PR for review.
                        </p>
                      </div>
                    </div>
                  )}
                </div>
              </CardContent>
            </Card>
          )}
        </CardContent>
      </Card>

      <div className="flex gap-3" role="group" aria-label="Git actions">
        <Button
          variant="outline"
          onClick={handleCreatePR}
          disabled={!workflowStatus.pushed || workflowStatus.prReady || isPRLoading}
          data-testid="button-create-pr"
          aria-label="Create pull request"
        >
          {isPRLoading ? (
            <Loader2 className="h-4 w-4 mr-2 animate-spin" />
          ) : (
            <GitPullRequest className="h-4 w-4 mr-2" />
          )}
          Create Pull Request
        </Button>

        <Button
          variant="outline"
          onClick={handleMergePR}
          disabled={!workflowStatus.prReady || workflowStatus.merged || isMergeLoading}
          data-testid="button-merge-pr"
          aria-label="Merge pull request"
        >
          {isMergeLoading ? (
            <Loader2 className="h-4 w-4 mr-2 animate-spin" />
          ) : (
            <GitMerge className="h-4 w-4 mr-2" />
          )}
          Merge PR
        </Button>
      </div>

      <Card>
        <CardHeader className="pb-3">
          <CardTitle className="text-base">Git Workflow Progress</CardTitle>
        </CardHeader>
        <CardContent>
          <div className="flex items-center gap-4">
            {Object.entries({
              'Branch': workflowStatus.branch,
              'Commit': workflowStatus.committed,
              'Push': workflowStatus.pushed,
              'PR': workflowStatus.prReady,
              'Merge': workflowStatus.merged,
            }).map(([label, completed], index, arr) => (
              <div key={label} className="flex items-center">
                <div className="flex flex-col items-center">
                  <div className={`w-8 h-8 rounded-full flex items-center justify-center ${
                    completed ? 'bg-green-100 text-green-600' : 'bg-gray-100 text-gray-400'
                  }`}>
                    {completed ? (
                      <CheckCircle2 className="h-4 w-4" />
                    ) : (
                      <span className="text-xs font-medium">{index + 1}</span>
                    )}
                  </div>
                  <span className={`text-xs mt-1 ${completed ? 'text-green-600' : 'text-gray-500'}`}>
                    {label}
                  </span>
                </div>
                {index < arr.length - 1 && (
                  <div className={`w-8 h-0.5 mx-2 ${completed ? 'bg-green-300' : 'bg-gray-200'}`} />
                )}
              </div>
            ))}
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

interface WorkflowStatusProps {
  status: {
    branch: boolean;
    hasChanges: boolean;
    committed: boolean;
    pushed: boolean;
    prReady: boolean;
    merged: boolean;
  };
}

function WorkflowStatus({ status }: WorkflowStatusProps) {
  const getStatusBadge = () => {
    if (status.merged) return { label: 'Merged', variant: 'default' as const, color: 'bg-purple-100 text-purple-700' };
    if (status.prReady) return { label: 'In Review', variant: 'default' as const, color: 'bg-blue-100 text-blue-700' };
    if (status.pushed) return { label: 'Ready for PR', variant: 'default' as const, color: 'bg-yellow-100 text-yellow-700' };
    if (status.committed) return { label: 'Changes Committed', variant: 'default' as const, color: 'bg-green-100 text-green-700' };
    if (status.hasChanges) return { label: 'Uncommitted Changes', variant: 'default' as const, color: 'bg-orange-100 text-orange-700' };
    if (status.branch) return { label: 'On Feature Branch', variant: 'default' as const, color: 'bg-gray-100 text-gray-700' };
    return { label: 'On Main', variant: 'outline' as const, color: '' };
  };

  const badge = getStatusBadge();
  return (
    <Badge className={badge.color || undefined} variant={badge.color ? 'default' : badge.variant}>
      {badge.label}
    </Badge>
  );
}

interface TipCardProps {
  title: string;
  suggestion: string;
  explanation: string;
  onCopy: () => void;
}

function TipCard({ title, suggestion, explanation, onCopy }: TipCardProps) {
  return (
    <div className="space-y-1">
      <div className="flex items-center justify-between">
        <span className="text-sm font-medium text-yellow-800">{title}</span>
        <TooltipProvider>
          <Tooltip>
            <TooltipTrigger asChild>
              <Button
                variant="ghost"
                size="sm"
                onClick={onCopy}
                className="h-6 px-2"
                data-testid={`button-copy-${title.toLowerCase().replace(/\s+/g, '-')}`}
              >
                <Copy className="h-3 w-3" />
              </Button>
            </TooltipTrigger>
            <TooltipContent>Copy command</TooltipContent>
          </Tooltip>
        </TooltipProvider>
      </div>
      <code className="block text-xs bg-yellow-100 p-2 rounded font-mono text-yellow-900">
        {suggestion}
      </code>
      <p className="text-xs text-yellow-700">{explanation}</p>
    </div>
  );
}

client/src/components/simulation/manager.content:
import { useState, useRef, useEffect } from "react";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Textarea } from "@/components/ui/textarea";
import { ScrollArea } from "@/components/ui/scroll-area";
import { Separator } from "@/components/ui/separator";
import { Avatar, AvatarFallback, AvatarImage } from "@/components/ui/avatar";
import { Progress } from "@/components/ui/progress";
import {
  Users,
  Clock,
  CheckCircle2,
  Send,
  Target,
  TrendingUp,
  Lightbulb,
  MessageSquare,
  Award,
  Brain,
  ChevronRight,
  Loader2,
  Star,
} from "lucide-react";
import type { UserJourney, Sprint } from "@shared/schema";

interface Manager {
  name: string;
  title: string;
  avatar?: string;
}

interface Message {
  id: string;
  speaker: 'manager' | 'user';
  content: string;
  timestamp: Date;
  type: 'message' | 'question' | 'feedback' | 'action';
}

interface ManagerOneOnOneProps {
  journey: UserJourney;
  sprint: Sprint;
  manager: Manager;
  readinessScore: number;
  competencyScores: Record<string, { score: number; band: string }>;
  onComplete: (outcome: any) => void;
  mode: 'journey' | 'practice';
}

const ONE_ON_ONE_TOPICS = [
  {
    id: 'progress',
    title: 'Sprint Progress',
    question: "Let's start with your work this sprint. What are you most proud of accomplishing?",
    followUp: "That's great to hear. Were there any challenges you faced along the way?",
  },
  {
    id: 'learning',
    title: 'Learning & Growth',
    question: "What new skills have you picked up this sprint? Any areas where you felt you grew?",
    followUp: "Those are valuable insights. How do you plan to build on these learnings?",
  },
  {
    id: 'blockers',
    title: 'Challenges & Support',
    question: "Are there any blockers or challenges I can help you with? Anything preventing you from doing your best work?",
    followUp: "I appreciate you sharing that. Let me think about how we can address this together.",
  },
  {
    id: 'goals',
    title: 'Goals & Next Steps',
    question: "Looking ahead, what would you like to focus on in the next sprint? Any goals you're setting for yourself?",
    followUp: "Those sound like solid goals. Let's make sure we track your progress on these.",
  },
];

export default function ManagerOneOnOne({
  journey,
  sprint,
  manager,
  readinessScore,
  competencyScores,
  onComplete,
  mode
}: ManagerOneOnOneProps) {
  const [messages, setMessages] = useState<Message[]>([]);
  const [currentInput, setCurrentInput] = useState("");
  const [currentTopicIndex, setCurrentTopicIndex] = useState(0);
  const [isProcessing, setIsProcessing] = useState(false);
  const [userResponses, setUserResponses] = useState<Record<string, string>>({});
  const [showFollowUp, setShowFollowUp] = useState(false);
  const scrollRef = useRef<HTMLDivElement>(null);

  useEffect(() => {
    if (scrollRef.current) {
      scrollRef.current.scrollTop = scrollRef.current.scrollHeight;
    }
  }, [messages]);

  useEffect(() => {
    if (messages.length === 0) {
      startSession();
    }
  }, []);

  const startSession = () => {
    const welcomeMessage: Message = {
      id: `msg-${Date.now()}`,
      speaker: 'manager',
      content: `Hi there! Thanks for making time for our 1:1. I've been reviewing your progress this sprint, and I'm impressed with how you've been growing. Your readiness score has improved to ${readinessScore}%. Let's talk about how things are going.`,
      timestamp: new Date(),
      type: 'message',
    };

    setMessages([welcomeMessage]);

    setTimeout(() => {
      const firstQuestion: Message = {
        id: `msg-${Date.now()}-q`,
        speaker: 'manager',
        content: ONE_ON_ONE_TOPICS[0].question,
        timestamp: new Date(),
        type: 'question',
      };
      setMessages(prev => [...prev, firstQuestion]);
    }, 1500);
  };

  const handleSendMessage = async () => {
    if (!currentInput.trim() || isProcessing) return;

    const userMessage: Message = {
      id: `msg-${Date.now()}`,
      speaker: 'user',
      content: currentInput,
      timestamp: new Date(),
      type: 'message',
    };

    setMessages(prev => [...prev, userMessage]);

    const currentTopic = ONE_ON_ONE_TOPICS[currentTopicIndex];
    setUserResponses(prev => ({
      ...prev,
      [currentTopic.id]: currentInput,
    }));

    setCurrentInput("");
    setIsProcessing(true);

    setTimeout(() => {
      processResponse(currentInput);
    }, 1000);
  };

  const processResponse = (userResponse: string) => {
    const currentTopic = ONE_ON_ONE_TOPICS[currentTopicIndex];

    if (!showFollowUp) {
      const followUpMessage: Message = {
        id: `msg-${Date.now()}-fu`,
        speaker: 'manager',
        content: currentTopic.followUp,
        timestamp: new Date(),
        type: 'feedback',
      };
      setMessages(prev => [...prev, followUpMessage]);
      setShowFollowUp(true);
      setIsProcessing(false);
      return;
    }

    const nextTopicIndex = currentTopicIndex + 1;
    setShowFollowUp(false);

    if (nextTopicIndex < ONE_ON_ONE_TOPICS.length) {
      setTimeout(() => {
        const transitionMessage: Message = {
          id: `msg-${Date.now()}-tr`,
          speaker: 'manager',
          content: getTransitionMessage(nextTopicIndex),
          timestamp: new Date(),
          type: 'message',
        };
        setMessages(prev => [...prev, transitionMessage]);

        setTimeout(() => {
          const nextQuestion: Message = {
            id: `msg-${Date.now()}-q`,
            speaker: 'manager',
            content: ONE_ON_ONE_TOPICS[nextTopicIndex].question,
            timestamp: new Date(),
            type: 'question',
          };
          setMessages(prev => [...prev, nextQuestion]);
          setCurrentTopicIndex(nextTopicIndex);
          setIsProcessing(false);
        }, 1000);
      }, 500);
    } else {
      setTimeout(() => {
        const closingMessage: Message = {
          id: `msg-${Date.now()}-close`,
          speaker: 'manager',
          content: getClosingMessage(),
          timestamp: new Date(),
          type: 'message',
        };
        setMessages(prev => [...prev, closingMessage]);
        setCurrentTopicIndex(nextTopicIndex);
        setIsProcessing(false);
      }, 500);
    }
  };

  const getTransitionMessage = (topicIndex: number): string => {
    const transitions = [
      "That's really helpful context. Now let's talk about something else...",
      "Great, I'm glad we discussed that. Moving on...",
      "Understood. I have another topic I'd like to cover...",
      "Thanks for sharing. One more thing I wanted to discuss...",
    ];
    return transitions[topicIndex - 1] || transitions[0];
  };

  const getClosingMessage = (): string => {
    const readinessMessage = readinessScore >= 85 
      ? "Based on your performance, I think you're very close to being ready for promotion to Junior Developer!"
      : readinessScore >= 70
      ? "You're making great progress. Keep up the momentum and you'll be ready for the next level soon."
      : "I can see you're developing well. Let's keep working on these areas and revisit in our next 1:1.";

    return `Thank you for this great conversation. I really appreciate your openness and self-reflection. ${readinessMessage} Keep up the excellent work, and don't hesitate to reach out if you need anything before our next 1:1.`;
  };

  const handleComplete = () => {
    onComplete({
      responses: userResponses,
      topicsCovered: ONE_ON_ONE_TOPICS.slice(0, currentTopicIndex + 1).map(t => t.id),
      completedAt: new Date().toISOString(),
    });
  };

  const isComplete = currentTopicIndex >= ONE_ON_ONE_TOPICS.length && !isProcessing;
  const progress = (currentTopicIndex / ONE_ON_ONE_TOPICS.length) * 100;

  return (
    <div className="max-w-5xl mx-auto p-6 space-y-6">
      <div className="flex items-center justify-between">
        <div className="flex items-center gap-4">
          <Avatar className="h-12 w-12">
            <AvatarImage src={manager.avatar} />
            <AvatarFallback className="text-lg bg-purple-100 text-purple-700">
              {manager.name.split(' ').map(n => n[0]).join('')}
            </AvatarFallback>
          </Avatar>
          <div>
            <h2 className="text-xl font-semibold">1:1 with {manager.name}</h2>
            <p className="text-sm text-muted-foreground">{manager.title}</p>
          </div>
        </div>
        <Badge variant="outline" className="text-sm">
          {mode === 'journey' ? 'Journey Mode' : 'Practice Mode'}
        </Badge>
      </div>

      <div className="grid grid-cols-12 gap-6">
        <div className="col-span-8">
          <Card>
            <CardHeader className="pb-3">
              <div className="flex items-center justify-between">
                <CardTitle className="text-base">Conversation</CardTitle>
                <div className="flex items-center gap-2 text-sm text-muted-foreground">
                  <Clock className="h-4 w-4" />
                  <span>~30 min</span>
                </div>
              </div>
              <div className="mt-2">
                <div className="flex justify-between text-sm mb-1">
                  <span className="text-muted-foreground">Topics Covered</span>
                  <span>{Math.min(currentTopicIndex + 1, ONE_ON_ONE_TOPICS.length)} / {ONE_ON_ONE_TOPICS.length}</span>
                </div>
                <Progress value={progress} />
              </div>
            </CardHeader>
            <CardContent className="p-0">
              <ScrollArea className="h-[400px] p-4" ref={scrollRef}>
                <div className="space-y-4">
                  {messages.map((message) => (
                    <MessageBubble 
                      key={message.id} 
                      message={message} 
                      manager={manager}
                    />
                  ))}

                  {isProcessing && (
                    <div className="flex items-center gap-2 text-muted-foreground">
                      <Loader2 className="h-4 w-4 animate-spin" />
                      <span className="text-sm">{manager.name.split(' ')[0]} is typing...</span>
                    </div>
                  )}
                </div>
              </ScrollArea>

              <Separator />

              <div className="p-4">
                {!isComplete ? (
                  <div className="flex gap-2">
                    <Textarea
                      value={currentInput}
                      onChange={(e) => setCurrentInput(e.target.value)}
                      placeholder="Share your thoughts..."
                      className="min-h-[80px] resize-none"
                      disabled={isProcessing}
                      onKeyDown={(e) => {
                        if (e.key === 'Enter' && !e.shiftKey) {
                          e.preventDefault();
                          handleSendMessage();
                        }
                      }}
                      data-testid="input-one-on-one-response"
                    />
                    <Button
                      onClick={handleSendMessage}
                      disabled={!currentInput.trim() || isProcessing}
                      className="self-end"
                      data-testid="button-send-one-on-one"
                    >
                      <Send className="h-4 w-4" />
                    </Button>
                  </div>
                ) : (
                  <div className="flex items-center justify-between p-4 bg-purple-50 rounded-lg">
                    <div className="flex items-center gap-2">
                      <CheckCircle2 className="h-5 w-5 text-purple-600" />
                      <span className="font-medium text-purple-800">
                        Great 1:1 session!
                      </span>
                    </div>
                    <Button onClick={handleComplete} data-testid="button-complete-one-on-one">
                      Complete Session
                      <ChevronRight className="h-4 w-4 ml-2" />
                    </Button>
                  </div>
                )}
              </div>
            </CardContent>
          </Card>
        </div>

        <div className="col-span-4 space-y-4">
          <Card>
            <CardHeader className="pb-3">
              <CardTitle className="text-base flex items-center gap-2">
                <TrendingUp className="h-4 w-4" />
                Your Progress
              </CardTitle>
            </CardHeader>
            <CardContent className="space-y-4">
              <div className="text-center p-4 bg-gradient-to-br from-purple-50 to-blue-50 rounded-lg">
                <div className="text-3xl font-bold text-purple-600">{readinessScore}%</div>
                <div className="text-sm text-muted-foreground">Readiness Score</div>
              </div>

              <Separator />

              <div>
                <h4 className="text-sm font-medium mb-2">Competency Highlights</h4>
                <div className="space-y-2">
                  {Object.entries(competencyScores).slice(0, 4).map(([key, value]) => (
                    <div key={key} className="flex items-center justify-between text-sm">
                      <span className="capitalize">{key.replace(/_/g, ' ')}</span>
                      <Badge variant={value.band === 'junior_ready' ? 'default' : 'secondary'}>
                        {value.band.replace(/_/g, ' ')}
                      </Badge>
                    </div>
                  ))}
                </div>
              </div>
            </CardContent>
          </Card>

          <Card>
            <CardHeader className="pb-3">
              <CardTitle className="text-base flex items-center gap-2">
                <MessageSquare className="h-4 w-4" />
                Discussion Topics
              </CardTitle>
            </CardHeader>
            <CardContent>
              <div className="space-y-2">
                {ONE_ON_ONE_TOPICS.map((topic, index) => (
                  <div
                    key={topic.id}
                    className={`flex items-center gap-2 p-2 rounded text-sm ${
                      index < currentTopicIndex
                        ? 'bg-green-50 text-green-700'
                        : index === currentTopicIndex
                        ? 'bg-purple-50 text-purple-700'
                        : 'bg-gray-50 text-gray-500'
                    }`}
                  >
                    {index < currentTopicIndex ? (
                      <CheckCircle2 className="h-4 w-4 text-green-500" />
                    ) : index === currentTopicIndex ? (
                      <div className="h-4 w-4 rounded-full bg-purple-500 animate-pulse" />
                    ) : (
                      <div className="h-4 w-4 rounded-full bg-gray-300" />
                    )}
                    <span>{topic.title}</span>
                  </div>
                ))}
              </div>
            </CardContent>
          </Card>

          <Card className="bg-gradient-to-br from-yellow-50 to-orange-50 border-yellow-200">
            <CardContent className="pt-4">
              <div className="flex items-start gap-2">
                <Lightbulb className="h-5 w-5 text-yellow-600 mt-0.5" />
                <div>
                  <h4 className="text-sm font-medium text-yellow-800">Tips for 1:1s</h4>
                  <ul className="text-xs text-yellow-700 mt-1 space-y-1">
                    <li> Be honest and open about challenges</li>
                    <li> Come prepared with specific examples</li>
                    <li> Ask for specific feedback</li>
                    <li> Take notes on action items</li>
                  </ul>
                </div>
              </div>
            </CardContent>
          </Card>
        </div>
      </div>
    </div>
  );
}

interface MessageBubbleProps {
  message: Message;
  manager: Manager;
}

function MessageBubble({ message, manager }: MessageBubbleProps) {
  const isUser = message.speaker === 'user';

  return (
    <div className={`flex gap-3 ${isUser ? 'flex-row-reverse' : ''}`}>
      {!isUser && (
        <Avatar className="h-8 w-8">
          <AvatarImage src={manager.avatar} />
          <AvatarFallback className="text-xs bg-purple-100 text-purple-700">
            {manager.name.split(' ').map(n => n[0]).join('')}
          </AvatarFallback>
        </Avatar>
      )}

      <div className={`flex-1 max-w-[80%] ${isUser ? 'text-right' : ''}`}>
        {!isUser && (
          <div className="flex items-center gap-2 mb-1">
            <span className="text-sm font-medium">{manager.name}</span>
          </div>
        )}
        <div
          className={`inline-block p-3 rounded-lg ${
            isUser
              ? 'bg-purple-600 text-white'
              : message.type === 'question'
              ? 'bg-yellow-50 border border-yellow-200'
              : message.type === 'feedback'
              ? 'bg-green-50 border border-green-200'
              : 'bg-gray-100'
          }`}
        >
          <p className="text-sm whitespace-pre-wrap">{message.content}</p>
        </div>
        <p className="text-xs text-muted-foreground mt-1">
          {message.timestamp.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}
        </p>
      </div>
    </div>
  );
}

client/src/pages/workspace-dashboard.tsx.content:
import { useParams, Link, useLocation } from "wouter";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Skeleton } from "@/components/ui/skeleton";
import { Checkbox } from "@/components/ui/checkbox";
import { Separator } from "@/components/ui/separator";
import { 
  ArrowRight, 
  Building2, 
  Briefcase, 
  CheckCircle2, 
  Clock, 
  Play,
  ChevronLeft,
  Lightbulb,
} from "lucide-react";
import { PhaseStepper, MiniPhaseStepper, type WorkspacePhase } from "@/components/workspace/phase-stepper";
import { useWorkspaceState, useAdvanceWorkspacePhase } from "@/hooks/use-sprint-workflow";
import { useToast } from "@/hooks/use-toast";

function getPhaseColor(phase: WorkspacePhase): string {
  switch (phase) {
    case 'onboarding': return 'border-teal-500 bg-teal-50 dark:bg-teal-950';
    case 'planning': return 'border-indigo-500 bg-indigo-50 dark:bg-indigo-950';
    case 'execution': return 'border-blue-500 bg-blue-50 dark:bg-blue-950';
    case 'review': return 'border-amber-500 bg-amber-50 dark:bg-amber-950';
    case 'retro': return 'border-violet-500 bg-violet-50 dark:bg-violet-950';
    default: return 'border-gray-500 bg-gray-50';
  }
}

function getPhaseLabel(phase: WorkspacePhase): string {
  switch (phase) {
    case 'onboarding': return 'Onboarding';
    case 'planning': return 'Sprint Planning';
    case 'execution': return 'Sprint Execution';
    case 'review': return 'Sprint Review';
    case 'retro': return 'Sprint Retrospective';
    default: return phase;
  }
}

function getPhaseTip(phase: WorkspacePhase): string {
  switch (phase) {
    case 'onboarding': 
      return "Welcome! Start by meeting your team and reviewing the company documentation. Take your time to understand how things work.";
    case 'planning': 
      return "Review the product backlog with your team. Select priority items and agree on a sprint goal. Commit to a realistic scope.";
    case 'execution': 
      return "Focus on completing your assigned tickets. Attend daily standups and don't hesitate to ask for help when stuck.";
    case 'review': 
      return "Prepare to demo your completed work. Gather feedback from stakeholders and celebrate what you've accomplished!";
    case 'retro': 
      return "Reflect on the sprint honestly. Share what went well, identify improvements, and commit to actionable changes for next time.";
    default: 
      return "";
  }
}

function getPhaseStatusDescription(phase: WorkspacePhase, sprint: { sprintNumber: number; theme: string; goal: string } | null): string {
  switch (phase) {
    case 'onboarding': 
      return "Complete onboarding to start your first sprint";
    case 'planning': 
      return "Define sprint scope and set your team's goal";
    case 'execution': 
      return sprint?.goal || "Complete your assigned tickets";
    case 'review': 
      return "Demo completed work to stakeholders";
    case 'retro': 
      return "Reflect on what went well and what to improve";
    default: 
      return "";
  }
}

export default function WorkspaceDashboard() {
  const params = useParams<{ workspaceId: string }>();
  const workspaceId = params.workspaceId ? parseInt(params.workspaceId) : null;
  const [, navigate] = useLocation();
  const { toast } = useToast();

  const { data: state, isLoading, error } = useWorkspaceState(workspaceId);
  const advancePhase = useAdvanceWorkspacePhase();

  const handleAdvancePhase = async () => {
    if (!workspaceId) return;

    try {
      await advancePhase.mutateAsync({ workspaceId });
      toast({
        title: "Phase Advanced",
        description: "Moving to the next phase of your workspace journey.",
      });
    } catch {
      toast({
        title: "Error",
        description: "Failed to advance phase. Please try again.",
        variant: "destructive",
      });
    }
  };

  if (isLoading) {
    return (
      <div className="container max-w-6xl mx-auto p-6 space-y-6" data-testid="workspace-dashboard-loading">
        <Skeleton className="h-8 w-64" />
        <Skeleton className="h-32 w-full" />
        <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
          <Skeleton className="h-64 lg:col-span-2" />
          <Skeleton className="h-64" />
        </div>
      </div>
    );
  }

  if (error || !state) {
    return (
      <div className="container max-w-6xl mx-auto p-6" data-testid="workspace-dashboard-error">
        <Card className="border-destructive">
          <CardHeader>
            <CardTitle className="text-destructive">Error Loading Workspace</CardTitle>
            <CardDescription>
              {error?.message || 'Workspace not found. Please check the URL and try again.'}
            </CardDescription>
          </CardHeader>
          <CardContent>
            <Link href="/journey">
              <Button variant="outline" className="gap-2">
                <ChevronLeft className="h-4 w-4" />
                Back to Journey
              </Button>
            </Link>
          </CardContent>
        </Card>
      </div>
    );
  }

  const { workspace, currentPhase, currentSprint, phaseChecklist, nextActions, phaseHistory } = state;
  const primaryAction = nextActions.find(a => a.priority === 'primary');
  const secondaryActions = nextActions.filter(a => a.priority === 'secondary');
  const completedItems = phaseChecklist.filter(item => item.completed).length;
  const requiredItems = phaseChecklist.filter(item => item.required);
  const allRequiredCompleted = requiredItems.every(item => item.completed);

  return (
    <div className="container max-w-6xl mx-auto p-6 space-y-6" data-testid="workspace-dashboard">
      <div className="flex flex-col md:flex-row justify-between items-start md:items-center gap-4">
        <div className="flex items-center gap-4">
          <Link href={`/journey/${workspace.journeyId}`}>
            <Button variant="ghost" size="sm" className="gap-2" data-testid="button-back-journey">
              <ChevronLeft className="h-4 w-4" />
              Journey
            </Button>
          </Link>
          <div>
            <h1 className="text-2xl font-bold tracking-tight flex items-center gap-2" data-testid="text-workspace-title">
              <Building2 className="h-6 w-6" />
              {workspace.companyName}
            </h1>
            <p className="text-muted-foreground flex items-center gap-2 mt-1">
              <Briefcase className="h-4 w-4" />
              <span data-testid="text-workspace-role">{workspace.role}</span>
            </p>
          </div>
        </div>

        {primaryAction && (
          <Link href={primaryAction.route}>
            <Button size="lg" className="gap-2" data-testid="button-primary-action">
              <Play className="h-4 w-4" />
              {primaryAction.action}
              <ArrowRight className="h-4 w-4" />
            </Button>
          </Link>
        )}
      </div>

      <Card className="border-t-4 border-t-primary" data-testid="card-phase-stepper">
        <CardHeader className="pb-2">
          <CardTitle className="text-lg">Your Progress</CardTitle>
          <CardDescription>
            Follow the Scrum workflow phases to complete your journey
          </CardDescription>
        </CardHeader>
        <CardContent>
          <PhaseStepper 
            currentPhase={currentPhase}
            workspaceId={workspace.id}
            sprintId={workspace.currentSprintId}
            journeyId={workspace.journeyId}
            size="md"
            showLabels={true}
            interactive={true}
          />
        </CardContent>
      </Card>

      <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
        <Card 
          className={`lg:col-span-2 border-l-4 ${getPhaseColor(currentPhase)}`}
          data-testid="card-current-phase"
        >
          <CardHeader>
            <div className="flex justify-between items-start">
              <div>
                <Badge 
                  variant="outline" 
                  className="mb-2"
                  data-testid="badge-phase"
                >
                  Current Phase
                </Badge>
                <CardTitle className="text-xl" data-testid="text-phase-title">
                  {getPhaseLabel(currentPhase)}
                </CardTitle>
              </div>
              <MiniPhaseStepper currentPhase={currentPhase} />
            </div>
          </CardHeader>
          <CardContent className="space-y-6">
            <div className="p-4 rounded-lg bg-muted/50 flex gap-3" data-testid="phase-tip">
              <Lightbulb className="h-5 w-5 text-amber-500 flex-shrink-0 mt-0.5" />
              <p className="text-sm text-muted-foreground">
                {getPhaseTip(currentPhase)}
              </p>
            </div>

            <div>
              <h4 className="font-medium mb-3 flex items-center gap-2">
                <CheckCircle2 className="h-4 w-4" />
                Phase Checklist
                <span className="text-sm text-muted-foreground font-normal">
                  ({completedItems}/{phaseChecklist.length} completed)
                </span>
              </h4>
              <div className="space-y-2">
                {phaseChecklist.map((item, index) => (
                  <div 
                    key={index}
                    className="flex items-center gap-3 p-3 rounded-lg bg-background border"
                    data-testid={`checklist-item-${index}`}
                  >
                    <Checkbox 
                      checked={item.completed}
                      disabled
                      className="data-[state=checked]:bg-green-500 data-[state=checked]:border-green-500"
                    />
                    <span className={item.completed ? "line-through text-muted-foreground" : ""}>
                      {item.item}
                    </span>
                    {item.required && !item.completed && (
                      <Badge variant="outline" className="ml-auto text-xs">
                        Required
                      </Badge>
                    )}
                  </div>
                ))}
              </div>
            </div>

            {secondaryActions.length > 0 && (
              <>
                <Separator />
                <div>
                  <h4 className="font-medium mb-3">Quick Actions</h4>
                  <div className="flex flex-wrap gap-2">
                    {secondaryActions.map((action, index) => (
                      <Link key={index} href={action.route}>
                        <Button variant="outline" size="sm" data-testid={`button-action-${index}`}>
                          {action.action}
                        </Button>
                      </Link>
                    ))}
                  </div>
                </div>
              </>
            )}
          </CardContent>
        </Card>

        <div className="space-y-6">
          <Card data-testid="card-sprint-info">
            <CardHeader className="pb-2">
              <CardTitle className="text-sm font-medium text-muted-foreground">
                Sprint Status
              </CardTitle>
            </CardHeader>
            <CardContent>
              <div className="space-y-2">
                <p className="font-medium" data-testid="text-sprint-phase">
                  {getPhaseLabel(currentPhase)}
                </p>
                <p className="text-sm text-muted-foreground" data-testid="text-sprint-description">
                  {getPhaseStatusDescription(currentPhase, currentSprint)}
                </p>
                {currentSprint && (
                  <div className="flex items-center gap-2 text-sm text-muted-foreground mt-3">
                    <Clock className="h-4 w-4" />
                    <span>Sprint {currentSprint.sprintNumber}</span>
                  </div>
                )}
              </div>
            </CardContent>
          </Card>

          <Card data-testid="card-phase-history">
            <CardHeader className="pb-2">
              <CardTitle className="text-sm font-medium text-muted-foreground">
                Recent Activity
              </CardTitle>
            </CardHeader>
            <CardContent>
              {phaseHistory.length > 0 ? (
                <div className="space-y-3">
                  {phaseHistory.slice(0, 5).map((event, index) => (
                    <div 
                      key={event.id}
                      className="flex items-start gap-2 text-sm"
                      data-testid={`history-item-${index}`}
                    >
                      <div className={`w-2 h-2 rounded-full mt-1.5 ${
                        event.status === 'completed' ? 'bg-green-500' : 'bg-blue-500'
                      }`} />
                      <div>
                        <p className="font-medium capitalize">
                          {event.phase.replace('_', ' ')} {event.status}
                        </p>
                        <p className="text-xs text-muted-foreground">
                          {new Date(event.createdAt).toLocaleDateString()}
                        </p>
                      </div>
                    </div>
                  ))}
                </div>
              ) : (
                <p className="text-sm text-muted-foreground">
                  No activity yet. Get started with your workspace!
                </p>
              )}
            </CardContent>
          </Card>
        </div>
      </div>
    </div>
  );
}

