import { useState, useMemo } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { Card, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Progress } from "@/components/ui/progress";
import { Separator } from "@/components/ui/separator";
import { Dialog, DialogContent, DialogDescription, DialogHeader, DialogTitle } from "@/components/ui/dialog";
import { Textarea } from "@/components/ui/textarea";
import { Skeleton } from "@/components/ui/skeleton";
import { apiRequest, queryClient } from "@/lib/queryClient";
import { 
  ArrowLeft,
  GitBranch, 
  GitPullRequest,
  CheckCircle2, 
  Clock, 
  Target,
  Users,
  AlertCircle,
  ChevronRight,
  Code,
  Bug,
  Star,
  Wrench,
  Send,
  MessageSquare,
  Trophy,
  Play
} from "lucide-react";
import { cn } from "@/lib/utils";
import type { WorkspacePhase } from "@/hooks/use-sprint-workflow";
import type { SprintTicket, GitTicketState } from "@shared/schema";
import { useToast } from "@/hooks/use-toast";
import { getSprintExecutionAdapter } from "@shared/adapters";
import type { Role, Level } from "@shared/adapters";

type TicketStatus = 'todo' | 'in_progress' | 'in_review' | 'done';
type TicketType = 'bug' | 'feature' | 'improvement';

interface ExecutionModuleProps {
  workspaceId: number;
  sprintId: number;
  userId: number;
  journeyId?: number;
  companyName: string;
  role: string;
  level?: string;
  sprintGoal?: string;
  onComplete: () => void;
  onBack?: () => void;
  onStartTicketWork?: (ticketId: number) => void;
  onShowStandup?: () => void;
}

function getTypeIcon(type: TicketType) {
  switch (type) {
    case 'bug': return Bug;
    case 'feature': return Star;
    case 'improvement': return Wrench;
    default: return Code;
  }
}

function getTypeColor(type: TicketType) {
  switch (type) {
    case 'bug': return 'bg-red-100 text-red-700 dark:bg-red-900 dark:text-red-300';
    case 'feature': return 'bg-blue-100 text-blue-700 dark:bg-blue-900 dark:text-blue-300';
    case 'improvement': return 'bg-purple-100 text-purple-700 dark:bg-purple-900 dark:text-purple-300';
    default: return 'bg-gray-100 text-gray-700';
  }
}

function parseGitState(gitState: unknown): GitTicketState {
  const defaultState: GitTicketState = {
    branchName: null,
    branchCreatedAt: null,
    commits: [],
    isPushed: false,
    prCreated: false,
    prApproved: false,
    isMerged: false,
  };

  if (!gitState || typeof gitState !== 'object') return defaultState;
  return { ...defaultState, ...gitState as Partial<GitTicketState> };
}

function TicketCard({ 
  ticket, 
  onSelect,
  onMove
}: { 
  ticket: SprintTicket;
  onSelect: (ticket: SprintTicket) => void;
  onMove: (ticketId: number, newStatus: TicketStatus) => void;
}) {
  const Icon = getTypeIcon(ticket.type as TicketType);
  const gitState = parseGitState(ticket.gitState);

  const priorityColors = {
    high: 'bg-red-500',
    medium: 'bg-amber-500',
    low: 'bg-gray-400',
  };

  const handleDragStart = (e: React.DragEvent) => {
    e.dataTransfer.setData('ticketId', String(ticket.id));
  };

  return (
    <Card 
      className="cursor-pointer hover:shadow-md transition-shadow"
      onClick={() => onSelect(ticket)}
      draggable
      onDragStart={handleDragStart}
      data-testid={`card-ticket-${ticket.ticketKey}`}
    >
      <CardContent className="p-3 space-y-2">
        <div className="flex items-start justify-between gap-2">
          <Badge variant="outline" className="text-xs font-mono">
            {ticket.ticketKey}
          </Badge>
          <div 
            className={`h-2 w-2 rounded-full ${priorityColors[ticket.priority as keyof typeof priorityColors] || priorityColors.medium}`}
            title={`Priority: ${ticket.priority}`}
          />
        </div>

        <p className="text-sm font-medium line-clamp-2">{ticket.title}</p>

        <div className="flex items-center justify-between text-xs text-muted-foreground">
          <div className="flex items-center gap-2">
            <Badge variant="secondary" className="text-xs">
              {ticket.storyPoints} pts
            </Badge>
            <Badge className={cn("text-xs", getTypeColor(ticket.type as TicketType))} variant="secondary">
              <Icon className="h-3 w-3 mr-1" />
              {ticket.type}
            </Badge>
          </div>

          <div className="flex items-center gap-1">
            {gitState.branchName && (
              <GitBranch className="h-3 w-3 text-blue-500" />
            )}
            {gitState.prCreated && (
              <GitPullRequest className="h-3 w-3 text-purple-500" />
            )}
            {gitState.isMerged && (
              <CheckCircle2 className="h-3 w-3 text-green-500" />
            )}
          </div>
        </div>
      </CardContent>
    </Card>
  );
}

function KanbanColumn({ 
  title, 
  status, 
  tickets,
  onSelectTicket,
  onDropTicket,
}: {
  title: string;
  status: TicketStatus;
  tickets: SprintTicket[];
  onSelectTicket: (ticket: SprintTicket) => void;
  onDropTicket: (ticketId: number, newStatus: TicketStatus) => void;
}) {
  const [isDragOver, setIsDragOver] = useState(false);

  const handleDragOver = (e: React.DragEvent) => {
    e.preventDefault();
    setIsDragOver(true);
  };

  const handleDragLeave = () => {
    setIsDragOver(false);
  };

  const handleDrop = (e: React.DragEvent) => {
    e.preventDefault();
    setIsDragOver(false);
    const ticketId = e.dataTransfer.getData('ticketId');
    if (ticketId) {
      onDropTicket(parseInt(ticketId), status);
    }
  };

  const statusColors = {
    todo: 'border-t-gray-400',
    in_progress: 'border-t-blue-500',
    in_review: 'border-t-amber-500',
    done: 'border-t-green-500',
  };

  return (
    <div 
      className={cn(
        "flex flex-col bg-muted/30 rounded-lg overflow-hidden border-t-4",
        statusColors[status],
        isDragOver && "ring-2 ring-blue-400"
      )}
      onDragOver={handleDragOver}
      onDragLeave={handleDragLeave}
      onDrop={handleDrop}
      data-testid={`column-${status}`}
    >
      <div className="p-3 bg-background border-b">
        <div className="flex items-center justify-between">
          <h3 className="font-semibold text-sm">{title}</h3>
          <Badge variant="secondary" className="text-xs">
            {tickets.length}
          </Badge>
        </div>
      </div>

      <div className="flex-1 p-2 space-y-2 min-h-[200px] overflow-y-auto">
        {tickets.map(ticket => (
          <TicketCard
            key={ticket.id}
            ticket={ticket}
            onSelect={onSelectTicket}
            onMove={onDropTicket}
          />
        ))}

        {tickets.length === 0 && (
          <div className="h-full flex items-center justify-center text-sm text-muted-foreground p-4">
            No tickets
          </div>
        )}
      </div>
    </div>
  );
}

function TicketDetailDialog({ 
  ticket, 
  open,
  onClose,
  onStartWork,
  showGitTerminal,
}: { 
  ticket: SprintTicket;
  open: boolean;
  onClose: () => void;
  onStartWork: (ticket: SprintTicket) => void;
  showGitTerminal: boolean;
}) {
  const Icon = getTypeIcon(ticket.type as TicketType);
  const gitState = parseGitState(ticket.gitState);
  const [teamMessage, setTeamMessage] = useState('');
  const [messages, setMessages] = useState<{from: string; text: string; isUser?: boolean}[]>([
    { from: 'Sarah', text: "Let me know if you need any help with this ticket!" }
  ]);

  const handleSendMessage = () => {
    if (!teamMessage.trim()) return;
    setMessages([...messages, { from: 'You', text: teamMessage, isUser: true }]);
    setTeamMessage('');

    setTimeout(() => {
      setMessages(prev => [...prev, { 
        from: 'Marcus', 
        text: "Good question! Check the existing codebase patterns in src/utils for reference. I can pair with you if needed." 
      }]);
    }, 1000);
  };

  const acceptanceCriteria = Array.isArray(ticket.acceptanceCriteria) 
    ? ticket.acceptanceCriteria 
    : [];

  return (
    <Dialog open={open} onOpenChange={onClose}>
      <DialogContent className="max-w-2xl max-h-[90vh] overflow-y-auto">
        <DialogHeader>
          <div className="flex items-center gap-2 mb-2">
            <Badge variant="outline" className="font-mono">{ticket.ticketKey}</Badge>
            <Badge className={getTypeColor(ticket.type as TicketType)} variant="secondary">
              <Icon className="h-3 w-3 mr-1" />
              {ticket.type}
            </Badge>
          </div>
          <DialogTitle>{ticket.title}</DialogTitle>
          <DialogDescription>{ticket.description}</DialogDescription>
        </DialogHeader>

        <div className="space-y-4">
          <div className="grid grid-cols-3 gap-3 text-sm">
            <div className="p-3 bg-muted/50 rounded-lg text-center">
              <div className="text-lg font-bold">{ticket.storyPoints}</div>
              <div className="text-xs text-muted-foreground">Points</div>
            </div>
            <div className="p-3 bg-muted/50 rounded-lg text-center">
              <div className="text-lg font-bold capitalize">{ticket.priority}</div>
              <div className="text-xs text-muted-foreground">Priority</div>
            </div>
            <div className="p-3 bg-muted/50 rounded-lg text-center">
              <div className="text-lg font-bold capitalize">{ticket.status?.replace('_', ' ')}</div>
              <div className="text-xs text-muted-foreground">Status</div>
            </div>
          </div>

          {acceptanceCriteria.length > 0 && (
            <>
              <Separator />
              <div>
                <h4 className="text-sm font-medium mb-2 flex items-center gap-2">
                  <CheckCircle2 className="h-4 w-4" />
                  Acceptance Criteria
                </h4>
                <ul className="space-y-1">
                  {acceptanceCriteria.map((criterion, i) => (
                    <li key={i} className="text-sm text-muted-foreground flex items-start gap-2">
                      <div className="h-4 w-4 rounded border border-muted-foreground/30 mt-0.5 flex-shrink-0" />
                      {String(criterion)}
                    </li>
                  ))}
                </ul>
              </div>
            </>
          )}

          <Separator />

          <div>
            <h4 className="text-sm font-medium mb-3 flex items-center gap-2">
              <GitBranch className="h-4 w-4" />
              Git Workflow Progress
            </h4>
            <div className="space-y-2">
              <div className="flex items-center justify-between p-2 bg-muted/30 rounded">
                <div className="flex items-center gap-2">
                  {gitState.branchName ? (
                    <CheckCircle2 className="h-4 w-4 text-green-500" />
                  ) : (
                    <div className="h-4 w-4 rounded-full border-2 border-muted-foreground" />
                  )}
                  <span className={gitState.branchName ? '' : 'text-muted-foreground'}>
                    Create feature branch
                  </span>
                </div>
                {gitState.branchName && (
                  <code className="text-xs bg-muted px-2 py-1 rounded">{gitState.branchName}</code>
                )}
              </div>

              <div className="flex items-center justify-between p-2 bg-muted/30 rounded">
                <div className="flex items-center gap-2">
                  {gitState.commits.length > 0 ? (
                    <CheckCircle2 className="h-4 w-4 text-green-500" />
                  ) : (
                    <div className="h-4 w-4 rounded-full border-2 border-muted-foreground" />
                  )}
                  <span className={gitState.commits.length > 0 ? '' : 'text-muted-foreground'}>
                    Commit changes
                  </span>
                </div>
                {gitState.commits.length > 0 && (
                  <span className="text-xs text-muted-foreground">{gitState.commits.length} commit(s)</span>
                )}
              </div>

              <div className="flex items-center justify-between p-2 bg-muted/30 rounded">
                <div className="flex items-center gap-2">
                  {gitState.isPushed ? (
                    <CheckCircle2 className="h-4 w-4 text-green-500" />
                  ) : (
                    <div className="h-4 w-4 rounded-full border-2 border-muted-foreground" />
                  )}
                  <span className={gitState.isPushed ? '' : 'text-muted-foreground'}>
                    Push to remote
                  </span>
                </div>
              </div>

              <div className="flex items-center justify-between p-2 bg-muted/30 rounded">
                <div className="flex items-center gap-2">
                  {gitState.prCreated ? (
                    <CheckCircle2 className="h-4 w-4 text-green-500" />
                  ) : (
                    <div className="h-4 w-4 rounded-full border-2 border-muted-foreground" />
                  )}
                  <span className={gitState.prCreated ? '' : 'text-muted-foreground'}>
                    Open pull request
                  </span>
                </div>
              </div>

              <div className="flex items-center justify-between p-2 bg-muted/30 rounded">
                <div className="flex items-center gap-2">
                  {gitState.isMerged ? (
                    <CheckCircle2 className="h-4 w-4 text-green-500" />
                  ) : (
                    <div className="h-4 w-4 rounded-full border-2 border-muted-foreground" />
                  )}
                  <span className={gitState.isMerged ? '' : 'text-muted-foreground'}>
                    Merge to main
                  </span>
                </div>
              </div>
            </div>
          </div>

          <Separator />

          <div>
            <h4 className="text-sm font-medium mb-3 flex items-center gap-2">
              <MessageSquare className="h-4 w-4" />
              Team Chat
            </h4>
            <div className="border rounded-lg">
              <div className="max-h-40 overflow-y-auto p-3 space-y-2">
                {messages.map((msg, i) => (
                  <div key={i} className={cn(
                    "text-sm p-2 rounded",
                    msg.isUser ? "bg-blue-100 dark:bg-blue-900 ml-8" : "bg-muted/50 mr-8"
                  )}>
                    <span className="font-medium">{msg.from}: </span>
                    {msg.text}
                  </div>
                ))}
              </div>
              <div className="flex gap-2 p-2 border-t">
                <Textarea 
                  value={teamMessage}
                  onChange={(e) => setTeamMessage(e.target.value)}
                  placeholder="Ask your team for help..."
                  className="min-h-[40px] resize-none"
                  rows={1}
                />
                <Button size="sm" onClick={handleSendMessage}>
                  <Send className="h-4 w-4" />
                </Button>
              </div>
            </div>
          </div>

          <div className="flex justify-end pt-4">
            {ticket.status === 'todo' && showGitTerminal && (
              <Button onClick={() => onStartWork(ticket)} data-testid="button-start-working">
                <Play className="h-4 w-4 mr-2" />
                Start Working
                <ChevronRight className="h-4 w-4 ml-1" />
              </Button>
            )}
            {ticket.status === 'in_progress' && showGitTerminal && (
              <Button onClick={() => onStartWork(ticket)} data-testid="button-continue-working">
                <Code className="h-4 w-4 mr-2" />
                Continue Working
                <ChevronRight className="h-4 w-4 ml-1" />
              </Button>
            )}
          </div>
        </div>
      </DialogContent>
    </Dialog>
  );
}

export function ExecutionModule({ 
  workspaceId, 
  sprintId,
  userId, 
  journeyId,
  companyName, 
  role,
  level = 'intern',
  sprintGoal = "Complete sprint deliverables",
  onComplete,
  onBack,
  onStartTicketWork,
  onShowStandup,
}: ExecutionModuleProps) {
  const { toast } = useToast();
  const [selectedTicket, setSelectedTicket] = useState<SprintTicket | null>(null);
  const [sprintDay, setSprintDay] = useState(1);

  const adapter = useMemo(() => {
    return getSprintExecutionAdapter(role as Role, level as Level);
  }, [role, level]);

  const { data: tickets = [], isLoading, error } = useQuery<SprintTicket[]>({
    queryKey: ['/api/sprints', sprintId, 'tickets'],
    enabled: !!sprintId,
  });

  const ticketsByStatus = useMemo(() => ({
    todo: tickets.filter(t => t.status === 'todo'),
    in_progress: tickets.filter(t => t.status === 'in_progress'),
    in_review: tickets.filter(t => t.status === 'in_review'),
    done: tickets.filter(t => t.status === 'done'),
  }), [tickets]);

  const totalPoints = tickets.reduce((sum, t) => sum + (t.storyPoints || 0), 0);
  const completedPoints = ticketsByStatus.done.reduce((sum, t) => sum + (t.storyPoints || 0), 0);
  const progressPercent = totalPoints > 0 ? Math.round((completedPoints / totalPoints) * 100) : 0;

  const allDone = tickets.length > 0 && tickets.every(t => t.status === 'done');

  const moveTicket = useMutation({
    mutationFn: async ({ ticketId, newStatus }: { ticketId: number; newStatus: TicketStatus }) => {
      return apiRequest('PATCH', `/api/tickets/${ticketId}/move`, { newStatus });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/sprints', sprintId, 'tickets'] });
    },
    onError: (error: any) => {
      const message = error?.message || "Failed to move ticket";
      const gate = error?.gate;

      if (gate === 'branch_required') {
        toast({
          title: "Branch Required",
          description: "Create a branch first before starting work on this ticket.",
          variant: "destructive"
        });
      } else if (gate === 'pr_required') {
        toast({
          title: "PR Required", 
          description: "Submit a pull request before moving to review.",
          variant: "destructive"
        });
      } else if (gate === 'merge_required') {
        toast({
          title: "Merge Required",
          description: "Merge your PR before marking as done.",
          variant: "destructive"
        });
      } else {
        toast({
          title: "Error",
          description: message,
          variant: "destructive"
        });
      }
    }
  });

  const completePhase = useMutation({
    mutationFn: async () => {
      return apiRequest('POST', `/api/workspaces/${workspaceId}/advance`, {
        payload: {
          completedItems: tickets.map(t => t.ticketKey),
          totalPoints,
          completedPoints
        }
      });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/workspaces', workspaceId] });
      onComplete();
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to complete execution phase. Please try again.",
        variant: "destructive"
      });
    }
  });

  const handleMoveTicket = (ticketId: number, newStatus: TicketStatus) => {
    moveTicket.mutate({ ticketId, newStatus });
  };

  const handleStartWork = (ticket: SprintTicket) => {
    setSelectedTicket(null);
    if (onStartTicketWork) {
      onStartTicketWork(ticket.id);
    }
  };

  if (isLoading) {
    return (
      <div className="space-y-6" data-testid="execution-module-loading">
        <div className="flex items-center justify-between">
          <div className="space-y-2">
            <Skeleton className="h-8 w-48" />
            <Skeleton className="h-4 w-64" />
          </div>
        </div>
        <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
          {[1, 2, 3, 4].map(i => (
            <Skeleton key={i} className="h-24" />
          ))}
        </div>
        <div className="grid grid-cols-4 gap-4">
          {[1, 2, 3, 4].map(i => (
            <Skeleton key={i} className="h-64" />
          ))}
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="flex flex-col items-center justify-center p-8 text-center">
        <AlertCircle className="h-12 w-12 text-red-500 mb-4" />
        <h3 className="text-lg font-semibold mb-2">Failed to load tickets</h3>
        <p className="text-muted-foreground mb-4">There was an error loading the sprint tickets.</p>
        <Button onClick={() => queryClient.invalidateQueries({ queryKey: ['/api/sprints', sprintId, 'tickets'] })}>
          Try Again
        </Button>
      </div>
    );
  }

  return (
    <div className="space-y-6" data-testid="execution-module">
      <div className="flex items-center justify-between">
        <div>
          <h2 className="text-2xl font-bold text-gray-900 dark:text-white">Sprint Execution</h2>
          <p className="text-gray-500 dark:text-gray-400">{sprintGoal}</p>
        </div>
        <div className="flex items-center gap-2">
          {onShowStandup && (
            <Button variant="default" onClick={onShowStandup} data-testid="button-daily-standup">
              <Users className="h-4 w-4 mr-2" />
              Daily Standup
            </Button>
          )}
          {onBack && (
            <Button variant="outline" onClick={onBack} data-testid="button-back-to-dashboard">
              <ArrowLeft className="h-4 w-4 mr-2" />
              Back
            </Button>
          )}
        </div>
      </div>

      <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
        <Card>
          <CardContent className="p-4 flex items-center gap-3">
            <div className="h-10 w-10 rounded-full bg-blue-100 dark:bg-blue-800 flex items-center justify-center">
              <Clock className="h-5 w-5 text-blue-600 dark:text-blue-300" />
            </div>
            <div>
              <p className="text-sm text-muted-foreground">Sprint Day</p>
              <p className="text-xl font-semibold">{sprintDay}/10</p>
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="p-4 flex items-center gap-3">
            <div className="h-10 w-10 rounded-full bg-indigo-100 dark:bg-indigo-800 flex items-center justify-center">
              <Target className="h-5 w-5 text-indigo-600 dark:text-indigo-300" />
            </div>
            <div>
              <p className="text-sm text-muted-foreground">Progress</p>
              <p className="text-xl font-semibold">{progressPercent}%</p>
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="p-4 flex items-center gap-3">
            <div className="h-10 w-10 rounded-full bg-amber-100 dark:bg-amber-800 flex items-center justify-center">
              <Users className="h-5 w-5 text-amber-600 dark:text-amber-300" />
            </div>
            <div>
              <p className="text-sm text-muted-foreground">In Review</p>
              <p className="text-xl font-semibold">{ticketsByStatus.in_review.length}</p>
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="p-4 flex items-center gap-3">
            <div className="h-10 w-10 rounded-full bg-green-100 dark:bg-green-800 flex items-center justify-center">
              <CheckCircle2 className="h-5 w-5 text-green-600 dark:text-green-300" />
            </div>
            <div>
              <p className="text-sm text-muted-foreground">Completed</p>
              <p className="text-xl font-semibold">{completedPoints}/{totalPoints} pts</p>
            </div>
          </CardContent>
        </Card>
      </div>

      <div className="grid grid-cols-4 gap-4">
        <KanbanColumn
          title="To Do"
          status="todo"
          tickets={ticketsByStatus.todo}
          onSelectTicket={setSelectedTicket}
          onDropTicket={handleMoveTicket}
        />
        <KanbanColumn
          title="In Progress"
          status="in_progress"
          tickets={ticketsByStatus.in_progress}
          onSelectTicket={setSelectedTicket}
          onDropTicket={handleMoveTicket}
        />
        <KanbanColumn
          title="In Review"
          status="in_review"
          tickets={ticketsByStatus.in_review}
          onSelectTicket={setSelectedTicket}
          onDropTicket={handleMoveTicket}
        />
        <KanbanColumn
          title="Done"
          status="done"
          tickets={ticketsByStatus.done}
          onSelectTicket={setSelectedTicket}
          onDropTicket={handleMoveTicket}
        />
      </div>

      {allDone && (
        <Card className="bg-green-50 dark:bg-green-900/20 border-green-200 dark:border-green-800">
          <CardContent className="p-6">
            <div className="flex items-center justify-between">
              <div className="flex items-center gap-4">
                <div className="h-12 w-12 rounded-full bg-green-100 dark:bg-green-800 flex items-center justify-center">
                  <Trophy className="h-6 w-6 text-green-600 dark:text-green-300" />
                </div>
                <div>
                  <h3 className="font-semibold text-green-800 dark:text-green-200">
                    Sprint Complete!
                  </h3>
                  <p className="text-sm text-green-600 dark:text-green-400">
                    All {tickets.length} tickets have been completed. You delivered {totalPoints} story points.
                  </p>
                </div>
              </div>
              <Button 
                onClick={() => completePhase.mutate()}
                disabled={completePhase.isPending}
                className="bg-green-600 hover:bg-green-700"
                data-testid="button-complete-sprint"
              >
                {completePhase.isPending ? 'Completing...' : 'Continue to Review'}
                <ChevronRight className="h-4 w-4 ml-1" />
              </Button>
            </div>
          </CardContent>
        </Card>
      )}

      {selectedTicket && (
        <TicketDetailDialog
          ticket={selectedTicket}
          open={!!selectedTicket}
          onClose={() => setSelectedTicket(null)}
          onStartWork={handleStartWork}
          showGitTerminal={adapter.uiControls.showGitTerminal}
        />
      )}
    </div>
  );
}

client/src/components/workspace/review-m.content:
import { useState, useEffect, useMemo } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Skeleton } from "@/components/ui/skeleton";
import { Textarea } from "@/components/ui/textarea";
import { Avatar, AvatarFallback } from "@/components/ui/avatar";
import { apiRequest, queryClient } from "@/lib/queryClient";
import { 
  ArrowLeft,
  ArrowRight,
  CheckCircle2, 
  Users,
  Play,
  Presentation,
  Star,
  MessageSquare,
  ThumbsUp,
  Lightbulb,
  Bug,
  Wrench,
  Trophy,
  Clock,
  AlertCircle
} from "lucide-react";
import { cn } from "@/lib/utils";
import type { WorkspacePhase } from "@/hooks/use-sprint-workflow";
import { useToast } from "@/hooks/use-toast";
import { getSprintReviewAdapter } from "@shared/adapters/review";
import type { 
  SprintReviewConfig, 
  StakeholderFeedback as AdapterStakeholderFeedback,
  CompletedTicketSummary 
} from "@shared/adapters/review";
import { normalizeRole, normalizeLevel, type Role, type Level } from "@shared/adapters";

interface ReviewModuleProps {
  workspaceId: number;
  userId: number;
  journeyId?: number;
  companyName: string;
  role: string;
  level?: string;
  sprintId?: number;
  onComplete: () => void;
  onBack?: () => void;
}

type ReviewStep = 'demo' | 'feedback' | 'summary';

function getTypeIcon(type: string) {
  switch (type) {
    case 'bug': return Bug;
    case 'feature': return Star;
    case 'improvement': return Wrench;
    default: return CheckCircle2;
  }
}

function getTypeColor(type: string) {
  switch (type) {
    case 'bug': return 'bg-red-100 text-red-700 dark:bg-red-900 dark:text-red-300';
    case 'feature': return 'bg-blue-100 text-blue-700 dark:bg-blue-900 dark:text-blue-300';
    case 'improvement': return 'bg-purple-100 text-purple-700 dark:bg-purple-900 dark:text-purple-300';
    default: return 'bg-gray-100 text-gray-700';
  }
}

function getInitials(name: string): string {
  return name.split(' ').map(n => n[0]).join('').toUpperCase();
}

export function ReviewModule({ 
  workspaceId, 
  userId, 
  journeyId,
  companyName, 
  role,
  level = 'intern',
  sprintId,
  onComplete,
  onBack 
}: ReviewModuleProps) {
  const { toast } = useToast();
  const [currentStep, setCurrentStep] = useState<ReviewStep>('demo');
  const [currentDemoItem, setCurrentDemoItem] = useState(0);
  const [demoNotes, setDemoNotes] = useState<Record<string, string>>({});
  const [currentFeedback, setCurrentFeedback] = useState(0);
  const [feedbackAcknowledged, setFeedbackAcknowledged] = useState<Set<number>>(new Set());
  const [generatedFeedback, setGeneratedFeedback] = useState<AdapterStakeholderFeedback[]>([]);
  const [isGeneratingFeedback, setIsGeneratingFeedback] = useState(false);

  const normalizedRole = normalizeRole(role) as Role;
  const normalizedLevel = normalizeLevel(level) as Level;
  const adapter = useMemo(() => 
    getSprintReviewAdapter(normalizedRole, normalizedLevel),
    [normalizedRole, normalizedLevel]
  );

  const { data: sprintData, isLoading: isLoadingSprint } = useQuery<{
    sprint: { id: number; goal: string; status: string };
  }>({
    queryKey: [`/api/sprints/${sprintId}`],
    enabled: !!sprintId,
  });

  const { data: ticketsData, isLoading: isLoadingTickets } = useQuery<{
    id: number;
    ticketKey: string;
    title: string;
    type: string;
    storyPoints: number;
    status: string;
    completedAt?: string;
  }[]>({
    queryKey: [`/api/sprints/${sprintId}/tickets`],
    enabled: !!sprintId,
  });

  const completedTickets: CompletedTicketSummary[] = useMemo(() => {
    if (!ticketsData) return [];
    return ticketsData
      .filter(t => t.status === 'done')
      .map(t => ({
        id: String(t.id),
        key: t.ticketKey,
        title: t.title,
        type: (t.type || 'task') as 'bug' | 'feature' | 'improvement' | 'task',
        points: t.storyPoints || 0,
        completedAt: t.completedAt || new Date().toISOString(),
        prMerged: true,
        reviewCycles: 1,
      }));
  }, [ticketsData]);

  const sprintGoal = sprintData?.sprint?.goal || 'Complete sprint deliverables';
  const totalPoints = completedTickets.reduce((sum, t) => sum + t.points, 0);

  const generateFeedbackMutation = useMutation({
    mutationFn: async () => {
      const response = await apiRequest('POST', `/api/workspaces/${workspaceId}/generate-review-feedback`, {
        completedTickets,
        sprintGoal,
        stakeholders: adapter.feedbackConfig.stakeholders,
        feedbackConfig: adapter.feedbackConfig,
        prompts: adapter.prompts,
      });
      return response.json();
    },
    onSuccess: (data) => {
      setGeneratedFeedback(data.feedback || []);
      setIsGeneratingFeedback(false);
    },
    onError: () => {
      const fallbackFeedback = generateFallbackFeedback();
      setGeneratedFeedback(fallbackFeedback);
      setIsGeneratingFeedback(false);
    },
  });

  const generateFallbackFeedback = (): AdapterStakeholderFeedback[] => {
    const { stakeholders, sentimentDistribution } = adapter.feedbackConfig;
    const feedback: AdapterStakeholderFeedback[] = [];

    stakeholders.forEach((stakeholder, idx) => {
      const sentiment = idx === 0 ? 'positive' : 
                        idx === 1 ? 'suggestion' : 'positive';

      const ticketRef = completedTickets[idx % completedTickets.length];
      const feedbackTemplates = {
        positive: [
          `Great work on ${ticketRef?.title || 'this sprint'}! The implementation looks solid and addresses the requirements well.`,
          `I'm impressed with the quality of work delivered. The team should be proud of what we accomplished this sprint.`,
        ],
        suggestion: [
          `Good progress overall. For future sprints, consider adding more documentation for ${ticketRef?.title || 'complex features'}.`,
          `Nice work! One suggestion: let's ensure we have better test coverage for edge cases in similar tickets.`,
        ],
        neutral: [
          `The work meets expectations. Let's discuss any lessons learned in the retrospective.`,
        ],
        concern: [
          `We should discuss the scope of work for next sprint to ensure we're aligned on priorities.`,
        ],
      };

      const templates = feedbackTemplates[sentiment];
      const content = templates[Math.floor(Math.random() * templates.length)];

      feedback.push({
        stakeholderId: stakeholder.id,
        content,
        sentiment,
        category: sentiment === 'positive' ? 'praise' : 'improvement',
        requiresResponse: sentiment === 'suggestion' || sentiment === 'concern',
        relatedTicketId: ticketRef?.id,
      });
    });

    return feedback;
  };

  useEffect(() => {
    if (currentStep === 'feedback' && generatedFeedback.length === 0 && !isGeneratingFeedback) {
      setIsGeneratingFeedback(true);
      generateFeedbackMutation.mutate();
    }
  }, [currentStep]);

  const saveProgress = useMutation({
    mutationFn: async (progress: { demoComplete?: boolean; feedbackComplete?: boolean }) => {
      return apiRequest('PATCH', `/api/workspaces/${workspaceId}/metadata`, {
        reviewProgress: {
          demoComplete: progress.demoComplete || currentStep === 'feedback' || currentStep === 'summary',
          feedbackComplete: progress.feedbackComplete || currentStep === 'summary',
          demoNotes,
          feedbackReceived: generatedFeedback.length,
        }
      });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/workspaces', workspaceId, 'state'] });
    },
  });

  const completePhase = useMutation({
    mutationFn: async () => {
      // First save final review progress to metadata
      await apiRequest('PATCH', `/api/workspaces/${workspaceId}/metadata`, {
        reviewProgress: {
          demoComplete: true,
          feedbackComplete: true,
          demoNotes,
          feedbackReceived: generatedFeedback.length,
          completedTickets: completedTickets.map(t => t.id),
          totalPoints,
        }
      });

      // Then advance to next phase using the standard advance endpoint
      return apiRequest('POST', `/api/workspaces/${workspaceId}/advance`, {
        payload: {
          demoNotes,
          feedbackReceived: generatedFeedback.length,
          completedTickets: completedTickets.map(t => t.id),
          totalPoints,
        }
      });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/workspaces', workspaceId] });
      queryClient.invalidateQueries({ queryKey: ['/api/workspaces', workspaceId, 'state'] });
      onComplete();
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to complete review phase. Please try again.",
        variant: "destructive"
      });
    }
  });

  const handleNextDemo = () => {
    if (currentDemoItem < completedTickets.length - 1) {
      setCurrentDemoItem(currentDemoItem + 1);
    } else {
      setCurrentStep('feedback');
      saveProgress.mutate({ demoComplete: true });
    }
  };

  const handleAcknowledgeFeedback = () => {
    const newAcknowledged = new Set(feedbackAcknowledged);
    newAcknowledged.add(currentFeedback);
    setFeedbackAcknowledged(newAcknowledged);

    if (adapter.feedbackModifiers?.autoAcknowledgePositive) {
      generatedFeedback.forEach((fb, idx) => {
        if (fb.sentiment === 'positive') {
          newAcknowledged.add(idx);
        }
      });
    }

    if (currentFeedback < generatedFeedback.length - 1) {
      setCurrentFeedback(currentFeedback + 1);
    } else {
      setCurrentStep('summary');
      saveProgress.mutate({ demoComplete: true, feedbackComplete: true });
    }
  };

  const isLoading = isLoadingSprint || isLoadingTickets;

  if (isLoading) {
    return (
      <div className="space-y-6" data-testid="review-module-loading">
        <Skeleton className="h-8 w-48" />
        <Skeleton className="h-24 w-full" />
        <Skeleton className="h-64 w-full" />
      </div>
    );
  }

  if (completedTickets.length === 0) {
    return (
      <div className="space-y-6" data-testid="review-module-empty">
        <Card className="border-amber-200 bg-amber-50 dark:bg-amber-900/20">
          <CardContent className="p-6">
            <div className="flex items-center gap-4">
              <AlertCircle className="h-8 w-8 text-amber-600" />
              <div>
                <h3 className="font-semibold text-amber-900 dark:text-amber-100">No Completed Tickets</h3>
                <p className="text-sm text-amber-700 dark:text-amber-300">
                  Complete some tickets before starting the sprint review.
                </p>
              </div>
            </div>
          </CardContent>
        </Card>
        {onBack && (
          <Button variant="outline" onClick={onBack}>
            <ArrowLeft className="h-4 w-4 mr-2" />
            Back to Execution
          </Button>
        )}
      </div>
    );
  }

  const renderDemo = () => {
    const currentTicket = completedTickets[currentDemoItem];
    if (!currentTicket) return null;

    const Icon = getTypeIcon(currentTicket.type);
    const { demoConfig, uiConfig } = adapter;

    return (
      <div className="space-y-6">
        <div className="flex items-center justify-between">
          <div>
            <h2 className="text-2xl font-bold text-gray-900 dark:text-white">Sprint Review Demo</h2>
            <p className="text-gray-500 dark:text-gray-400">
              {adapter.metadata.description}
            </p>
          </div>
          {onBack && (
            <Button variant="outline" onClick={onBack} data-testid="button-back-to-dashboard">
              <ArrowLeft className="h-4 w-4 mr-2" />
              Back
            </Button>
          )}
        </div>

        <Card className="bg-gradient-to-r from-amber-50 to-orange-50 dark:from-amber-900/20 dark:to-orange-900/20 border-amber-200 dark:border-amber-800">
          <CardContent className="p-6">
            <div className="flex items-center gap-4">
              <div className="h-12 w-12 rounded-full bg-amber-100 dark:bg-amber-800 flex items-center justify-center">
                <Presentation className="h-6 w-6 text-amber-600 dark:text-amber-300" />
              </div>
              <div className="flex-1">
                <h3 className="font-semibold text-amber-900 dark:text-amber-100">Demo Session</h3>
                <p className="text-sm text-amber-700 dark:text-amber-300">
                  {demoConfig.format === 'guided' ? 'Follow the guided demo script' :
                   demoConfig.format === 'prompted' ? 'Use the prompts to structure your demo' :
                   'Present your completed work freely'}
                </p>
              </div>
              <div className="text-right">
                <div className="text-sm text-amber-600">Item</div>
                <div className="text-2xl font-bold text-amber-900">{currentDemoItem + 1}/{completedTickets.length}</div>
              </div>
              {demoConfig.showTimer && (
                <div className="text-right">
                  <div className="flex items-center gap-1 text-sm text-amber-600">
                    <Clock className="h-4 w-4" />
                    Target
                  </div>
                  <div className="text-lg font-semibold text-amber-900">
                    {Math.round(demoConfig.timePerTicket / 60)}m
                  </div>
                </div>
              )}
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardHeader>
            <div className="flex items-center justify-between">
              <div>
                <div className="flex items-center gap-2 mb-2">
                  <Badge variant="outline" className="font-mono">{currentTicket.key}</Badge>
                  <Badge className={getTypeColor(currentTicket.type)} variant="secondary">
                    <Icon className="h-3 w-3 mr-1" />
                    {currentTicket.type}
                  </Badge>
                  {uiConfig.showPointsSummary && (
                    <Badge variant="secondary">{currentTicket.points} pts</Badge>
                  )}
                </div>
                <CardTitle>{currentTicket.title}</CardTitle>
              </div>
            </div>
          </CardHeader>
          <CardContent className="space-y-4">
            {demoConfig.showScript && demoConfig.scriptSteps.length > 0 && (
              <div className="p-4 bg-muted/50 rounded-lg">
                <div className="flex items-start gap-3">
                  <Play className="h-5 w-5 text-amber-500 flex-shrink-0 mt-0.5" />
                  <div className="text-sm">
                    <p className="font-medium text-foreground mb-2">Demo Script:</p>
                    <ol className="list-decimal list-inside space-y-1 text-muted-foreground">
                      {demoConfig.scriptSteps.map((step, idx) => (
                        <li key={idx}>
                          {step.instruction}
                          {step.hint && demoConfig.format === 'guided' && (
                            <span className="text-xs text-muted-foreground ml-2">
                              ({step.hint})
                            </span>
                          )}
                        </li>
                      ))}
                    </ol>
                  </div>
                </div>
              </div>
            )}

            {demoConfig.allowNotes && (
              <div className="space-y-2">
                <label className="text-sm font-medium">Demo Notes (Optional)</label>
                <Textarea
                  value={demoNotes[currentTicket.id] || ''}
                  onChange={(e) => setDemoNotes({ ...demoNotes, [currentTicket.id]: e.target.value })}
                  placeholder="Any notes about the demo, questions asked, or feedback received..."
                  rows={3}
                  data-testid={`input-demo-notes-${currentTicket.key}`}
                />
              </div>
            )}

            <div className="flex items-center justify-between p-4 bg-green-50 dark:bg-green-900/20 rounded-lg border border-green-200">
              <div className="flex items-center gap-3">
                <CheckCircle2 className="h-5 w-5 text-green-600" />
                <span className="text-green-800 dark:text-green-200">Demo complete!</span>
              </div>
              <Button onClick={handleNextDemo} data-testid="button-next-demo">
                {currentDemoItem < completedTickets.length - 1 ? 'Next Item' : 'Continue to Feedback'}
                <ArrowRight className="h-4 w-4 ml-2" />
              </Button>
            </div>
          </CardContent>
        </Card>

        {uiConfig.showProgressBar && (
          <div className="flex gap-2">
            {completedTickets.map((_, index) => (
              <div 
                key={index}
                className={cn(
                  "flex-1 h-2 rounded-full transition-colors",
                  index < currentDemoItem ? "bg-green-500" :
                  index === currentDemoItem ? "bg-amber-500" : "bg-gray-200"
                )}
              />
            ))}
          </div>
        )}

        {adapter.tips.length > 0 && (
          <Card className="bg-blue-50/50 dark:bg-blue-900/10 border-blue-200">
            <CardContent className="p-4">
              <div className="flex items-start gap-3">
                <Lightbulb className="h-5 w-5 text-blue-600 flex-shrink-0 mt-0.5" />
                <div className="text-sm text-blue-800 dark:text-blue-200">
                  <span className="font-medium">Tip: </span>
                  {adapter.tips[currentDemoItem % adapter.tips.length]}
                </div>
              </div>
            </CardContent>
          </Card>
        )}
      </div>
    );
  };

  const renderFeedback = () => {
    if (isGeneratingFeedback || generatedFeedback.length === 0) {
      return (
        <div className="space-y-6" data-testid="review-feedback-loading">
          <div className="flex items-center justify-between">
            <div>
              <h2 className="text-2xl font-bold text-gray-900 dark:text-white">Stakeholder Feedback</h2>
              <p className="text-gray-500 dark:text-gray-400">Generating feedback from stakeholders...</p>
            </div>
          </div>
          <Card>
            <CardContent className="p-8">
              <div className="flex flex-col items-center justify-center gap-4">
                <div className="h-12 w-12 rounded-full bg-blue-100 flex items-center justify-center animate-pulse">
                  <MessageSquare className="h-6 w-6 text-blue-600" />
                </div>
                <p className="text-muted-foreground">Stakeholders are reviewing your demo...</p>
              </div>
            </CardContent>
          </Card>
        </div>
      );
    }

    const currentFb = generatedFeedback[currentFeedback];
    const stakeholder = adapter.feedbackConfig.stakeholders.find(s => s.id === currentFb.stakeholderId);

    if (!stakeholder || !currentFb) return null;

    return (
      <div className="space-y-6">
        <div className="flex items-center justify-between">
          <div>
            <h2 className="text-2xl font-bold text-gray-900 dark:text-white">Stakeholder Feedback</h2>
            <p className="text-gray-500 dark:text-gray-400">Receive feedback from the product team</p>
          </div>
          <Button variant="outline" onClick={() => setCurrentStep('demo')} data-testid="button-back-to-demo">
            <ArrowLeft className="h-4 w-4 mr-2" />
            Back to Demo
          </Button>
        </div>

        <Card className="bg-gradient-to-r from-blue-50 to-indigo-50 dark:from-blue-900/20 dark:to-indigo-900/20 border-blue-200 dark:border-blue-800">
          <CardContent className="p-6">
            <div className="flex items-center gap-4">
              <div className="h-12 w-12 rounded-full bg-blue-100 dark:bg-blue-800 flex items-center justify-center">
                <MessageSquare className="h-6 w-6 text-blue-600 dark:text-blue-300" />
              </div>
              <div className="flex-1">
                <h3 className="font-semibold text-blue-900 dark:text-blue-100">Feedback Session</h3>
                <p className="text-sm text-blue-700 dark:text-blue-300">
                  Review and acknowledge stakeholder feedback
                </p>
              </div>
              <div className="text-right">
                <div className="text-sm text-blue-600">Feedback</div>
                <div className="text-2xl font-bold text-blue-900">{currentFeedback + 1}/{generatedFeedback.length}</div>
              </div>
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="p-6">
            <div className="flex items-start gap-4">
              <Avatar className="h-12 w-12">
                <AvatarFallback 
                  className={cn(
                    currentFb.sentiment === 'positive' ? 'bg-green-100 text-green-700' :
                    currentFb.sentiment === 'suggestion' ? 'bg-amber-100 text-amber-700' :
                    currentFb.sentiment === 'concern' ? 'bg-red-100 text-red-700' :
                    'bg-gray-100 text-gray-700'
                  )}
                  style={{ backgroundColor: stakeholder.color + '20', color: stakeholder.color }}
                >
                  {getInitials(stakeholder.name)}
                </AvatarFallback>
              </Avatar>
              <div className="flex-1">
                <div className="flex items-center gap-2 mb-1">
                  <h4 className="font-semibold">{stakeholder.name}</h4>
                  <Badge variant="outline">{stakeholder.role}</Badge>
                  {adapter.feedbackConfig.showSentimentIcons && (
                    <>
                      {currentFb.sentiment === 'positive' && (
                        <ThumbsUp className="h-4 w-4 text-green-500" />
                      )}
                      {currentFb.sentiment === 'suggestion' && (
                        <Lightbulb className="h-4 w-4 text-amber-500" />
                      )}
                    </>
                  )}
                </div>
                <p className="text-muted-foreground">{currentFb.content}</p>
                {currentFb.relatedTicketId && (
                  <div className="mt-2">
                    <Badge variant="secondary" className="text-xs">
                      Re: {completedTickets.find(t => t.id === currentFb.relatedTicketId)?.key}
                    </Badge>
                  </div>
                )}
              </div>
            </div>
          </CardContent>
        </Card>

        <Card className="border-blue-200 dark:border-blue-800 bg-blue-50/50 dark:bg-blue-950/50">
          <CardContent className="p-4">
            <div className="flex items-center justify-between">
              <div className="flex items-center gap-3">
                <CheckCircle2 className="h-5 w-5 text-blue-600" />
                <span className="text-blue-800 dark:text-blue-200">
                  {feedbackAcknowledged.has(currentFeedback) 
                    ? 'Feedback acknowledged' 
                    : 'Acknowledge this feedback to continue'}
                </span>
              </div>
              <Button onClick={handleAcknowledgeFeedback} data-testid="button-acknowledge-feedback">
                {currentFeedback < generatedFeedback.length - 1 ? 'Acknowledge & Next' : 'Complete Feedback'}
                <ArrowRight className="h-4 w-4 ml-2" />
              </Button>
            </div>
          </CardContent>
        </Card>

        {adapter.uiConfig.showProgressBar && (
          <div className="flex gap-2">
            {generatedFeedback.map((_, index) => (
              <div 
                key={index}
                className={cn(
                  "flex-1 h-2 rounded-full transition-colors",
                  feedbackAcknowledged.has(index) ? "bg-green-500" :
                  index === currentFeedback ? "bg-blue-500" : "bg-gray-200"
                )}
              />
            ))}
          </div>
        )}
      </div>
    );
  };

  const renderSummary = () => (
    <div className="space-y-6">
      <div className="flex items-center justify-between">
        <div>
          <h2 className="text-2xl font-bold text-gray-900 dark:text-white">Review Summary</h2>
          <p className="text-gray-500 dark:text-gray-400">Sprint review completed successfully</p>
        </div>
        <Button variant="outline" onClick={() => setCurrentStep('feedback')} data-testid="button-back-to-feedback">
          <ArrowLeft className="h-4 w-4 mr-2" />
          Back
        </Button>
      </div>

      <Card className="bg-gradient-to-r from-green-50 to-emerald-50 dark:from-green-900/20 dark:to-emerald-900/20 border-green-200 dark:border-green-800">
        <CardContent className="p-6">
          <div className="flex items-center gap-4">
            <div className="h-12 w-12 rounded-full bg-green-100 dark:bg-green-800 flex items-center justify-center">
              <Trophy className="h-6 w-6 text-green-600 dark:text-green-300" />
            </div>
            <div>
              <h3 className="font-semibold text-green-900 dark:text-green-100">Sprint Review Complete!</h3>
              <p className="text-sm text-green-700 dark:text-green-300">
                Successfully demonstrated all completed work
              </p>
            </div>
          </div>
        </CardContent>
      </Card>

      <div className="grid grid-cols-3 gap-4">
        <Card>
          <CardContent className="p-4 text-center">
            <div className="text-3xl font-bold text-green-600">{completedTickets.length}</div>
            <div className="text-sm text-muted-foreground">Items Demoed</div>
          </CardContent>
        </Card>
        <Card>
          <CardContent className="p-4 text-center">
            <div className="text-3xl font-bold text-blue-600">{totalPoints}</div>
            <div className="text-sm text-muted-foreground">Points Delivered</div>
          </CardContent>
        </Card>
        <Card>
          <CardContent className="p-4 text-center">
            <div className="text-3xl font-bold text-amber-600">{generatedFeedback.length}</div>
            <div className="text-sm text-muted-foreground">Feedback Received</div>
          </CardContent>
        </Card>
      </div>

      {adapter.uiConfig.showSprintGoal && (
        <Card>
          <CardHeader>
            <CardTitle className="text-base">Sprint Goal Achievement</CardTitle>
          </CardHeader>
          <CardContent>
            <div className="p-4 bg-green-50 dark:bg-green-900/20 rounded-lg border border-green-200">
              <div className="flex items-start gap-3">
                <CheckCircle2 className="h-5 w-5 text-green-600 mt-0.5" />
                <div>
                  <p className="font-medium text-green-800 dark:text-green-200">Goal Met</p>
                  <p className="text-sm text-green-700 dark:text-green-300">{sprintGoal}</p>
                </div>
              </div>
            </div>
          </CardContent>
        </Card>
      )}

      <Card>
        <CardHeader>
          <CardTitle className="text-base">Key Feedback Highlights</CardTitle>
        </CardHeader>
        <CardContent className="space-y-3">
          {generatedFeedback.slice(0, 3).map((fb, i) => {
            const stakeholder = adapter.feedbackConfig.stakeholders.find(s => s.id === fb.stakeholderId);
            if (!stakeholder) return null;
            return (
              <div key={i} className="flex items-start gap-3 p-3 bg-muted/30 rounded-lg">
                <Avatar className="h-8 w-8">
                  <AvatarFallback 
                    className="text-xs"
                    style={{ backgroundColor: stakeholder.color + '20', color: stakeholder.color }}
                  >
                    {getInitials(stakeholder.name)}
                  </AvatarFallback>
                </Avatar>
                <div>
                  <p className="text-sm font-medium">{stakeholder.name}</p>
                  <p className="text-xs text-muted-foreground line-clamp-2">{fb.content}</p>
                </div>
              </div>
            );
          })}
        </CardContent>
      </Card>

      <Card className="bg-gradient-to-r from-violet-50 to-purple-50 dark:from-violet-900/20 dark:to-purple-900/20 border-violet-200 dark:border-violet-800">
        <CardContent className="p-6">
          <div className="flex items-center justify-between">
            <div className="flex items-center gap-4">
              <div className="h-12 w-12 rounded-full bg-violet-100 flex items-center justify-center">
                <Users className="h-6 w-6 text-violet-600" />
              </div>
              <div>
                <h3 className="font-semibold text-violet-900 dark:text-violet-100">Ready for Retrospective</h3>
                <p className="text-sm text-violet-700 dark:text-violet-300">
                  Reflect on the sprint and identify improvements
                </p>
              </div>
            </div>
            <Button 
              onClick={() => completePhase.mutate()}
              disabled={completePhase.isPending}
              className="bg-violet-600 hover:bg-violet-700"
              data-testid="button-start-retro"
            >
              <Play className="h-4 w-4 mr-2" />
              Start Retrospective
            </Button>
          </div>
        </CardContent>
      </Card>
    </div>
  );

  return (
    <div className="h-full" data-testid="review-module">
      {currentStep === 'demo' && renderDemo()}
      {currentStep === 'feedback' && renderFeedback()}
      {currentStep === 'summary' && renderSummary()}
    </div>
  );
}

