            id: `comment-${threadId}-reviewer-${replyTimestamp}`,
            authorId: currentThread.reviewerId,
            authorName: currentThread.reviewerName,
            authorRole: currentThread.reviewerRole,
            content: reviewerResponse,
            type: shouldResolveThread ? 'approval' : 'comment',
            isUser: false,
            createdAt: new Date().toISOString(),
          };

          if (shouldResolveThread) {
            onThreadResolve(threadId);
          }

          return prev.map(t => {
            if (t.id !== threadId) return t;
            return {
              ...t,
              comments: [...t.comments, reviewerComment],
              ...(shouldResolveThread ? { status: 'resolved' as const } : {}),
            };
          });
        });

        const next = new Set(currentAwaiting);
        next.delete(threadId);
        return next;
      });
    }, responseDelay);
  }, [replyInputs, threads, levelModifiers.minorResponseBehavior, levelModifiers.feedbackTone, onCommentSubmit, onThreadResolve, getReviewerFollowUpMessage, analyzeUserMessageIntent, getIntelligentFollowUp]);

  const handleResolve = useCallback((threadId: string) => {
    setThreads(prev => prev.map(thread => 
      thread.id === threadId 
        ? { ...thread, status: 'resolved' as const }
        : thread
    ));
    onThreadResolve(threadId);
  }, [onThreadResolve]);

  const toggleThread = useCallback((threadId: string) => {
    setExpandedThreads(prev => {
      const next = new Set(prev);
      if (next.has(threadId)) {
        next.delete(threadId);
      } else {
        next.add(threadId);
      }
      return next;
    });
  }, []);

  const handleRequestReReview = useCallback(() => {
    setReviewPhase('in_review');
    onRequestReReview();

    setTimeout(() => {
      const hasBlockingOpen = threads.some(t => t.status === 'open' && t.severity === 'blocking');
      if (!hasBlockingOpen) {
        setReviewPhase('approved');
      } else {
        setReviewPhase('changes_requested');
      }
    }, 2000);
  }, [threads, onRequestReReview]);

  const getGuidanceMessage = () => {
    switch (levelModifiers.feedbackTone) {
      case 'educational':
        return {
          title: 'Getting Started with Code Review',
          message: "Your teammates have reviewed your code and left feedback. Read through each comment, make any necessary changes, and reply to let them know you've addressed their feedback.",
        };
      case 'collaborative':
        return {
          title: 'Team Feedback',
          message: 'Your team has some suggestions to discuss. Review their comments and collaborate on the best approach.',
        };
      case 'direct':
        return {
          title: 'Review Comments',
          message: 'Address the following review comments before merging.',
        };
      case 'peer':
        return {
          title: 'Peer Review',
          message: 'Your peers have shared their perspectives. Review and respond as needed.',
        };
      default:
        return {
          title: 'Code Review',
          message: 'Review and address the comments below.',
        };
    }
  };

  const renderConversationFirstLayout = () => {
    const guidance = getGuidanceMessage();

    return (
    <div className="space-y-4">
      {levelModifiers.feedbackTone === 'educational' && (
        <div 
          className="bg-blue-50 dark:bg-blue-900/20 border border-blue-200 dark:border-blue-800 rounded-lg p-4"
          role="region"
          aria-label="Getting started guidance"
        >
          <div className="flex items-start gap-3">
            <Lightbulb className="h-5 w-5 text-blue-500 mt-0.5 flex-shrink-0" aria-hidden="true" />
            <div>
              <p className="font-medium text-blue-700 dark:text-blue-300">{guidance.title}</p>
              <p className="text-sm text-muted-foreground mt-1 leading-relaxed">
                {guidance.message}
              </p>
            </div>
          </div>
        </div>
      )}

      <ScrollArea className="h-[500px]">
        <div className="space-y-4 pr-4">
          {threads.map(thread => (
            <ThreadCard
              key={thread.id}
              thread={thread}
              isExpanded={expandedThreads.has(thread.id)}
              onToggle={() => toggleThread(thread.id)}
              onReply={handleReply}
              onResolve={handleResolve}
              replyValue={replyInputs[thread.id] || ''}
              onReplyChange={(value) => setReplyInputs(prev => ({ ...prev, [thread.id]: value }))}
              uiConfig={uiConfig}
              levelModifiers={levelModifiers}
              showExampleResponse={levelModifiers.showExampleResponses}
              isAwaitingResponse={awaitingResponse.has(thread.id)}
            />
          ))}
        </div>
      </ScrollArea>
    </div>
    );
  };

  const renderSplitDiffLayout = () => (
    <div className="flex gap-4 h-[600px]">
      {showFileTree && (
        <div className="w-64 border-r pr-4">
          <h4 className="text-sm font-medium mb-3 flex items-center gap-2">
            <FileCode className="h-4 w-4" />
            Changed Files
          </h4>
          <div className="space-y-1">
            {Object.entries(fileGroups).map(([filename, fileThreads]) => (
              <div
                key={filename}
                className="flex items-center justify-between p-2 rounded hover:bg-muted/50 cursor-pointer text-sm"
              >
                <span className="truncate">{filename}</span>
                {fileThreads.some(t => t.status === 'open') && (
                  <Badge variant="secondary" className="text-xs">
                    {fileThreads.filter(t => t.status === 'open').length}
                  </Badge>
                )}
              </div>
            ))}
          </div>
        </div>
      )}

      <ScrollArea className="flex-1">
        <div className="space-y-4 pr-4">
          {Object.entries(fileGroups).map(([filename, fileThreads]) => (
            <div key={filename} className="space-y-3">
              <div className="flex items-center gap-2 sticky top-0 bg-background py-2">
                <FileCode className="h-4 w-4 text-muted-foreground" />
                <span className="font-mono text-sm">{filename}</span>
                <Badge variant="outline" className="text-xs ml-auto">
                  {fileThreads.length} comment{fileThreads.length !== 1 ? 's' : ''}
                </Badge>
              </div>

              {fileThreads.map(thread => (
                <ThreadCard
                  key={thread.id}
                  thread={thread}
                  isExpanded={expandedThreads.has(thread.id)}
                  onToggle={() => toggleThread(thread.id)}
                  onReply={handleReply}
                  onResolve={handleResolve}
                  replyValue={replyInputs[thread.id] || ''}
                  onReplyChange={(value) => setReplyInputs(prev => ({ ...prev, [thread.id]: value }))}
                  uiConfig={uiConfig}
                  levelModifiers={levelModifiers}
                  showExampleResponse={levelModifiers.showExampleResponses}
                  showCodeSnippet={uiConfig.inlineComments}
                  isAwaitingResponse={awaitingResponse.has(thread.id)}
                />
              ))}
            </div>
          ))}
        </div>
      </ScrollArea>
    </div>
  );

  const renderUnifiedLayout = () => (
    <ScrollArea className="h-[600px]">
      <div className="space-y-3 pr-4">
        {threads.map(thread => (
          <ThreadCard
            key={thread.id}
            thread={thread}
            isExpanded={expandedThreads.has(thread.id)}
            onToggle={() => toggleThread(thread.id)}
            onReply={handleReply}
            onResolve={handleResolve}
            replyValue={replyInputs[thread.id] || ''}
            onReplyChange={(value) => setReplyInputs(prev => ({ ...prev, [thread.id]: value }))}
            uiConfig={uiConfig}
            levelModifiers={levelModifiers}
            showExampleResponse={levelModifiers.showExampleResponses}
            compact
            isAwaitingResponse={awaitingResponse.has(thread.id)}
          />
        ))}
      </div>
    </ScrollArea>
  );

  const resolvedCount = threads.length - unresolvedCount;
  const progressPercent = threads.length > 0 ? Math.round((resolvedCount / threads.length) * 100) : 0;

  const addressedThreadsCount = useMemo(() => 
    threads.filter(t => t.status === 'resolved' || t.comments.some(c => c.isUser)).length,
    [threads]
  );

  const canRequestReReviewNow = useMemo(() => {
    const hasUnaddressedBlocking = threads.some(
      t => t.status === 'open' && t.severity === 'blocking' && !t.comments.some(c => c.isUser)
    );
    return !hasUnaddressedBlocking && reviewPhase === 'changes_requested';
  }, [threads, reviewPhase]);

  const renderChangesRequestedBanner = () => {
    if (reviewPhase !== 'changes_requested' || !uiConfig.showReviewBanner) return null;

    return (
      <div 
        className="bg-amber-50 dark:bg-amber-900/20 border border-amber-200 dark:border-amber-800 rounded-lg p-4 mb-4"
        role="alert"
        aria-label="Changes requested - action required"
      >
        <div className="flex items-start justify-between gap-4">
          <div className="flex items-start gap-3">
            <AlertTriangle className="h-5 w-5 text-amber-500 mt-0.5 flex-shrink-0" aria-hidden="true" />
            <div>
              <p className="font-medium text-amber-700 dark:text-amber-300">
                Changes Requested
              </p>
              <p className="text-sm text-muted-foreground mt-1">
                {blockingCount > 0 
                  ? `${blockingCount} blocking issue${blockingCount !== 1 ? 's' : ''} need${blockingCount === 1 ? 's' : ''} to be fixed in your code.`
                  : `Review the feedback below and address the comments.`
                }
              </p>
              {uiConfig.showProgressIndicator && (
                <div className="flex items-center gap-2 mt-2">
                  <div className="flex gap-0.5">
                    {threads.map((t, i) => (
                      <div 
                        key={i}
                        className={cn(
                          "w-2 h-2 rounded-full",
                          t.status === 'resolved' 
                            ? "bg-green-500" 
                            : t.comments.some(c => c.isUser)
                              ? "bg-blue-500"
                              : t.severity === 'blocking'
                                ? "bg-red-500"
                                : "bg-amber-500"
                        )}
                        title={t.status === 'resolved' ? 'Resolved' : t.comments.some(c => c.isUser) ? 'Addressed' : 'Open'}
                      />
                    ))}
                  </div>
                  <span className="text-xs text-muted-foreground">
                    {addressedThreadsCount}/{threads.length} addressed
                  </span>
                </div>
              )}
            </div>
          </div>

          {uiConfig.showReturnToCode && onReturnToCode && reviewPhase === 'changes_requested' && (
            <Button
              variant="default"
              size="sm"
              onClick={onReturnToCode}
              className="bg-amber-600 hover:bg-amber-700 text-white flex-shrink-0"
              data-testid="button-return-to-code"
            >
              <ArrowLeft className="h-4 w-4 mr-2" aria-hidden="true" />
              Return to Code
            </Button>
          )}
        </div>
      </div>
    );
  };

  const renderReviewChecklist = () => {
    if (!uiConfig.showReviewChecklist) return null;

    const checklistItems = [
      { id: 'read', label: 'Read all review comments', done: true },
      { id: 'address', label: 'Address blocking issues', done: blockingCount === 0 },
      { id: 'respond', label: 'Respond to feedback', done: threads.every(t => t.status === 'resolved' || t.comments.some(c => c.isUser)) },
      { id: 'resolve', label: 'Resolve all threads', done: unresolvedCount === 0 },
    ];

    return (
      <div className="bg-muted/30 rounded-lg p-4 mb-4" role="region" aria-label="Review progress checklist">
        <h4 className="text-sm font-semibold mb-3 flex items-center gap-2">
          <CheckCircle2 className="h-4 w-4 text-muted-foreground" />
          Review Checklist
        </h4>
        <div className="space-y-2">
          {checklistItems.map(item => (
            <div key={item.id} className="flex items-center gap-2 text-sm">
              {item.done ? (
                <CheckCircle2 className="h-4 w-4 text-green-500" aria-hidden="true" />
              ) : (
                <div className="h-4 w-4 rounded-full border-2 border-muted-foreground/30" aria-hidden="true" />
              )}
              <span className={cn(item.done && "text-muted-foreground line-through")}>{item.label}</span>
            </div>
          ))}
        </div>
      </div>
    );
  };

  return (
    <Card className="border-green-200 dark:border-green-800" data-testid="pr-review-panel">
      <CardHeader className="pb-3">
        <div className="flex items-center justify-between">
          <div className="flex items-center gap-3">
            <div className="p-2 rounded-lg bg-green-100 dark:bg-green-900/30">
              <GitPullRequest className="h-5 w-5 text-green-600 dark:text-green-400" aria-hidden="true" />
            </div>
            <div>
              <CardTitle className="text-lg">Pull Request Review</CardTitle>
              <p className="text-sm text-muted-foreground mt-0.5">
                {ticketKey}: {ticketTitle}
              </p>
            </div>
          </div>

          <div className="flex items-center gap-3">
            <div className="text-right">
              <div className="text-sm font-medium" aria-live="polite">
                {resolvedCount} of {threads.length} resolved
              </div>
              <div className="w-24 h-1.5 bg-muted rounded-full mt-1 overflow-hidden">
                <div 
                  className="h-full bg-green-500 rounded-full transition-all duration-300"
                  style={{ width: `${progressPercent}%` }}
                  role="progressbar"
                  aria-valuenow={progressPercent}
                  aria-valuemin={0}
                  aria-valuemax={100}
                  aria-label={`${progressPercent}% of threads resolved`}
                />
              </div>
            </div>

            {reviewPhase === 'in_review' && (
              <Badge variant="secondary" className="bg-blue-100 text-blue-700 dark:bg-blue-900 dark:text-blue-300">
                <Clock className="h-3 w-3 mr-1" aria-hidden="true" />
                In Review
              </Badge>
            )}
            {reviewPhase === 'changes_requested' && (
              <Badge variant="secondary" className="bg-amber-100 text-amber-700 dark:bg-amber-900 dark:text-amber-300">
                <RefreshCw className="h-3 w-3 mr-1" aria-hidden="true" />
                Changes Requested
              </Badge>
            )}
            {reviewPhase === 'approved' && (
              <Badge variant="secondary" className="bg-green-100 text-green-700 dark:bg-green-900 dark:text-green-300">
                <Check className="h-3 w-3 mr-1" aria-hidden="true" />
                Approved
              </Badge>
            )}
          </div>
        </div>

        <div className="flex items-center gap-4 mt-4 text-sm text-muted-foreground">
          <span className="flex items-center gap-1.5">
            <MessageSquare className="h-4 w-4" aria-hidden="true" />
            {threads.length} comment{threads.length !== 1 ? 's' : ''}
          </span>
          {unresolvedCount > 0 && (
            <span className={cn(
              "flex items-center gap-1.5",
              uiConfig.highlightUnresolved && "text-amber-600 dark:text-amber-400 font-medium"
            )}>
              <AlertTriangle className="h-4 w-4" aria-hidden="true" />
              {unresolvedCount} unresolved
            </span>
          )}
          {blockingCount > 0 && (
            <span className="flex items-center gap-1.5 text-red-600 dark:text-red-400 font-medium">
              <AlertCircle className="h-4 w-4" aria-hidden="true" />
              {blockingCount} blocking
            </span>
          )}
          <span className="flex items-center gap-1.5 ml-auto">
            <Eye className="h-4 w-4" aria-hidden="true" />
            Reviewers: {reviewers.map(r => r.name).join(', ')}
          </span>
        </div>
      </CardHeader>

      <Separator />

      <CardContent className="pt-4">
        {renderChangesRequestedBanner()}
        {renderReviewChecklist()}

        {uiConfig.layoutMode === 'conversation-first' && renderConversationFirstLayout()}
        {uiConfig.layoutMode === 'split-diff' && renderSplitDiffLayout()}
        {uiConfig.layoutMode === 'unified' && renderUnifiedLayout()}

        <div 
          className="mt-6 pt-4 border-t bg-muted/20 -mx-6 -mb-6 px-6 py-4 rounded-b-lg"
          role="region"
          aria-label="Review actions"
        >
          <div className="flex items-center justify-between gap-4">
            <div className="flex-1">
              {blockingCount > 0 ? (
                <div className="flex items-center gap-2 text-sm text-red-600 dark:text-red-400">
                  <AlertCircle className="h-4 w-4" aria-hidden="true" />
                  <span className="font-medium">{blockingCount} blocking issue{blockingCount !== 1 ? 's' : ''} must be resolved before merge</span>
                </div>
              ) : testsAreBlocking ? (
                <div className="flex items-center gap-2 text-sm text-amber-600 dark:text-amber-400">
                  <AlertTriangle className="h-4 w-4" aria-hidden="true" />
                  <span className="font-medium">Tests must pass before requesting re-review</span>
                </div>
              ) : unresolvedCount > 0 ? (
                <div className="flex items-center gap-2 text-sm text-muted-foreground">
                  <MessageSquare className="h-4 w-4" aria-hidden="true" />
                  <span>
                    {levelModifiers.requireExplicitApprovalRequest 
                      ? `Resolve ${unresolvedCount} remaining comment${unresolvedCount !== 1 ? 's' : ''} and request re-review`
                      : `Address ${unresolvedCount} remaining comment${unresolvedCount !== 1 ? 's' : ''} to proceed`}
                  </span>
                </div>
              ) : reviewPhase === 'approved' ? (
                <div className="flex items-center gap-2 text-sm text-green-600 dark:text-green-400">
                  <CheckCircle2 className="h-4 w-4" aria-hidden="true" />
                  <span className="font-medium">All checks passed - ready to merge!</span>
                </div>
              ) : (
                <div className="flex items-center gap-2 text-sm text-blue-600 dark:text-blue-400">
                  <CheckCircle2 className="h-4 w-4" aria-hidden="true" />
                  <span>All feedback addressed - request re-review to proceed</span>
                </div>
              )}
            </div>

            <div className="flex items-center gap-3">
              {reviewPhase !== 'approved' && (
                <Button 
                  variant="outline" 
                  onClick={handleRequestReReview}
                  disabled={!canRequestReReview || reviewPhase === 'in_review'}
                  aria-label={canRequestReReview ? "Request reviewers to re-review your changes" : "Cannot request re-review - resolve blocking issues and pass tests first"}
                  data-testid="button-request-re-review"
                >
                  <RefreshCw className={cn("h-4 w-4 mr-2", reviewPhase === 'in_review' && "animate-spin")} aria-hidden="true" />
                  {reviewPhase === 'in_review' ? 'Reviewing...' : 'Request Re-review'}
                </Button>
              )}

              <Button 
                onClick={onMerge}
                disabled={!canMerge}
                className={cn(
                  "min-w-[120px] transition-colors",
                  canMerge 
                    ? "bg-green-600 hover:bg-green-700 text-white" 
                    : "bg-muted text-muted-foreground"
                )}
                aria-label={canMerge ? "Merge pull request into main branch" : "Cannot merge - resolve all issues first"}
                data-testid="button-merge-pr"
              >
                <GitMerge className="h-4 w-4 mr-2" aria-hidden="true" />
                Merge PR
              </Button>
            </div>
          </div>
        </div>
      </CardContent>
    </Card>
  );
}

interface ThreadCardProps {
  thread: SimulatedThread;
  isExpanded: boolean;
  onToggle: () => void;
  onReply: (threadId: string) => void;
  onResolve: (threadId: string) => void;
  replyValue: string;
  onReplyChange: (value: string) => void;
  uiConfig: PRReviewUIConfig;
  levelModifiers: PRReviewModifiers;
  showExampleResponse?: boolean;
  showCodeSnippet?: boolean;
  compact?: boolean;
  isAwaitingResponse?: boolean;
}

function ThreadCard({
  thread,
  isExpanded,
  onToggle,
  onReply,
  onResolve,
  replyValue,
  onReplyChange,
  uiConfig,
  levelModifiers,
  showExampleResponse,
  showCodeSnippet = true,
  compact = false,
  isAwaitingResponse = false,
}: ThreadCardProps) {
  const isResolved = thread.status === 'resolved';

  return (
    <div 
      className={cn(
        "border rounded-lg overflow-hidden transition-colors",
        isResolved && "opacity-60 bg-muted/30",
        !isResolved && thread.severity === 'blocking' && uiConfig.highlightUnresolved && "border-red-300 dark:border-red-700",
        !isResolved && thread.severity === 'major' && uiConfig.highlightUnresolved && "border-amber-300 dark:border-amber-700"
      )}
      data-testid={`thread-${thread.id}`}
    >
      <Collapsible open={isExpanded} onOpenChange={onToggle}>
        <CollapsibleTrigger className="w-full">
          <div className={cn(
            "flex items-center gap-3 p-3 hover:bg-muted/50",
            compact && "p-2"
          )}>
            {isExpanded ? (
              <ChevronDown className="h-4 w-4 text-muted-foreground" />
            ) : (
              <ChevronRight className="h-4 w-4 text-muted-foreground" />
            )}

            <Avatar className={cn("h-7 w-7", compact && "h-6 w-6")}>
              <AvatarFallback className={cn("text-white text-xs", thread.reviewerColor)}>
                {thread.reviewerName[0]}
              </AvatarFallback>
            </Avatar>

            <div className="flex-1 text-left">
              <div className="flex items-center gap-2">
                <span className="font-medium text-sm">{thread.reviewerName}</span>
                <span className="text-xs text-muted-foreground">{thread.reviewerRole}</span>
              </div>
              {thread.filename && (
                <div className="flex items-center gap-2 text-xs text-muted-foreground">
                  <Code className="h-3 w-3" />
                  <span className="font-mono">{thread.filename}:{thread.lineNumber}</span>
                </div>
              )}
            </div>

            <div className="flex items-center gap-2">
              {getSeverityBadge(thread.severity)}
              {isResolved ? (
                <Badge variant="secondary" className="bg-green-100 text-green-700 dark:bg-green-900 dark:text-green-300 text-xs">
                  <Check className="h-3 w-3 mr-1" />
                  Resolved
                </Badge>
              ) : isAwaitingResponse ? (
                <Badge variant="secondary" className="bg-blue-100 text-blue-700 dark:bg-blue-900 dark:text-blue-300 text-xs animate-pulse">
                  <Clock className="h-3 w-3 mr-1" />
                  Awaiting response
                </Badge>
              ) : (
                <Badge variant="outline" className="text-xs">Open</Badge>
              )}
            </div>
          </div>
        </CollapsibleTrigger>

        <CollapsibleContent>
          <div className="border-t">
            {showCodeSnippet && thread.codeSnippet && (
              <div className="bg-gray-900 text-gray-300 p-3 font-mono text-xs overflow-x-auto">
                <div className="text-gray-500 mb-1">Line {thread.lineNumber}</div>
                <pre>{thread.codeSnippet}</pre>
              </div>
            )}

            <div className="p-3 space-y-3">
              {thread.comments.map((comment, idx) => (
                <div 
                  key={comment.id}
                  className={cn(
                    "flex gap-3",
                    comment.isUser && "justify-end"
                  )}
                >
                  {!comment.isUser && (
                    <Avatar className="h-7 w-7">
                      <AvatarFallback className={cn("text-white text-xs", thread.reviewerColor)}>
                        {comment.authorName[0]}
                      </AvatarFallback>
                    </Avatar>
                  )}

                  <div className={cn(
                    "flex-1 max-w-[85%]",
                    comment.isUser && "flex flex-col items-end"
                  )}>
                    <div className={cn(
                      "rounded-lg p-3",
                      comment.isUser 
                        ? "bg-blue-500 text-white" 
                        : "bg-muted"
                    )}>
                      {!comment.isUser && (
                        <div className="flex items-center gap-2 mb-1">
                          <span className="text-xs font-medium">{comment.authorName}</span>
                          <span className="text-xs text-muted-foreground">
                            {new Date(comment.createdAt).toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}
                          </span>
                        </div>
                      )}
                      <p className="text-sm whitespace-pre-wrap">{comment.content}</p>
                    </div>

                    {showExampleResponse && comment.exampleResponse && !comment.isUser && idx === 0 && thread.status === 'open' && (
                      <div 
                        className="mt-3 p-3 bg-blue-50 dark:bg-blue-900/20 rounded-lg border border-blue-200 dark:border-blue-800"
                        role="note"
                        aria-label="Suggested response example"
                      >
                        <div className="flex items-center gap-1.5 text-xs font-medium text-blue-700 dark:text-blue-300 mb-1.5">
                          <Lightbulb className="h-3.5 w-3.5" />
                          Suggested response
                        </div>
                        <p className="text-sm text-blue-600 dark:text-blue-400 leading-relaxed">"{comment.exampleResponse}"</p>
                      </div>
                    )}
                  </div>

                  {comment.isUser && (
                    <Avatar className="h-7 w-7">
                      <AvatarFallback className="bg-blue-500 text-white text-xs">Y</AvatarFallback>
                    </Avatar>
                  )}
                </div>
              ))}

              {!isResolved && (
                <div className="space-y-3 mt-4 pt-4 border-t border-dashed">
                  <Textarea
                    value={replyValue}
                    onChange={(e) => onReplyChange(e.target.value)}
                    placeholder="Write your response to this feedback..."
                    className="min-h-[80px] text-sm resize-none"
                    aria-label="Reply to review comment"
                    data-testid={`input-reply-${thread.id}`}
                  />

                  <div className="flex items-center justify-between gap-3">
                    <Button
                      variant="ghost"
                      size="sm"
                      onClick={() => onResolve(thread.id)}
                      className="text-muted-foreground hover:text-foreground"
                      aria-label="Mark this thread as resolved without replying"
                      data-testid={`button-resolve-${thread.id}`}
                    >
                      <Check className="h-4 w-4 mr-1.5" />
                      Mark Resolved
                    </Button>

                    <Button
                      size="sm"
                      onClick={() => onReply(thread.id)}
                      disabled={!replyValue.trim()}
                      className="min-w-[100px]"
                      aria-label="Submit reply to reviewer"
                      data-testid={`button-reply-${thread.id}`}
                    >
                      <Send className="h-4 w-4 mr-1.5" />
                      Reply
                    </Button>
                  </div>
                </div>
              )}
            </div>
          </div>
        </CollapsibleContent>
      </Collapsible>
    </div>
  );
}

docs/CODE_EXECUTION_ARCHITECTURE.md.content:
# Code Execution Architecture

> **Status**:  Implemented - Monaco editor with LLM-simulated execution is live

## Overview

edmap uses **LLM-Simulated Code Execution** to provide instant, educational feedback on user-written code without the infrastructure costs and complexity of real sandboxed execution. The LLM analyzes code, predicts behavior, and simulates test results while providing rich explanatory feedback.

This approach aligns with edmap's core value proposition: users are learning through simulation, not being graded by a compiler.

---

## Architecture Decision

### Why LLM Simulation?

| Consideration | Real Execution | LLM Simulation |
|--------------|----------------|----------------|
| Infrastructure cost | $60-1,700/mo | ~$10-30/mo |
| Cold start latency | 2-40 seconds | None |
| Security concerns | Container hardening required | None (no execution) |
| Educational feedback | "Test failed" | "Here's why it failed..." |
| Fits simulation narrative | Partial | Perfect |
| Multi-language support | Complex per language | Prompt adjustment |

### Trade-offs Accepted

| Risk | Mitigation |
|------|------------|
| LLM hallucinations | Ticket complexity calibrated; future static analysis planned |
| Complex logic errors | Ticket complexity calibrated to LLM capabilities |
| User trust ("is this real?") | Position as "AI mentor review" within simulation |

> **Note**: Static analysis (ESLint, TypeScript type checking) is **planned but not yet implemented**. Currently LLM simulation handles all code analysis.

---

## System Architecture (Current Implementation)

```

                        USER INTERFACE                                    

                      TICKET WORKSPACE                                  

     Ticket Details      CODE EDITOR (Monaco)                       
     - Description               
     - Acceptance         // user writes code here                
       criteria           function getUser(id: string) {          
     - Test cases           ...                                   
                          }                                        
     TEAM CHAT                   
     Sarah: "Let me              
     know if you          SIMULATED TEST OUTPUT                   
     need help!"           2 tests passed                        
                           1 test failed: edge case...           







                       LLM SIMULATION PIPELINE                          


                 LLM CODE ANALYSIS (Groq)                               

     Analyzes code for correctness                                     
     Predicts test pass/fail for each test case                        
     Explains why tests pass or fail                                   
     Provides educational feedback and suggestions                     
     Returns mentor-style improvement tips                             




                 FEEDBACK TO USER                                       
     Simulated test results (~90% accurate)                            
     Educational explanations of why code works/fails                  
     Level-appropriate hints and suggestions                           





                       PR REVIEW INTEGRATION                              

  AI reviewers reference simulated execution results:                     
   "The null check test is failing - line 42 returns undefined"         
   "Great job! All 3 tests pass. Ready for merge."                      
   "Edge case not handled - what if userId is empty string?"            

```

> **Future Enhancement**: Static analysis (ESLint, TypeScript) will be added as a second pipeline branch to catch real syntax/type errors with 100% accuracy.

---

## Integration with Adapter Architecture

### New Types (extend `shared/adapters/execution/types.ts`)

```typescript
/**
 * Code Editor Configuration
 * Controls the embedded Monaco editor behavior
 */
export type EditorLanguage = 'typescript' | 'javascript' | 'python' | 'cpp';

export interface CodeEditorConfig {
  enabled: boolean;
  language: EditorLanguage;
  theme: 'vs-dark' | 'vs-light' | 'edmap-dark';
  fontSize: number;
  minimap: boolean;
  lineNumbers: boolean;
  wordWrap: 'on' | 'off';
  tabSize: number;
  readOnlyFiles: string[];           // Files user cannot edit
  hiddenFiles: string[];             // Files not shown to user
  starterCode: Record<string, string>; // filename -> initial content
}

/**
 * Simulated Execution Configuration
 * Controls how the LLM analyzes and simulates code execution
 */
export interface SimulatedExecutionConfig {
  enabled: boolean;
  staticAnalysis: {
    enabled: boolean;
    lintRules: 'strict' | 'moderate' | 'lenient';
    typeChecking: boolean;
    showInlineErrors: boolean;
  };
  llmSimulation: {
    enabled: boolean;
    model: string;                    // e.g., 'llama-3.3-70b-versatile'
    maxTokens: number;
    temperature: number;
    simulateTestRunner: boolean;
    showExecutionTrace: boolean;
    provideFixes: boolean;
  };
  feedbackStyle: 'educational' | 'professional' | 'minimal';
  autoRunOnSave: boolean;
  debounceMs: number;
}

/**
 * Test Case Definition
 * Tickets include test cases for the LLM to simulate
 */
export interface TestCase {
  id: string;
  name: string;
  description: string;
  input?: string;
  expectedOutput?: string;
  assertions: string[];              // Human-readable assertions
  hidden: boolean;                   // Hidden tests not shown to user
}

/**
 * Extended Ticket Work Config
 * Adds code editing and execution simulation
 */
export interface ExtendedTicketWorkConfig extends TicketWorkConfig {
  codeEditor: CodeEditorConfig;
  execution: SimulatedExecutionConfig;
  testCases: TestCase[];
}
```

### Level-Based Editor Scaffolding

| Level | Editor Mode | Starter Code | Tests Visible | Hints | Auto-Fix |
|-------|-------------|--------------|---------------|-------|----------|
| **Intern** | 80% complete template | Heavy | All shown | Always | Suggested |
| **Junior** | Function stubs | Moderate | Names only | On error | On request |
| **Mid** | File structure only | Light | Hidden | On request | Disabled |
| **Senior** | Empty project | None | Write your own | Never | Disabled |

### Level Overlay Extensions

```typescript
// Extend LevelExecutionOverlay
export interface CodeEditorModifiers {
  starterCodeAmount: 'full' | 'partial' | 'stubs' | 'empty';
  showTestCases: 'all' | 'names' | 'hidden';
  showInlineHints: boolean;
  autoSuggestFixes: boolean;
  allowRunWithErrors: boolean;
  executionFeedbackDetail: 'verbose' | 'standard' | 'minimal';
}
```

---

## Component Architecture

### Implemented Components

```
client/src/components/workspace/
 code-editor/
    code-editor-panel.tsx   #  Main Monaco editor wrapper
    bottom-dock.tsx         #  Test output and feedback panel
    index.tsx               #  Exports
 code-work-panel.tsx         #  Code work simulation
 pr-review-panel.tsx         #  PR review with threads
 ticket-workspace.tsx        #  Full ticket environment

server/services/
 code-analysis.ts            #  LLM code execution service

shared/adapters/code-execution/
 index.ts                    #  Factory function
 types.ts                    #  ExecutionProvider, CodeChallenge
 roles/                      #  Developer, PM configs
 levels/                     #  Intern  Senior scaffolding
 providers/
     llm-provider.ts         #  Groq-powered analysis
```

### Not Yet Implemented

```
client/src/components/workspace/
 execution/                  #  Planned
    test-runner-panel.tsx   # Separate test output display
    execution-trace.tsx     # Step-by-step code trace
    static-analysis.tsx     # Real lint/type errors (ESLint)
    combined-feedback.tsx   # Merged analysis results
```

> **Note**: Static analysis (ESLint, TypeScript) is planned but not yet implemented. Currently only LLM simulation is used.

### Data Flow (Current Implementation)

```
User Types Code in Monaco Editor



  User clicks    
  "Run Tests"    




         LLM SIMULATION              
         (API call)                  

  POST /api/analyze-code             
   Sends code + test cases          
   Groq analyzes and predicts       
   Returns simulated test results   




         FEEDBACK                    

   Test pass/fail results           
   Explanations of failures         
   Educational tips                 
   Mentor-style comments            




         UPDATE UI                   

   Bottom dock shows results        
   PR Review references execution   
   Ticket status updated            

```

### Planned Enhancement (Static Analysis)

```
User Types Code




 Static Analysis          LLM Simulation  
 (client-side)            (API call)      
  PLANNED                IMPLEMENTED  

  ESLint                 POST /api/      
  TypeScript               analyze-code  





            Combined Result 

```

---

## API Design

### Code Analysis Endpoint

```typescript
// POST /api/analyze-code
interface AnalyzeCodeRequest {
  ticketId: string;
  files: Record<string, string>;     // filename -> content
  testCases: TestCase[];
  userLevel: Level;
  language: EditorLanguage;
}

interface AnalyzeCodeResponse {
  staticAnalysis: {
    errors: StaticError[];
    warnings: StaticWarning[];
    passesTypeCheck: boolean;
  };
  simulatedExecution: {
    testResults: SimulatedTestResult[];
    overallPass: boolean;
    executionTrace?: string;
  };
  feedback: {
    summary: string;
    improvements: string[];
    mentorComment?: string;
  };
  timestamp: string;
}

interface SimulatedTestResult {
  testId: string;
  testName: string;
  passed: boolean;
  expectedOutput?: string;
  actualOutput?: string;           // LLM's prediction
  explanation: string;             // Why it passed/failed
  lineReferences?: number[];       // Relevant code lines
}
```

### LLM Prompt Structure

```typescript
const analyzeCodePrompt = (request: AnalyzeCodeRequest) => `
You are a code execution simulator for an educational platform.

## Context
- User Level: ${request.userLevel}
- Ticket: ${ticketDescription}
- Language: ${request.language}

## User's Code
${formatFiles(request.files)}

## Test Cases to Simulate
${formatTestCases(request.testCases)}

## Instructions
1. Analyze the code for correctness
2. For each test case, predict whether it would pass or fail
3. If a test fails, explain why with specific line references
4. Provide educational feedback appropriate for a ${request.userLevel}
5. Be encouraging but accurate

## Output Format (JSON)
{
  "testResults": [
    {
      "testId": "test-1",
      "testName": "should return null for empty id",
      "passed": false,
      "explanation": "Line 3 checks 'if (!id)' but empty string '' is falsy, so it returns null. However, the test expects this behavior, so this should pass.",
      "actualOutput": "null",
      "expectedOutput": "null"
    }
  ],
  "overallPass": true,
  "executionTrace": "Step 1: getUser('') called...",
  "feedback": {
    "summary": "Good defensive coding! 2/3 tests pass.",
    "improvements": ["Consider handling undefined separately from empty string"],
    "mentorComment": "Nice null check! Think about what happens with whitespace-only strings."
  }
}
`;
```

---

## Backlog Catalogue Extension

Each ticket in `backlog-catalogue.ts` will be extended with:

```typescript
interface BacklogItem {
  // ... existing fields ...

  codeChallenge?: {
    language: EditorLanguage;
    starterFiles: Record<string, string>;
    testCases: TestCase[];
    solutionFiles?: Record<string, string>;  // For validation
    hints: string[];
    acceptanceCriteria: string[];
  };
}

// Example ticket
{
  key: 'TICKET-NULL-CHECK',
  title: 'Fix null pointer exception in user service',
  type: 'bug',
  // ... other fields ...

  codeChallenge: {
    language: 'typescript',
    starterFiles: {
      'user-service.ts': `
export function getUser(id: string) {
  // BUG: Crashes when id is null or undefined
  return database.users[id].name;
}
`,
      'user-service.test.ts': `
import { getUser } from './user-service';

test('returns null for invalid id', () => {
  expect(getUser(null)).toBeNull();
});

test('returns user name for valid id', () => {
  expect(getUser('123')).toBe('John');
});
`
    },
    testCases: [
      {
        id: 'test-null',
        name: 'returns null for invalid id',
        assertions: ['getUser(null) should return null, not throw'],
        hidden: false
      },
      {
        id: 'test-valid',
        name: 'returns user name for valid id',
        assertions: ['getUser("123") should return "John"'],
        hidden: false
      }
    ],
    hints: [
      'Check if id exists before accessing database',
      'Consider using optional chaining (?.) or null check'
    ],
    acceptanceCriteria: [
      'No exceptions thrown for null/undefined input',
      'Valid IDs still return correct user name'
    ]
  }
}
```

---

## Language Support Roadmap

### Phase 1: TypeScript (MVP)
- Monaco with TypeScript language server
- ESLint + TypeScript compiler for static analysis
- Full LLM simulation support

### Phase 2: JavaScript
- Minimal changes (TypeScript superset)
- Adjust linting rules

### Phase 3: Python
- Monaco Python mode
- Ruff/Pylint for static analysis
- Adjust prompts for Python idioms

### Phase 4: C++
- Monaco C++ mode
- Clang-tidy for static analysis
- Additional prompt engineering for memory/pointer concepts

---

## Cost Projections

| MAU | API Calls/Month | Token Usage | Estimated Cost |
|-----|-----------------|-------------|----------------|
| 100 | ~3,000 | ~1.5M tokens | ~$3-5 |
| 1,000 | ~30,000 | ~15M tokens | ~$30-50 |
| 10,000 | ~300,000 | ~150M tokens | ~$300-500 |

*Based on: ~30 executions/user/month, ~500 tokens/request average*

---

## Future: Hybrid Execution

When enterprise customers require verified execution:

```
User Code

     LLM Simulation (instant feedback)

     Real Execution (background verification)


         Reconciliation
          Flag LLM errors
          Improve prompts
          Track accuracy
```

This allows gradual migration without breaking the user experience.

---

## Success Metrics

1. **Accuracy**: LLM predictions match expected behavior >90% of time
2. **Latency**: Feedback returned in <2 seconds
3. **Engagement**: Users complete more tickets with editor enabled
4. **Learning**: PR review comments reference code issues accurately
5. **Satisfaction**: Users report feeling "prepared" for real coding

